css:
@-moz-document url-prefix("https://catwar.su")
{
  #t,
#t_hide/* реклама*/
  {
    display: none;
  }
}

@-moz-document url("https://catwar.su/cw3/")
{
  ::-webkit-scrollbar
  {
    width: auto;
  }

.mouth>li>img:hover
  {
    transform: scale(1.5);
  }

  ::-webkit-scrollbar-track
  {
    background: #111111 !important;
    border-radius: 10px;
  }

  ::-webkit-scrollbar-thumb
  {
    background: #383838 !important;
    border: 0.5px solid #282828 !important;
    border-radius: 10px;
  }

  ::-webkit-scrollbar-corner
  {
    background: transparent !important;
  }

  #history_block div
  {
    background-color: #838383 !important;
    font-size: 0;
    padding: 8px;
  }

  #thdey
  {
    margin-left: 10px;
  }

  #history div
  {
    background-color: #222222 !important;
    margin-top: 10px;
    margin-left: 5px;
    color: transparent;
  }

  #location
  {
    color: #969696 !important;
    position: fixed;
    width: 170px;
    top: 243px;
    left: 65px;
    background-color: #282828;
    border: 0.5px solid #282828;
    border-radius: 10px;
    text-align: center;
    padding: 5px;
    font-weight: bold;
    font-size: 14px !important;
  }

  a#history_clean
  {
    margin-left: 5px;
  }

  span#ist
  {
    margin-left: 5px;
    color: #838383 !important;
  }

  div#cages_div
  {
    opacity: 1 !important;
  }

  select
  {
    background-color: #111111;
    color: #838383;
  }

  body
  {
    background-color: #191919 !important;
    color: #838383 !important;
  }

  .small
  {
    font-size: 14px;
    margin-left: 42%;
  }

  div#tir
  {
    margin-left: 10px;
    margin-top: 10px;
  }

  #akten
  {
    margin-left: 5px;
  }

  a
  {
    color: #b2b2b2;
  }

  input
  {
    background-color: #111111 !important;
    color: #b2b2b2 !important;
    border: 0.5px solid #282828 !important;
    border-radius: 5px;
  }

  hr
  {
    border: 0.5px solid #282828 !important;
  }

  .ui-slider
  {
    background: #111111 !important;
    border: 0.5px solid #282828 !important;
    margin-right: 50px;
  }

  .ui-slider .ui-slider-handle
  {
    background: #383838 !important;
    border: 0.5px solid #282828 !important;
  }

  .move_name
  {
    color: #000000 !important;
  }

  .nick
  {
    color: #b2b2b2 !important;
  }

  .adopt-id
  {
    color: #fff !important;
  }

  .hotkey
  {
    background-color: #111111 !important;
    color: #838383 !important;
    border: 0.5px solid #282828 !important;
    border-radius: 5px;
  }

  .ui-scroll
  {
    background-color: #bcbcbc;
  }

  #fightPanel
  {
    background-color: #2d2d2d;
    border: 0.5px solid #2d2d2d;
    height: 195px;
  }

  #fightLog
  {
    height: 168px !important;
  }

  .log_claws
  {
    color: #482419 !important;
  }

  .log_teeth
  {
    color: #353535 !important;
  }

  #block_mess
  {
    margin-left: 10px;
  }

  #tr_chat
  {
    background-color: #2d2d2d;
    padding: 0px;
  }

  #chat_msg
  {
    background-color: #2d2d2d;
    border: 0px;
    overflow-x: hidden;
  }

  #tr_actions
  {
    background-color: #222222;
    border: 0px;
  }

  .active_thing
  {
    border: 1px solid #2b2c33;
  }

  #block_deys
  {
    margin-top: 5px;
    font-size: 0;
  }

  #mit
  {
    background-color: #111111;
    color: #838383;
    border: 0.5px solid #282828;
    border-radius: 5px;
    margin-top: 18px;
    font-size: 14px;
  }

  #mitok
  {
    margin-top: 2px;
    background-color: #111111;
    color: #838383;
    border: 0.5px solid #282828;
    border-radius: 5px;
    font-size: 14px;
  }

  #main_table
  {
    border: 0px;
    margin-top: 10px;
  }

  #app>p:not([id])
  {
    position: relative;
    text-align: center;
    font-weight: bold;
    font-size: 14px !important;
  }

  #tr_tos
  {
    background: #111111;
    color: #111111;
  }

  #tos
  {
    margin-top: 5px;
    height: 15px;
    border-radius: 10px;
  }

  #tr_mouth
  {
    position: fixed;
    width: 315px;
    top: 240px;
    right: 25px;
    border-radius: 10px;
    text-align: left;
    padding: 3px;
    background-color: #222222;
    color: #969696;
  }

  #info_main
  {
    background-color: #222222;
  }

  #family
  {
    background-color: #222222;
    color: #838383;
  }

  a#history.toggle
  {
    font-size: 19px;
    color: #b2b2b2;
    margin-left: 10px;
    margin-bottom: 5px;
  }

  #history
  {
    background-color: #222222;
    width: 100%;
    margin-left: 10px;
  }

  #parameter
  {
    position: fixed;
    width: 183px;
    top: 20px;
    left: 60px;
    border-radius: 10px;
    text-align: left;
    padding: 3px;
  }

  a#parameters.toggle
  {
    display: none;
  }

  #block_mess
  {
    margin-top: 5px;
    margin-bottom: 5px;
  }

  #family
  {
    background-color: #222222;
  }

  #cages
  {
    background-color: transparent;
  }

  #text
  {
    color: #fff;
  }

  #might_table
  {
    margin-bottom: 21px;
  }

  p#error
  {
    top: 290px;
    left: 55px;
    background-color: #282828;
    border: 0.5px solid #282828;
  }

  #items
  {
    padding-left: 17px;
    padding-top: 5px;
    padding-bottom: 5px;
  }

  input:focus
  {
    background-color: #383838;
  }

  #newchat
  {
    background-color: #191919 !important;
    color: #bcbcbc !important;
  }

  #newls
  {
    background-color: #191919 !important;
    color: #bcbcbc !important;
  }

  a:hover
  {
    color: #bcbcbc;
  }

  .myname
  {
    background-color: #bcbcbc !important;
    color: black;
  }

  .title
  {
    display: none;
  }

  #tr_sky
  {
    display: none;
  }

