css:
@-moz-document domain(kanbanflow.com) {
	div.taskList {
    background-color: #404040;
    border-color: #6b636b;
    border-style: none;
}

div.boardHeader {
    background-color: #404040;
}

h1.boardHeader-boardName.truncate {
    color: #ffffff;
}

div.columnHeader-inner {
    background-color: #1c1c1c;
}

h2.columnHeader-name {
    color: #ffffff;
}

i.columnHeader-descriptionIcon.icons-info {
    color: #ffffff;
    border-color: #000;
    background-color: #ffffff;
}

button.icons-timer.boardHeader-timerShortcut.boardHeader-shortcut.icons--button {
    color: #ffffff;
}

div.board-wrapper-inner {
    background-color: #1f1f1f;
}

div.board-collapsedColumnName {
    background-color: #141414;
}

h2.collapsed-info-title {
    color: #e0e0e0;
}

.board-taskListCell {
    border-color: #000;
}

.column-first {
    border-color: #000;
}

.columnHeader {
    border-color: #000;
}

span.boardHeader-menuName {
    color: #ffffff;
}

i.icons-menu {
    color: #ffffff;
}

}
