css:
@-moz-document regexp("\\S+/_+(graphiql|graphql).*") {
.graphiql-container .CodeMirror-scroll {
      background-color: #222;
  }
.CodeMirror-gutters {
    border: none;
}
.graphiql-container .history-contents li {
    border: none;
}
.graphiql-container .history-contents > li:hover {
    background-color: #0a0a0a;
}

::selection {
    background: #444!important;
}
::-moz-selection {
    background: #444!important;
}

.CodeMirror-selected {
    background: #333!important;
}
.CodeMirror-focused .CodeMirror-selected {
    background: #123!important;
}

pre.CodeMirror-line::selection, pre.CodeMirror-line > span::selection, pre.CodeMirror-line > span > span::selection {
    background: #b53!important;
}
pre.CodeMirror-line:selection {
    background: #b53!important;
}
.graphiql-container button {
    background: transparent!important;
    color: #ddd!important;
}
.graphiql-container button:hover {
    background: transparent!important;
    color: #fff!important;
}
.graphiql-container .toolbar-button, .graphiql-container .execute-button, .docExplorerShow {
    transition: all .2s;
    background: #1d1d1d!important;
    color: #ddd!important;
    box-shadow: none;
}
.graphiql-container .toolbar-button:hover, .graphiql-container .execute-button:hover, .docExplorerShow {
    transition: all .2s;
    background: #111!important;
    color: #fff!important;
    box-shadow: none;
}
ul.CodeMirror-hints {
    background: #252525;
}
.CodeMirror-hints li {
    color: #eee!important;
    border: none;
}
.CodeMirror-hints p, .CodeMirror-hints .content {
    color: #bbb!important;
}
.CodeMirror-hints li.CodeMirror-hint-active {
    color: #fff!important;
    background: #384;
}
    .CodeMirror-linenumber, .graphiql-container .history-contents button {
      color: #ddd;
    }
  .graphiql-container .title {
      color: #EEE;
  }
  .graphiql-container .docExplorerShow,
  .graphiql-container .historyShow,
  .graphiql-container .doc-explorer {
      background: #444;
      color: #EEE;
  }
  .graphiql-container .variable-editor-title {
      border-color: #333;
      background: #222;
      color: #CCC;
  }
  .graphiql-container .result-window .CodeMirror-foldgutter,
  .graphiql-container .result-window .CodeMirror-foldgutter-open:after,
  .graphiql-container .result-window .CodeMirror-foldgutter-folded:after {
      background-color: #444;
  }
  .graphiql-container .topBar,
  .graphiql-container .docExplorerShow {
      border-color: #444;
  }
  .graphiql-container .result-window .CodeMirror-gutters,
  .graphiql-container .resultWrap {
      border-color: #555;
  }
  .graphiql-container .doc-explorer-title-bar,
  .graphiql-container .history-title-bar {
      height: 36px;
  }
  .graphiql-container .doc-explorer-contents,
  .graphiql-container .history-contents {
      background-color: #222;
      top: 49px;
  }
  .graphiql-container .search-box > input {
      background-color: #666;
      color: #FFF;
  }
  .CodeMirror div.CodeMirror-cursor {
      border-left: 2px solid #ddd;
  }
  .CodeMirror div.CodeMirror-selection {
       background-color: #666!important;
  }
  .graphiql-container .keyword {
      color: #F70;
  }
  .graphiql-container .field-name {
      color: #3ca0ff;
  }
  .graphiql-container .doc-explorer-back {
      color: #FFF;
  }
  .graphiql-container .arg-name {
      color: #fff;
  }
  .graphiql-container .type-name {
      color: #ffc30c;
  }
  .graphiql-container .doc-explorer-back:before, button.docExplorerShow:before {
      border-left: 2px solid #ccc!important;
      border-top: 2px solid #ccc!important;
  }
  .cm-keyword, div[style*="color: rgb(177, 26, 4);"] {
      color: #f9b456!important;
  }
  .cm-def, div[style*="color: rgb(177, 26, 4);"] input {
      color: #ccfd51!important;
  }
  .cm-property, span[style="color: rgb(31, 97, 160);"], .graphiql-container .history-contents input[style*="color: rgb(214, 66, 146);"], .graphiql-container .history-contents span[style*="color: rgb(214, 66, 146);"] {
      color: #e3e3e3!important;
  }
  .cm-attribute, span[style="color: rgb(139, 43, 185);"] {
      color: #68d8ff!important;
  }
  .cm-string, .cm-string-2 {
      color: #f2d625;
  }
  .cm-builtin {
      color: #fff;
  }
  .cm-qualifier {
      color: #1dd761;
  }
  .graphiql-container div.CodeMirror span.CodeMirror-matchingbracket,
  .cm-punctuation, .cm-ws, .cm-operator, .CodeMirror pre {
      color: #999;
  }
  .graphiql-container .execute-options, select {
      background-color: #444!important;
      color: #DDD!important;
      border: none;
  }
  .graphiql-container .execute-options > li.selected,
  .graphiql-container .toolbar-menu-items > li.hover,
  .graphiql-container .toolbar-menu-items > li:active,
  .graphiql-container .toolbar-menu-items > li:hover,
  .graphiql-container .toolbar-select-options > li.hover,
  .graphiql-container .toolbar-select-options > li:active,
  .graphiql-container .toolbar-select-options > li:hover,
  .graphiql-container .history-contents > p:hover,
  .graphiql-container .history-contents > p:active {
      background: #6f6f6f;
      color: #fff;
  }
  .graphiql-container .doc-explorer-title-bar,
  .graphiql-container .history-title-bar {
      background: #222;
      color: #EEE;
  }
  .graphiql-container .doc-explorer-contents,
  .graphiql-container .history-contents {
      background-color: #181818;
      top: 48px;
      color: #ccc;
  }
  .graphiql-container .doc-explorer-contents,
  .graphiql-container .history-contents {
      border-top: 1px solid #555;
        border: none;
  }
  .graphiql-container .history-contents p {
      color: #EEE;
  }
  .CodeMirror-gutters {
      background-color: #444;
  }

  /* TO RESTORE THE PRETTIFY BUTTON, REMOVE THIS NEXT ITEM */
  .graphiql-container .toolbar-button:first-child {
      display: none;
  }

.graphiql-container .search-box > input {
    background-color: #222!important;
}
.graphiql-container .search-box {
    border: none;
}
.graphiql-explorer-root > div {
/*     border: none!important; */
    border-color: #333!important;
    padding-top: 10px;
}
.graphiql-container .topBar, .CodeMirror-gutters div, button.docExplorerShow, .doc-explorer-back {
  background: #2b2b2b!important;
  border: none!important;
}
.doc-explorer-back, button.docExplorerShow {
  background: #2b2b2b!important;
  border: none!important;
}
button.docExplorerShow:hover, button.doc-explorer-back:hover {
  background: #222!important;
}
.doc-explorer-back {
  transition: all .2s;
  border: none!important;
}
button.doc-explorer-back:hover {
  transition: all .2s;
}

/* ----- ----- ----- ----- ----- ----- ----- ----- ----- -----
   Webkit-only here (Chrome, Opera, Steam)
   ----- ----- ----- ----- ----- ----- ----- ----- ----- ----- */
::-webkit-scrollbar {
    background-color: transparent !important;
    height: 12px !important;
    width: 12px !important;
}
::-webkit-scrollbar-button {
    display: none !important;
}
::-webkit-scrollbar-track {
    background-color: transparent !important;
}
::-webkit-scrollbar-track:horizontal {
    margin-left: 4px !important;
    margin-right: 4px !important;
}
::-webkit-scrollbar-track:vertical {
    margin-top: 4px !important;
    margin-bottom: 4px !important;
}
::-webkit-scrollbar-track-piece {
    background-color: transparent !important;
    border-radius: 0px !important;
}
::-webkit-scrollbar-thumb {
    background-color: #333 !important;
    border-radius: 16px;
}
::-webkit-scrollbar-thumb:hover {
    background-color: #3b3b3b !important;
}
}
