/* latin */
@font-face
{
	font-family: 'Lato';
	font-style: normal;
	font-weight: 700;
	src: local('Lato Bold'), local('Lato-Bold'), url(https://fonts.gstatic.com/s/lato/v14/S6u9w4BMUTPHh6UVSwiPGQ3q5d0.woff2) format('woff2');
	unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}

/*General*/
body, html
{
	font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
	font-size: 12px;
	line-height: 1.2;
	color: #ccc;
	text-align: center;
	background-color: #222;
}

a
{
	color: #da3;
}

a:hover
{
	color: #eb7
}

a:visited
{
	color: #986
}

font
{
	font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
}

input, select, textarea
{
	font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
	font-size: 1rem;
	border-radius: 0px;
	border: 1px solid #da3;
	padding: .5em;
	background-color: #333;
	outline: 0;
	vertical-align: middle;
	color: #ccc;
}

FORM#search_form INPUT[type=text]
{
	padding: .5em;
	width: 80%;
	max-width: 30em;
}

FORM#search_form INPUT[type=submit]
{
	max-width: 20%;
}

.inline-fields li:nth-child(2)
{
	padding-top: 1em;
}

input[type="text"], [type="edit"], [name="req"]
{
	box-shadow: none;
}

input[type="text"]:focus, [type="edit"]:focus, [name="req"]:focus
{
	border-color: #eb7;
}

td[colspan='4'] input[name="s"]
{
	max-width: 48em;
}

select
{
	-webkit-appearance: none;
	-moz-appearance: none;
	appearance: none;
	margin: 10px;
	box-shadow: none;
	padding: .5em 1em .5em .5em;
	position: relative;
	background: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='%23ced4da'><polygon points='0,0 100,0 50,50'/></svg>") no-repeat;
	background-size: 12px;
	background-position: calc(100% - 10px) 55%;
	background-repeat: no-repeat;
	padding-right: 30px;
	background-color: #222;
}

select:before
{
	content: 'x';
	position: absolute;
	top: 0;
	right: 0;
}

input[type="submit"], button, td#info h2 a, td[bgcolor="#A9F5BC"] a
{
	border: 0;
	background-color: #1971c2;
	color: #ccc;
	font-size: .825rem;
	text-transform: uppercase;
	padding: .65rem 1rem;
	box-shadow: none;
	border-radius: 0px;
}

input[type="submit"]:hover, button:hover, td#info h2 a:hover, td[bgcolor="#A9F5BC"] a:hover
{
	background-color: #1864ab;
	box-shadow: none;
}

label
{
	margin-right: .5em;
}

form[name="libgen"]
{
	line-height: 1.8;
}

form + font[color="grey"]
{
	display: inline-block;
	text-align: center;
	margin: 2rem 0 1rem;
}

font[color="#A00000"]
{
	font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
	color: #ccc;
}

table
{
	text-align: left;
	font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
}

table.c
{
	font-family: inherit;
	font-size: .8rem;
}

tr
{
	border-bottom-color: #999
}

td
{
	vertical-align: top;
}

/*non-fiction*/
table.c td
{
	padding: .5em 1em;
	color: #ccc;
}

table.c td td
{
	padding: 0 .25em;
}

table td tr
{
	background-color: transparent !important;
}

/*fiction*/
table[rules="rows"] td
{
	padding: .5em;
}

table[rules="rows"] td[width="400"]
{
	font-weight: bold;
}

table[rules="rows"] td[width="150"]
{
	font-style: italic;
}

table[rules="rows"] td[width="150"] b
{
	font-style: normal;
}

.catalog TD
{
	border: 1px solid #ccc;
}

.catalog tr:nth-child(even) td
{
	background-color: #222;
}

#mlink
{
	display: inline-block;
	margin-right: .5em;
}

tr[bgcolor="#C0C0C0"] td, font + table[rules="rows"] td
{
	padding: 1em;
}

tr[bgcolor="#C0C0C0"] td:first-child, font + table[rules="rows"] td:first-child
{
	text-transform: uppercase;
	font-size: .8em;
}

.catalog_authors li a
{
	text-transform: uppercase;
	font-size: .8em;
}

.catalog tr td:nth-child(2)
{
	font-size: .85em;
	font-style: italic;
	color: #ccc;
}

.catalog tr td:nth-child(4), .catalog tr td:nth-child(5)
{
	font-size: .85em;
	color: #ccc;
}

tr[valign="top"] td
{
	padding: .25em .5em;
}

tr[valign="top"][bgcolor="#C0C0C0"] td a, thead td
{
	font-size: .75rem;
	text-transform: uppercase;
	color: #fff;
}

tr[valign="top"][bgcolor="#C0C0C0"], thead tr
{
	background-color: #553;
}

tr[bgcolor="#C6DEFF"], table[rules="rows"] tr:nth-child(even)
{
	background-color: #222;
}

tr[bgcolor="#C6DEFF"] td
{
	background-color: #333;
}

td[width="500"] a
{
	color: #dd5;
}

td[width="500"]
{
	width: auto;
}

a[title]
{
	font-size: 1rem;
	font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
}

a[title] font
{
	font-size: .8rem;
	color: #ccc;
	font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
}

p[style="margin:1em 0;text-align:center"], p[style="margin:1em 0;text-align:center"] a, caption, caption a
{
	color: #ccc;
	text-decoration: none;
	font-style: italic;
	padding: 1em;
}

.paginator
{
	margin: 2em;
}

.paginator span strong, .paginator .current_page_mark
{
	background-color: #339af0;
}

a[href*="/scimag/?q="], a[href*="/fiction/?q="], .letter_selector LI, .page_selector + a
{
	border-radius: 0px;
	padding-top: 3px;
	padding-bottom: 3px;
	border-color: #db4;
	box-shadow: none;
	display: inline-block;
}

a[href*="/scimag/?q="]:hover, a[href*="/fiction/?q="]:hover, .letter_selector LI:hover, .page_selector + a:hover
{
	border-color: #228be6;
}

a[href*="author"]
{
	text-transform: uppercase;
	color: #9d3;
}

font[color="green"]
{
	color: #ccc !important;
}

/* Single book view */
.gr__library1_org table, .gr__93_174_95_29 table
{
	max-width: 40em;
	width: 100%;
}

td#ad
{
	display: none;
}

img[width="240"]
{
	display: block;
	margin: 0 auto;
}

td#info
{
	max-width: 40em !important;
	font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
}

td#info h1
{
	text-align: center;
}

td#info h2 a
{
	display: inline-block;
	border-radius: 0px;
	text-decoration: none;
}

td[bgcolor="#F5F6CE"]
{
	display: none;
}

td[bgcolor="#A9F5BC"]
{
	background-color: #222;
	margin-bottom: 1em;
	padding: .5em;
	border-bottom: 2px solid #ccc;
}

td[bgcolor="#A9F5BC"] a
{
	color: #ccc;
	display: inline-block;
	padding: .25em 2em;
	line-height: 1;
	text-decoration: none;
}

td[bgcolor="#F5F6CE"] + td
{
	text-align: center;
	padding: .5em;
}

td[bgcolor="brown"]
{
	background-color: #222;
}

input[size="9"]
{
	width: 20em;
	margin: 0 1em;
}

#main
{
	max-width: 45em;
}

#main tr:nth-of-type(4) td:nth-of-type(1)
{
	display: none;
}

#main tr:nth-of-type(4) td:nth-of-type(2)
{
	padding: 1em;
}

#main td[colspan="2"]
{
	width: 40em;
}

/*Scientific articles*/
table[width="1024"] tbody td[width="200"]
{
	text-transform: uppercase;
	font-size: .8em;
	color: #ccc;
}

table[width="1024"] tbody td[width="300"]
{
	font-size: 1.1em;
}

/*----Menu------*/
ul#menu
{
	background-color: #222;
	border: 0;
	text-align: center;
	border-bottom: 1px solid #ccc;
	width: 100%;
	padding: 0;
	height: auto;
	border-radius: 0px;
	margin-bottom: 10px;
}

#menu li, #menu li:hover
{
	padding: 0;
	margin: 0;
	display: inline-block;
	float: none;
	border: 0;
}

#menu li:hover
{
	background-color: #222;
	color: #593;
}

#menu li .drop, #menu li:hover .drop
{
	padding: 8px 30px;
	font-size: .85rem;
	background: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='%23adb5bd'><polygon points='0,0 100,0 50,50'/></svg>") no-repeat;
	background-size: 12px;
	background-position: calc(100% - 10px) 55%;
	background-repeat: no-repeat;
}

#menu li a
{
	display: block;
	font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
	padding: .6em 2em;
	border-radius: 0px;
	color: #fff;
}

#menu > li a:hover
{
	color: #db4;
	box-shadow: none;
	padding: .6em 2em;
	border: 0;
}

#menu li .drop:hover
{
	padding-right: 30px;
	background: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='%23145591'><polygon points='0,0 100,0 50,50'/></svg>") no-repeat;
	background-size: 12px;
	background-position: calc(100% - 10px) 55%;
	background-repeat: no-repeat;
	color: #db4;
	box-shadow: none;
	background-color: #222;
}

.dropdown_3lettercolumn, .dropdown_1column, .dropdown_2columns, .dropdown_3columns, .dropdown_4columns, .dropdown_5columns, .dropdown_6columns
{
	z-index: 100;
	margin: 0px auto;
	float: left;
	position: absolute;
	top: -5px;
	left: -999em;
	text-align: left;
	padding: 5px;
	/* border-top: none; */
	background: #333;
	box-shadow: 0 3px 3px #db4;
	border-radius: 0px 5px 5px 5px;
}

#menu li ul li
{
	line-height: 1.3;
	padding: 0;
	width: 98%;
	text-shadow: none;
}

#menu li ul a
{
	font-family: Lato, sans-serif;
	font-size: 14px;
	padding: .25em !important;
}

#menu li:hover div a
{
	color: #ccc;
}

#menu li ul a:hover, #menu li div a:hover, #menu li li:hover a, #menu li div div a:hover
{
	box-shadow: none;
	background-color: #222;
	color: #db4 !important;
	text-shadow: none;
	border-radius: 0;
}

#menu li .dropdown_3lettercolumn .greybox li, #menu li .dropdown_3lettercolumn .greybox li:hover
{
	border: 0;
	padding: 0;
	text-align: center;
}

#menu li .dropdown_3lettercolumn .greybox li a
{
	background-color: #222;
	border-radius: 5px;
	font-weight: bold;
}

#menu li .dropdown_3lettercolumn .greybox li a:hover
{
	background-color: #339af0;
}

#menu p, #menu h2, #menu h3, #menu ul li
{
	font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
	line-height: 1.4;
	text-shadow: none;
}

#menu h3
{
	font-size: 15px;
	padding: .25em !important;
	margin: 0;
	border-bottom: 1px solid #ccc;
}

#menu li .greybox li
{
	background: none;
	border: none;
	margin: 0 2px 2px 0;
	padding: 0;
	border-radius: 0;
}

#menu li .greybox li a.drop
{
	background-color: #222;
	padding: .75em;
	border-radius: 3px;
}

#menu li .greybox li:hover
{
	background: none;
	border: none !important;
	color: #ccc;
}

#menu li .greybox li:hover a, #menu li:hover a
{
	padding: 0;
	color: #ccc;
	text-shadow: none;
}

#menu li .greybox li a.drop:hover
{
	background-color: #339af0;
	color: #ccc;
	padding: .75em;
}

table[width="600"]
{
	font-family: Lato, Roboto, "Open Sans", Calibri, sans-serif;
	font-size: .9rem;
	border: 0 !important;
	line-height: 1.2;
	background-color: #222 !important;
	border-color: #ccc !important;
	padding: 0;
	box-shadow: none;
	border-collapse: collapse;
}

table[width="600"] td
{
	vertical-align: top;
}

table[width="600"] tr:first-child td:nth-child(3)
{
	font-weight: bold;
}

table[width="600"] tr:first-child td:nth-child(2), table[width="600"] tr:nth-child(n + 2) td:nth-child(1)
{
	font-size: .8rem;
	text-transform: uppercase;
}

table[width="600"] td[ROWSPAN="2"]
{
	font-size: .95rem !important;
	font-style: italic;
	line-height: 1.3;
	text-transform: none !important;
}
}
