css:
@-moz-document url-prefix("https://portal.dhw.ac.jp/") {
body {
	background:black;
    color:white;
}

/* ログイン */
.loginForm {
    background: black;
	border: solid 1px white;
}
.information {
	background: black;
}

/* メイン画面 */

/* 各種ボタン */
.ui-widget-content a{
	color:white;
}

/* パスワード変更ボタン・その他各種ブロック内背景*/
.ui-widget .ui-widget{
	background:black;
    color:white;	
}

/* 重要・期限ボタン */
#portalSupport .ui-widget-header {
	background:black;
    color:white;
}
#portalSupport .ui-tabs .ui-tabs-nav li a{
	color:white;
}
#portalSupport .ui-state-active a, #portalSupport .ui-state-active a:link, #portalSupport .ui-state-active a:visited {
    background:black;
    color:white !important;
}

/* 重要・期限の詳細 */
#portalSupport .ui-tabs .ui-tabs-panel {
	background:black;
}
.ui-datalist .ui-datalist-empty-message {
	background:black;
	color:white;
}

.textDate {
	color: white;
}
#portalCont .ui-widget-content a.ui-commandlink, #portalSupport .ui-widget-content a.ui-commandlink {
	color: white !important;
}
.signPortal {
	background: black;
}

/* 日表示・月表示・履修授業ボタン */
#portalCont .ui-tabs .ui-tabs-nav li a {
	background:black;
    color:white;	
}
/* 日表示・月表示・文字白 */
.eventJugyo, .eventJugyo .fc-time, .eventJugyo .fc-title {
	color: white !important;
}
#portalCont ul.ui-widget-header{
    background:black;
    color:white;	
} /* 選択中 */
#portalCont .ui-state-active a, #portalCont .ui-state-active a:link, #portalCont .ui-state-active a:visited{
	color: white !important;
}	
/* 日表示・月表示・履修授業 内にあるインフォメーション+画面中央にある履修授業一覧の枠線 */
#portalCont .ui-tabs .ui-tabs-panel {
	background:black;
    color:white;	
}
/* 日表示・月表示・履修授業+リンクの詳細 */
.portalSub .portalSubDetail{
	background:black !important;
    color:white;
}
/* 日表示の予定一覧 */
#portalCont .portalMain > .ui-panel-content.ui-widget-content {
	color:white;
}
.portalMain h3 .portalTitle {
    color:white !important;
}
#portalCont .ui-widget-content:not(ul) {
	background:black !important;
    
}
#funcContent .ui-button-text-only .ui-button-text {
	color: white;;
}

/* 月表示の予定一覧 */
.portalSchedule .fc-toolbar .fc-center h2 {
	background:black;
}
#portalCont .ui-widget-header, .portalSchedule .ui-widget-header {
	background: black;
}
.portalSchedule .fc-day-header {
    color: white !important;
}
#portalCont .ui-state-default, .portalSchedule .ui-state-default {
	background: black;
}
#portalCont .ui-button, .portalSchedule .ui-button {
    border: solid 1px white !important;
    color: white;
}
/* マイスケジュール編集 */
div[id*="bsa00501"].ui-dialog .ui-dialog-titlebar, div[id*="bsa00601"].ui-dialog .ui-dialog-titlebar, div[id*="bsa00701"].ui-dialog .ui-dialog-titlebar, div[id*="bsa01001"].ui-dialog .ui-dialog-titlebar {
	background:black;
    color:white;
}
html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, embed, figure, figcaption, footer, header, hgroup, menu, nav, output, ruby, section, summary, time, mark, audio, video {
	color:white;
}
/* マイスケジュール編集→繰り返すチェック有り */
div[id*="bsa00501"].ui-dialog .scheduleRepeatInputArea, div[id*="bsa00601"].ui-dialog .scheduleRepeatInputArea, div[id*="bsa00701"].ui-dialog .scheduleRepeatInputArea, div[id*="bsa01001"].ui-dialog .scheduleRepeatInputArea {
	background: black !important;
}


/* 設定 */

/* テキスト→個人設定 */
.ui-breadcrumb ul li span {
	color:white;
}
/* メール設定 */
/* 背景 */
.ui-accordion-content.ui-widget-content {
	background: black !important;
}
.ui-widget-content {
	background: black;
    color:white;
}
/* メール設定アドレス・背景時刻設定・配信停止設定　ツールバー */
.ui-accordion-header.ui-state-active {
    background: black;
    color:white;
}
.ui-state-default a, .ui-state-default a:link, .ui-state-default a:visited {
	color:white;
} /* 選択中 */
.ui-tabs .ui-tabs-nav li.ui-tabs-selected a, .ui-tabs .ui-tabs-nav li.ui-state-disabled a, .ui-tabs .ui-tabs-nav li.ui-state-processing a {
	color: red;
    font-weight: bold; 
}

/* エクセルみたいなところ */
.ui-datatable-resizable thead th, .ui-datatable-resizable tbody td, .ui-datatable-resizable tfoot td {
    background: black;
    color: white;
}
.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    background:black;
    color:white;
    border: 1px solid white;
}
/* loading ウインドウ */
.ui-blockui-content.ui-widget-content {
	background:black !important;
	color:white !important;
	border: 1px solid white !important;
}
/* ログアウト ウインドウ */
.ui-confirm-dialog.ui-widget-content{
	background: black !important;
}
.ui-dialog-content.ui-widget-content{
	background: black !important;
}
.ui-dialog .ui-confirm-dialog-message {
	color:white;
}
html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, embed, figure, figcaption, footer, header, hgroup, menu, nav, output, ruby, section, summary, time, mark, audio, video {
	background:black;
}


/* その他一括 */

a {
	background:black;
    color:white;	
}

table {
	background: black;
    color:white;
}

/* フッター */
footer {
	background:black;
    color:white;	
}

#menu {
	background:black;
    color:white;	
}

}
