css:
@-moz-document domain("javascript.ru"), domain("javascript.info") {
body {
    background: #1b1b1f;
    color: #e6e6e6;
}
.comments__header-title, .quiz-timeline__number_current~.quiz-timeline__number, .courses-frontpage-list__title, .courses-masters, .courses-mix, .courses-masters__text, .intro, .course-master, .tabbed-pane .tabbed-pane__body, .frontpage-content {
    color: #e6e6e6;
}
.sidebar {
    background: #25252a;
    border-right: 1px solid #3f3f3f;
}
.sidebar__link {
    color: #e4e2e1;
}
.breadcrumbs__link {
    color: #6aa9e8;
}
.main code {
    background: #3b3f46;
}
.main .block-highlight .mask {
    background: #535353;
    outline: 1px solid #535353;
}
pre[class*=language-] {
    background: #282c34;
}
.line-numbers .line-numbers-rows {
    background: #24272e;
}
.line-numbers-rows>span:before, .line-numbers .line-numbers-rows:after {
    background: #24272e;
}
.token.boolean, .token.number, .token.property, .token.tag {
    color: #d41a81;
}
.language-css .token.string, .style .token.string, .token.entity, .token.operator, .token.url {
    color: #b9ab9c;
}
color black, pre[class*=language-], pre[class*=language-]>code {
    text-shadow: none;
}
.formatted ul>li:before, .courses-mix ul>li:before {
    color: #adadad;
}
.token.attr-name, .token.selector, .token.string {
    color: #88b037;
}
.important, .task__answer {
    border: 3px solid #9d9d9d;
}
.tasks__task {
    border-top: 2px solid #9d9d9d;
}
.quiz-question__body {
    border: 3px solid #eee;
}
.about-layout__title, .courses-certificate__title {
    color: #fff;
}
.about-contacts {
    background: #25252a;
}
.frontpage-content {
    background: #1b1b1f;
}
.frontpage-content .list__link, .frontpage-content__title, .courses-list__item-link:visited, .courses-list__item-link {
    color: #e4e4e4;
}
.frontpage-banner:before {
    border-bottom: 19px solid #1b1b1f;
}
.frontpage-content__container_colored {
    background-color: #313131;
}
.profile-brief__title, .sidebar_profile .sidebar__link {
    color: #f6f6f6;
}
.sidebar__navigation-link_active, .quiz-selector__title {
    color: #ff6a5b !important;
}
.course-feedback_external {
    border-color: #767676;
}
.formatted table tr:nth-child(2n), .quiz-selector, .subscribe_fancy {
    background: #25252b;
}
.formatted table tr, .quiz-selector .quiz-selector__item {
    border-bottom: 1px solid #6d6d6d;
}
.toolbar .toolbar__button {
    background: #5e5e5e;
}
.courses-frontpage-list {
    background-color: #292323;
}
.courses-faq li.courses-faq__question {
    background-color: #272932;
}
.courses-faq__answer, .courses-faq__answer:before {
    background: #1b1b1f;
}
.slider__container:after, .slider__container:before {
    display: none;
}
.list :visited, .lessons-list__link:visited {
    color: #ead3ff;
}
.list :link, .course-master__name, .courses-master__name, .lessons-list__link {
    color: #a7d3ff;
}
.lessons-list {
    border: 4px solid #4b4b4b;
    background: #2c2c30;
}
.lessons-list__lesson {
    border-bottom: 1px solid #4f4f4f;
}
.tabbed-pane .tabbed-pane__body, .courses-certificate, .multiselect__dropdown-container, .course-nav.sticky, .video-player__info {
    background-color: #2c2c30 !important;
}
.video-player__info {
    border: none;
}
.video-player__control {
    fill: #cccccc;
}
.course-nav__bar, .screencast-player__nav {
    border-left: 1px solid #828282;
}
.screencast-tabs {
    border-bottom: 1px solid #828282;
}
.multiselect__active-button {
    color: #4d4d4d;
}
.courses-signup {
    background: #fbf3e5 !important;
    color: #000 !important;
}
.courses-feedback-slides__review, .courses-mix.courses-how .section, .course-master {
    background-color: #2c2c30;   
}
h3.courses-feedback-slides__title {
    color: #fff;
}
.courses-feedback-slides__content, .courses-feedback-slides__address {
    color: #e1e1e1;
}
}

@-moz-document url("https://learn.javascript.ru/courses") {
:link {
    color: #59acff;
}
}
