css:
@-moz-document url-prefix("https://kotlinlang.org"), url-prefix("http://kotlinlang.org") {
/*home page tiles*/
.kotlin-overview-section._get-kotlin .get-kotlin-options-list .option-item, 
.kotlin-overview-section._testimonials .testimonial, 
.kotlin-overview-section._features .kotlin-features-list .kotlin-feature {
    background-color: #222;
}
.kotlin-overview-section._features, .kotlin-overview-section._testimonials {
    background-color: #333;
    background-image: none;
}
.kotlin-overview-section._features .kotlin-features-list .feature-description {
    color: #B7D6F5;
}
.kotlin-overview-section._features .kotlin-features-list .feature-example-link {
    color: #87A7CD;
}
.kotlin-overview-section._features .kotlin-features-list .feature-example-link:hover {
    color: #EEE;
}

/*main body*/
body {
    color: #EEE;
    background-color: #222;
}
/*keywords in the body*/
.page-content .keyword {
    color: #CC7832;
}
/*code expressions in the body*/
.page-content li>code, 
.page-content p>code {
    color: #A9D7E6;
    background-color: #2B2B2B;
}
.page-content table td, .page-content table th {
    background-color: #2B2B2B;
}
.page-content blockquote,
.page-content blockquote.note {
     background-color: #2B2B2B;
}

/*navigation buttons*/
.docs-nav .nav-item-text {
    color: #B7D6F5;
    background-color: #252525;
    border-color: #555;
}

.docs-nav .nav-item {
    background-color: transparent;
    background-image: none;
}

.docs-nav .nav-item.is_active, .docs-nav .nav-item:hover {
    background-color: transparent;
}

/*selected navigation button*/
.docs-nav .nav-item.is_active .nav-item-text {
    color: #252525;
    background-color: #555;
    border-color: #555;
}
/*triangle under navigation button*/
.docs-nav .nav-item-text:after {
    border-top-color: #555;
}
.docs-nav .nav-item-text:before {
    border-top-color: #555;
}
.docs-nav .nav-item.is_active {
    border-top-color: #555;
}

/*navigation panel on the left*/
.tree-branch {
    border-color: #555;
}
.tree-branch-title {
    background-color: #222;
    color: #B7D6F5;
}
.tree-branch-title:hover {
    background-color: #222;
    color: #FFF;
}
.tree-branch-title.is_active {
    background-color: #222;
    color: #B7D6F5;
}
.tree-branch-title.is_active:hover {
    background-color: #222;
    color: #FFF;
}
.tree-branch._opened {
    background-color: #252525;
}
.tree-leaf-title.is_active {
    color: #EEE;
}
.tree-branch.js-item.js-branch._opened  a, 
a{
 	color: #87A7CD;   
}
.tree-branch.js-item.js-branch._opened  a:hover, 
a:hover{
 	color: #EEE;   
}

/*code blocks*/
.CodeMirror-code,
.CodeMirror-code > div,
.unmodifiable-line,
.CodeMirror-code span.cm-def, .cm-s-default .cm-def,
.CodeMirror-code span.cm-type, .cm-s-default .cm-type,
.CodeMirror-code span.cm-variable, .cm-s-default .cm-variable,
.CodeMirror-code span.cm-variable-2, .cm-s-default .cm-variable-2,
.CodeMirror-code span.cm-variable-3, .cm-s-default .cm-variable-3,
.CodeMirror-code span.cm-operator, .cm-s-default .cm-operator,
.CodeMirror-code span.cm-property, .cm-s-default .cm-property
.CodeMirror-code span.cm-qualifier,
.page-content .signature,
.api-panel__select .dropdown-selected-value {
    color: #A9B7C6;
    background: #2B2B2B;
}
.executable-fragment {
    border: 1px solid #444;
}
.CodeMirror-gutters {
    border-right: 1px solid #444;
}
.unmodifiable-line {
    background-color: transparent !important;
}
.CodeMirror-code span.cm-keyword, .cm-s-default .cm-keyword {
    color: #CB772F;
}
.CodeMirror-code span.cm-string, .cm-s-default .cm-string {
    color: #A5C25C;
}
.CodeMirror-code span.cm-comment, .cm-s-default .cm-comment {
    color: #808080;
}
.CodeMirror-code span.cm-number, .cm-s-default .cm-number {
    color: #6897BB;
}
.CodeMirror-code span.cm-atom, .cm-s-default .cm-atom {
    color: #9876AA;
}
.CodeMirror-code span.cm-tag {
    color: #E8BF6A;
}
.CodeMirror-code span.cm-attribute {
    color: #BABABA;
}
.CodeMirror-code span.cm-meta {
    color: #E8BF6A;
}
.CodeMirror,
.CodeMirror-gutters,
.CodeMirror pre, 
.CodeMirror-scroll, 
.CodeMirror-hscrollbar {
    color: #A9B7C6;
    background: #2B2B2B;
    font-size: 14;
}
.standard-output {
    color: #EEE!important;
}
.error-output {
    color: #CD3524!important;
}
/*dropdowns*/
.dropdown-items {
 	background: #2B2B2B!important;
}
.dropdown.js-dropdown {
 	background: #252525!important;
}

