css:
@-moz-document regexp("http?s\\:\\/\\/(www)?\\.pygame\\.org\\/docs(.*)") {
  div.document {
    background-color: transparent;
  }
  .header,
  .logo,
  .pagelinks{
    background-color: transparent !important;
  }
  .header table,
  .related {
    background: rgba(0, 0, 0, 0.3) !important;
  }

  .header table,
  .pagelinks {
    border: none !important;
  }
  .logo {
    border-right: 1px solid #262626 !important;
  }
  a,
  .related a {
    color: #7878fa !important;
  }
  a:hover,
  .related a:hover {
    color: #78fafa !important;
  }
  a:visited,
  .related a:visited {
    color: #af78fa !important;
  }
  .top input {
    background-color: rgba(0, 0, 0, 0.7) !important;
    color: #78fa78;
    border: 0;
    border-bottom: 1px solid #78fa78;
    border-radius: 0;
    font-size: 10px;
    padding-top: 1px;
    padding-bottom: 1px;
  }
  .documentwrapper {
    max-width: 700px;
    color: #efefef;
  }
  .addcomment input,
  a.commentButton {
    background-color: #132613 !important;
    color: #78fa78;
  }
  .descclassname {
    color: #98fa98;
  }
  .descname {
    color: #9898fa;
  }
  .sig-paren {
    color: #fafa98;
  }
  .tooltip-content {
    background-color: rgba(0, 0, 0, 0.9) !important;
    border-radius: 0px !important;
    border: 1px solid #262626 !important;
  }
  .highlight {
    background-color: transparent !important;
  }
  .highlight pre {
    background-color: rgba(0, 0, 0, 0.3);
  }
  .highlight .n { color: #fafaaf !important; } /* name */
  .highlight .o { color: #fafafa !important; } /* operator */
  .highlight .p { color: #787878 !important; } /* parentheses */
  .highlight .mi { color: #7878fa !important; } /* idk but may be number */
  .highlight .si { color: #afaffa !important; } /* looks like this is string format */
  .highlight .s1 { color: #7878fa !important; } /* string itself */
  .highlight .nb { color: #78fa78 !important; } /* name of built-in */
  .highlight .kc { color: #78fafa !important; } /* constant or keyword */
}

@-moz-document regexp("http?s\\:\\/\\/(www)?\\.pygame\\.org\\/docs(.*)") {
  body {
    background-color: #131313;
    background-image: url(data:image/png;base64,);
    background-repeat: repeat;
    color: #787878 !important;
  }
}
