css:
@-moz-document url-prefix("http://hdrezka.ag"), url-prefix("https://hdrezka.cm"), url-prefix("http://hdrezka.name"), url-prefix("http://hdrezkayou.com/"), url-prefix("https://rezka.ag/"),regexp("https://rezka"),regexp("https://hdrezka") {

/*1*/
body,
body.has-brand #wrapper,
.b-translators__block {
	background-color: #1b1919;
}
body.active-brand #wrapper {
	width: 100%;
	min-width: 100%;
}
.b-content__main {
    float: left;
    width: 100%;
    background-color: #1b1919;
}
.b-content__columns.pdt {
    padding-top: 38px;
    background-color: #1b1919;
}

/*поиск*/
#top-nav {
    position: relative;
    top: 0;
    background-color: #313131;
}
.b-sidecover {
    -webkit-border-radius: 2px;
    -khtml-border-radius: 2px;
    -moz-border-radius: 2px;
    -o-border-radius: 2px;
    border-radius: 2px;
    background: #1b1919;
    border: 1px solid #312f2f;
    margin-bottom: 15px;
    min-height: 70px;
    padding: 5px;
    position: relative;
    box-shadow: 1px -2px 1px 0px black;
}
.b-post__infotable_left {
    float: left;
    margin-left: -260px;
    width: 260px;
}
.b-sidelist__holder {
    /* overflow: hidden; */
    width: 160%;
    margin-left: -100px;
}
/*r*/
#b46fec00c0 {
	display: none !important;
    padding: 0px !important;
    width: 0px !important;
    height: 0px !important;
}

#ownplayer-link-show {
	display: none !important;
    padding: 0px !important;
    width: 0px !important;
    height: 0px !important;
}
#player > div.b-post__status_wrapper.clearfix {
	display: none !important;
    padding: 0px !important;
    width: 0px !important;
    height: 0px !important; 
} 
.dom_overlay_container,
.animation_container,
.canvas,
.mgbox {
	display: none !important;
    padding: 0px !important;
    width: 0px !important;
    height: 0px !important;
}
/*Щрифт*/
a {
    color: #21baca;
    text-decoration: none;
}
html, body, div, span, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, abbr, address, cite, code, del, dfn, em, img, ins, kbd, q, samp, small, strong, sub, sup, var, b, i, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, figcaption, figure, footer, header, hgroup, menu, nav, section, summary, time, mark, audio, video, .b-post__description_text {
    margin: 0;
    padding: 0;
    border: 0;
    outline: 0;
    font-size: 100%;
    vertical-align: baseline;
    background: #67383800;
    color: #eaeae3;
}
.b-topnav__item-link.single {
    font-weight: normal;
    color: rgb(236, 42, 42);
    text-shadow: 0px 3px 2px black;
}
.b-topnav__item-link, .b-topnav__item-link:visited, .b-topnav__item-link:hover, .b-topnav__item-link.active, .b-topnav__item-link.active_section {
    color: #ffedba;
    display: inline-bl;
    font-size: 16px;
    font-weight: bold;
    padding-top: 5px;
    position: relative;
    z-index: 101;
}
.b-search__field {
    -webkit-transition: all 0.3s linear;
    -moz-transition: all 0.3s linear;
    -ms-transition: all 0.3s linear;
    -o-transition: all 0.3s linear;
    transition: all 0.3s linear;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: #4e4a4a;
    color: #b7b7b7;
    font-size: 14px;
  position: absolute;
    right: 0;
    outline: none;
    width: 100%;
    z-index: 104;
}
input, textarea, button {
    font-family: Arial,Helvetica,sans-serif;
    font-size: 14px;
    font-weight: normal;
    -webkit-font-smoothing: antialiased;
}

/*	dop	*/
a:visited {
    color: rgb(222, 213, 167)!important;
}
.b-topnav_wrapper,
.b-search__field,
.fixed-header .b-topnav_wrapper.shi,
.fixed-header .b-topnav_wrapper.shm {
	background-image: none;
	background-color: #222;
	box-shadow: none;
	border: none;
	margin-bottom: 20px;
}
.b-search__live {
	background-color: #313131;
}
.b-search__section_list .enty {
	color: #31313;
	font-weight: 600;
	text-decoration: none;
}
.b-search__section_list .enty:hover {
	color: #999;
}
.b-topnav__sub_inner {
	background-color: rgba(15, 15, 15, .98);
	box-shadow: inset 0 10px 1px -10px rgba(0,0,0,0.3), 0 0px 0px 0px rgba(0,0,0,0.3);
	border-radius: 0 0 3px 3px;
}
.b-topnav__findbest_block {
	color: #444;
}
.sod_select {
	border-radius: 3px;
	background-color: #222;
	border: 3px solid #333;
}
.sod_select:hover {
	border: 3px solid #000;
}
.sod_select .sod_option {
	background-color: #111;
}
.sod_select .sod_option.active {
	color: #444;
	background-color: #222;
}
.sod_select .sod_option.selected {
	color: #999;
	background-color: #333;
}

.b-qa__entity_text {
    color: #fdfdfd;
    font-size: 15px;
    line-height: 16px;
    padding-top: 12px;
}

.b-comment {
    -webkit-transition: background-color 2s linear;
    -moz-transition: background-color 2s linear;
    -ms-transition: background-color 2s linear;
    -o-transition: background-color 2s linear;
    transition: background-color 2s linear;
    color: #737171;
    font-size: 15px;
    line-height: 20px;
    padding: 10px 10px 10px 80px;
    background-color: #292727;
    box-shadow: 3px 2px 5px 3px black;
}
tree-list .comments-tree-list {
    background: #1b1919;
}

.comments-form {
    background: #524e4e;
}

.clearfix {
    background-color: #462d2d00;
}
.b-player {
    background: #1b1919;
    padding-top: 10px;
    position: relative;
}
.b-post__info_rates a {
    color: #ffedba;
}
/*Чат*/
#hd-comments-list>.comments-tree-list>li:nth-child(odd), #dlemasscomments #dle-comments-list>div:nth-child(odd) {
    background-color: #292828;
}
#hd-comments-list>.comments-tree-list>li:nth-child(even), #dlemasscomments #dle-comments-list>div:nth-child(even) {
    background-color: #292828;
}
#hd-comments-list>.comments-tree-list .comments-tree-list {
    background: #1b1919;
}
#hd-comments-list>.comments-tree-list .comments-tree-list .b-comment {
    border-left: 2px dashed #af5858;
    background-color: #363b42;
}
.text_spoiler {
    background-color: #292828; 
    color: rgb(88, 17, 17);
    color: #ffedba;
    padding-right: 3px;
    padding-left: 3px;
}
#vk_groups,
*[id^="heipf"] {
	display: none !important;
}
.b-user__settings .b-tabs__main_link {
    font-size: 14px;
    color: #ffedba;
}
.b-videosaves__list_item .th {
    border: 1px solid #ddd;
    border-left-width: 0;
    color: #ec4b4b;
    display: table-cell;
    font-size: 14px;
    font-weight: bold;
    padding: 10px 5px;
    vertical-align: middle;
}

.b-videosaves__list_item .new-episode.own {
    background-color: #9b364b;
}
.b-videosaves__list_item .new-episode {
    -webkit-transition: background-color 0.1s ease-in-out,color 0.1s ease-in-out;
    -moz-transition: background-color 0.1s ease-in-out,color 0.1s ease-in-out;
    -ms-transition: background-color 0.1s ease-in-out,color 0.1s ease-in-out;
    -o-transition: background-color 0.1s ease-in-out,color 0.1s ease-in-out;
    transition: background-color 0.1s ease-in-out,color 0.1s ease-in-out;
    background-color: #1b1919;
    cursor: pointer;
    display: inline-block;
    font-size: 12px;
    font-style: normal;
    font-weight: bold;
    line-height: 12px;
    margin-top: 3px;
    padding: 3px;
    color: #289e1a;
}
.b-videosaves__list_item .td {
    -webkit-transition: background 0.1s ease-in-out;
    -moz-transition: background 0.1s ease-in-out;
    -ms-transition: background 0.1s ease-in-out;
    -o-transition: background 0.1s ease-in-out;
    transition: background 0.1s ease-in-out;
    border: 1px solid #ddd;
    border-top-width: 0;
    border-left-width: 0;
    color: #ece6e6;
    display: table-cell;
    font-size: 14px;
    padding: 5px;
    vertical-align: middle;
}
.b-player {
    background: #1b1919;
    padding-top: 10px;
    position: relative;
    box-shadow: -1px 5px 7px 4px black;
}
.b-post__social_holder {
    background: #f7f7f72b;
