    height: 108px;
    background-color: #292828;
    color: #fffcf2;
}
body, body.has-brand #wrapper, .b-translators__block {
    background-color: #1b1919 !important;
    box-shadow: 0 0 13px 3px black;
}
body.has-brand .b-post__schedule_table .current-episode {
    background-color: #124c52 !important;
}
.b-navigation a {
    color: #ffedba;
}
.b-seriesupdate__block_list_item_inner .cell-1 {
    color: #000;
    font-size: 12px;
    padding-right: 5px;
    padding-left: 10px;
    background-color: #2f2d2d;
}
.b-seriesupdate__block_list_item_inner .cell-2 {
    color: #000;
    font-size: 11px;
    font-weight: bold;
    padding-right: 10px;
    text-align: right;
    white-space: nowrap;
    background-color: #2f2d2d;
}
.b-content__htitle {
    color: #b90005;
}
.b-seriesupdate__block_list_link {
    -webkit-transition: color 0.2s ease-in-out;
    -moz-transition: color 0.2s ease-in-out;
    -ms-transition: color 0.2s ease-in-out;
    -o-transition: color 0.2s ease-in-out;
    transition: color 0.2s ease-in-out;
    color: #ffedba;
    font-weight: bold;
    text-decoration: none;
}
.b-seriesupdate__block_list_item_inner .cell-2 {
    color: #21baca;
    font-size: 12px;
    font-weight: bold;
    padding-right: 10px;
    text-align: right;
    white-space: nowrap;
    background-color: #2f2d2d;
}
.b-content__filters_types .filter-link {
    -webkit-transition: background 0.1s ease-in-out,color 0.1s ease-in-out,border-color 0.1s ease-in-out;
    -moz-transition: background 0.1s ease-in-out,color 0.1s ease-in-out,border-color 0.1s ease-in-out;
    -ms-transition: background 0.1s ease-in-out,color 0.1s ease-in-out,border-color 0.1s ease-in-out;
    -o-transition: background 0.1s ease-in-out,color 0.1s ease-in-out,border-color 0.1s ease-in-out;
    transition: background 0.1s ease-in-out,color 0.1s ease-in-out,border-color 0.1s ease-in-out;
    color: #ffedba;
    cursor: pointer;
    font-size: 15px;
    float: right;
    display: block;
    padding: 10px 5px;
    text-decoration: underline;
    -webkit-font-smoothing: initial;
}
.b-content__main_filters_link {
    -webkit-transition: background 0.1s ease-in-out,color 0.1s ease-in-out;
    -moz-transition: background 0.1s ease-in-out,color 0.1s ease-in-out;
    -ms-transition: background 0.1s ease-in-out,color 0.1s ease-in-out;
    -o-transition: background 0.1s ease-in-out,color 0.1s ease-in-out;
    transition: background 0.1s ease-in-out,color 0.1s ease-in-out;
    color: #ffedad;
    cursor: pointer;
    font-size: 14px;
    float: right;
    display: block;
    padding: 10px;
    text-decoration: underline;
    -webkit-font-smoothing: initial;
}
.b-newest_slider_wrapper {
    background: #1b1919;
}
.b-newest_slider {
    background: #1b1919;
    margin: 0 auto;
    overflow: hidden;
    padding: 20px 65px 0;
    position: relative;
    width: 870px;
    z-index: 80;
}
.b-newest_slider__list {
    background: #1b1919;
    overflow: hidden;
    padding-bottom: 20px;
    width: 999999em;
}
.b-content__inline_item-cover {
    -webkit-border-radius: 2px;
    -khtml-border-radius: 2px;
    -moz-border-radius: 2px;
    -o-border-radius: 2px;
    border-radius: 2px;
    background: #484545;
    /* border: 1px solid #ad9999; */
    padding: 3px;
    position: relative;
    box-shadow: 1px 3px 16px 2px black;
}
.b-seriesupdate__block_list_item_inner {
    display: table-row;
    box-shadow: 6px 7px 0px 6px black;
}
/*	tophead	*/
.b-tophead,
.b-tophead-right a,
.b-tophead-dropdown a {
	font-size: 10px;
	text-decoration: none;
	padding: 0 10px 0;
	background-color: #313131;
}
.b-tophead_wrapper,
.b-tophead-dropdown.hovered {
	background-color: #222 !important;
}
.b-tophead-dropdown a {
	background-color: #222;
	border: none;
	margin-left: 4px;
	padding: 10px;
}
.b-tophead-dropdown a:hover {
	color: #777;
	background-color: #000;
}
.b-tophead-dropdown ul {
	background-color: #444;
}
.b-tophead__logo .i-sprt {
	height: 18px;
	width: 100px;
	margin: 11px auto;
}
.user-things a:hover .count {
	color: #fff;
	text-decoration: none;
}
.tooltipster-default {
	background-color: rgba(247, 247, 247, .98);
	box-shadow: none;
	border: none;
	border-radius: 3px;
	padding: 15px 20px;
}
.b-person__popup a {
	color: #555;
	font-weight: 600;
	text-decoration: none;
}
.b-person__popup,
.tooltipster-arrow {
	background-color: #111;
}
.tooltipster-arrow-top span, .tooltipster-arrow-top-right span, .tooltipster-arrow-top-left span {
