}
.b-person__popup a:hover {
	color: #777;
}
.b-person__popup_namealt {
	padding: 8px 0 5px;
}
.b-person__popup_photo {
	background-color: #444;
	border: none;
}
.b-person__popup_content .t {
	font-weight: 400;
	color: #777;
	padding-left: 10px;
}
.b-person__popup_content .stats a {
	color: #777;
	background: #444 !important;
}
.b-person__popup_content .stats a:hover {
	color: #999;
	background: #555 !important;
}
.b-person__popup_content .v,
.b-person__popup_content .stats {
	color: #555;
	padding-left: 10px;
}
/*Oo*/
.b-post__description_title {
	color: #000;
	text-align: center;
	padding: 20px 0 5px;
}
.b-post__description_title h2 {
	color: #555;
	font-size: 22px;
	font-weight: 600;
	margin-left: 10px;
}
.b-post__description_text {
	font-size: 16px;
	line-height: 28px;
	padding-top: 15px;
	padding-bottom: 15px;
