	border: none;
}
.b-userset__fav_holder {
	background-color: #222;
	padding-bottom: 30px;
	border: none;
}
.b-userset__fav_holder_data .start-info,
.hd-label-row input[type="checkbox"]+label {
	color: #777;
}
.b-userset__fav_link,
.b-userset__fav_link a:visited {
	color: #777 !important;
	font-size: 12px;
	font-weight: 600;
	text-transform: uppercase;
	letter-spacing: 1px;
	text-decoration: none;
	background-color: #444;
	padding: 10px 20px;
