css:
@-moz-document domain("brick-hill.com") {
/* made by rowbot :) */



body {
    background-color: #1f2223;
    color: #d7d7d7;
}

nav div.primary {
    background-color: #141414;
    color: #fff;
    border-bottom: 1px solid #35353500;
}

nav div.secondary {
    background-color: #1a1a1a;
    color: #b4d2e3;
    border-bottom: 1px solid #353535;
}

nav .info {
    border-radius: 0px;
    background-color: #1a1a1a;
    border: 1px solid #353535;
}

.card {
    border-radius: 0px;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0)
}


.card .footer {
    border: 1px solid #353535;
    border-top: 0px solid #353535;
    background-color: #141414;
}

.card .content {
    border-radius: 0px !important; 
    background-color: #1A1A1A;
    border: 1px solid;
    border-color: #353535;
}

.card .item-card-details, .card .item-content {
    background-color: #1A1A1A;
    border: 1px solid #353535;
    border-top: 0px solid #353535;
}

.hover-card.viewed, .hover-card:hover, .profile-card:hover, .friend-card:hover  {
    background-color: #4C4C4C;
    border-radius: 0px;
}


.profile-card.crate img {
    width: 100%;
    border: 1px solid #353535;
    background-color: #141414;
    border-radius: 0px;
}

.card .thumbnail.dark {
    background-color: #141414;
    border: 1px solid #353535;
    border-bottom: 0px solid;
}

.avatar-card.crate .img-holder {
    border: 1px solid #353535;
    border-radius: 0px;
}

.avatar-card.crate img {
    background-color: #0000;
}

.thread-row {
    position: relative;
    border-bottom: 2px solid #353535;
}

.sellers .seller:not(:last-child) {
    border-bottom: 1px solid #353535;
}

.crate-types {
    border-right: 2px solid #353535;
}

.line, hr {
    border-top: 1px solid #353535;
    border-bottom: 1px solid #353535;
}

.divide {
    background-color: #353535;
}

.trade-item-box {
    border: 1px solid #353535;
    border-radius: 0px;
}

.trade-item img {
    border: 1px solid #353535;
    border-radius: 0px;
}

.box {
    border: 1px solid #353535;
    border-radius: 0px;
}

.box.item-img.owns {
    border: 5px solid #4cb04c;
    border-radius: 0px;
}

.box.item-img.special {
    border: 5px solid #ffd52d;
    border-radius: 0px;
}

.box.shaded {
    background-color: #141414;
}

.box.game-img img {
    border-radius: 0px;
    border: 2px solid #353535
}

.thread {
    padding-bottom: 10px;
    border-bottom: 1px solid #353535;
}

.tabs .tab-body {
    border: 1px solid #353535;
    border-top: 0;
    border-radius: 0 0 0px 0px;
    background-color: #1A1A1A;
}

.tabs .tab.active {
    background-color: #1A1A1A;
    font-weight: 600;
    border-bottom: 1px solid transparent;
    border-radius: 0px;
}

.tabs .tab {
    text-align: center;
    color: #999;
    border-radius: 0px 0px 0 0;
    background-color: #141414;
    border: 1px solid #353535;
}


.card .top:not(.absolute) {
    color: #999;
    background-color: #141414;
    border-bottom: 1.5px solid;
    border-color: #999;
}

.card .top:not(.absolute).blue {
    color: #2D88EF;
    background-color: #141414;
    border-color: #2D88EF;
}

.card .top:not(.absolute).green {
    color: #008A00;
    background-color: #141414;
    border-color: #008A00;
}

.card .top:not(.absolute).orange {
    color: #fc9700;
    background-color: #141414;
    border-color: #fc9700;
}

.card .top:not(.absolute).red {
    color: #fe5746;
    background-color: #141414;
    border-color: #fe5746;
}

.blockquote, blockquote {
    background-color: #141414;
}

.button, button {
    text-align: center;
    border-radius: 0px;
    text-transform: uppercase;
    color: #fff;
    background-color: #353535;
    border: 2px solid #1e1e1e;
}

.thread-label {
    background-color: #353535;
    border-radius: 0px;
}

.thread-label.green {
    background-color: #008A00;
}

.thread-label.blue {
    background-color: #2D88EF;
}

.thread-label.red {
    background-color: #fe5746;
}

.thread-label.orange {
    background-color: #fc9700;
}


.colorPallete {
    border: 2px solid;
    border-color: #0005;
    border-style: inset;
}

.button.blue, button.blue, .button.free, button.free {
    color: #2D88EF;
    background-color: #2d88ef17;
    border-color: #2D88EF;
}

.button.green, button.green {
    color: #08b308;
    background-color: #08b30829;
    border-color: #08b308;
}

.button.orange, button.orange {
    color: #fc9700;
    background-color: #fc97001f;
    border-color: #fc9700;
}

.button.red, button.red {
    color: #dd3a3a;
    background-color: #dd3a3a21;
    border-color: #dd3a3a;
}

.button.bits, button.bits {
    color: #fff;
    background-color: #f49b00;
    border-color: #ea720a;
}

.button.bucks, button.bucks {
    color: #fff;
    background-color: #009624;
    border-color: #04621a;
}

.login-button {
    border: 2px solid #04861b;
    color: #fff;
    border-radius: 0px;
}

.register-button {
    border: 2px solid #007988;
    color: #fff;
    border-radius: 0px;
}

.modal .cancel-button {
    background-color: #ffffff17;
    color: #cacaca;
    border: 2px solid #cacaca;
}

.toggle-user-desc:not(.open):after {
    background-color: #1a1a1a;
    color: grey;
}

.read-more-desc {
    background-color: #1a1a1a;
}

.dropdown-content li * {
    color: #bdbebe;
    background-color: #353535;
    border-radius: 0px;
}

.dropdown-content li :hover {
    background-color: #4C4C4C
}

.dropdown-content .dropdown-arrow {
    border-bottom: 7px solid #353535;
}

.dropdown-content.logout-dropdown li * {
    color: #fff;
    background-color: #353535;
    border-radius: 0px;
}

.dropdown-content.logout-dropdown li :hover {
    background-color: #4C4C4C;
}

.dropdown-content.logout-dropdown .dropdown-arrow {
    border-bottom: 7px solid #353535
}


.input-group .input-button {
    border-left: 1px solid #353535;
    color: #bdbebe;
    background-color: #353535;
}


.crate-types .active, .crate-types :hover {
    background-color: #4C4C4C;
    border-radius: 1px;
    border-left: 3px solid #fe5746;
}

.crate-types li:first-child, .crate-types li:last-child {
    border-radius: 0px 0px 0px 0;
}

.forum-tag {
    background: #353535;
    color: #fff;
    border-radius: 0px;
}

.forum-tag:after {
    border: 5px solid transparent;
    border-right-color: #353535;
}

.modal .modal-content {
    background-color: #141414;
    border: 1px solid #353535;
}


.alert.success {
    background-color: #19c65612;
    border: 2px solid #079137;
    color: #079137;
    border-radius: 0px;
}

.alert.warning {
    background-color: #ef952633;
    border: 2px solid #ef9526;
    color: #ef9526;
    border-radius: 0px;
}

.alert.error {
    background-color: #af001421;
    border: 2px solid #ef263d;
    color: #ef263d;
    border-radius: 0px;
}

.membership-cubes {
    filter: brightness(0)
}

.mem-cols ul li b {
    color: #bdbebe;
}

.mem-cols .col:not(:last-child) {
    background-color: #141414;
}

.mem-cols .col {
    background-color: #141414;
    border: 1px solid #353535;
    border-radius: 0px;
}

.membership-info {
    background-color: #141414;
    border: 1px solid #353535;
    border-radius: 0px;
}

.mint-gradient {
    background-image: -webkit-gradient(linear,right top,left top,from(#2D88EF),to(#fefefe00));
    background-image: linear-gradient(270deg,#2D88EF,#fefefe00);
}

.ace-gradient {
    background-image: -webkit-gradient(linear,right top,left top,from(#fe5746),to(#fefefe00));
    background-image: linear-gradient(270deg,#fe5746,#fefefe00);
}

.royal-gradient {
    background-image: -webkit-gradient(linear,right top,left top,from(#f49b00),to(#fefefe00));
    background-image: linear-gradient(270deg,#f49b00,#fefefe00);
}

footer {
    background-color: #141414;
}


.loader {
    border: 3px solid #353535;
    border-top-color: #3498db;
}

.nav-notif, .notif {
    background-color: #c60000;
    color: #fff;
    border: 1px solid #9d0000;
    border-radius: 0px;
}
}

@-moz-document domain("brick-hill.com") {
.card .name {
    color: #d8d8d8;
}


.label.dark {
    color: #d8d8d8;
}

.ablack-text {
    color: #fff;
}

.agray-text {
    color: #bdbebe;
}

.board-info a, .stat .title {
    color: #969696;
}

.darkest-gray-text {
    color: #7d7d7d;
}

.black-text {
    color: #fff;
}

.trade-serial {
    margin: 0px;
    border-radius: 0px;
    padding: 5px;
    background-color: #353535;
    color: #fefefe;
}

.shop-categories .category.active, .shop-categories .category a:hover {
    font-weight: 600;
    color: #2D88EF;
}

input, textarea {
    color: #bdbaba;
    border-radius: 0px;
    background-color: #141414;
    border: 1px solid #353535;
}

input.rigid, textarea.rigid {
    background-color: #141414;
    color: #797979;
    border: 1px solid #353535;
    border-radius: 0px;
}

.input-group.fill .input, .input-group.fill input, .input-group.fill textarea {
    background-color: #141414; 
    border: 0px solid #353535;
    border-radius: 0px;
}

.input-group {
    border-radius: 0px;
    border: 1px solid #353535;
}

.select, select {
    background-color: #141414;
    border-radius: 0px;
    BORDER-COLOR: #353535;
}

::-webkit-scrollbar {
    background-color: #151515
}

::-webkit-scrollbar-thumb {
    background-color: #353535;
}

::-webkit-scrollbar-thumb:hover {
  background: #464646; 
}
}
