css:
@-moz-document regexp("(http|https)://(gelbooru|youhate).(com|us)/.*") {
#myTopnav > a,
.navSubmenu > a {
    padding-top: 0px;
    padding-bottom: 0px;
    margin-top: 0px;
    margin-bottom: 0px;
    max-height: 17px;
}
.navSubmenu {
    padding: 0;
}
#myTopnav > a.homeIcon {
    margin-top: 0 !important;
    margin-bottom: 0 !important;
    padding-top: 0 !important;
    padding-bottom: 0 !important;
    line-height: 17px !important;
    font-size: 14px !important;
}
#myTopnav > a.homeIcon > img {
    margin: 0 !important;
    padding: 0 !important;
    height: 17px !important;
    width: auto !important;
}
#myTopnav {
    max-height: 17px;
}
body > div.container-fluid > nav.navbar.navbar-default {
    min-height: 17px;
    height: 17px;
    max-height: 17px;
}
body > div.container-fluid > nav.navbar.navbar-default .navbar-header .navbar-brand {
    background-size: cover;
    padding-top: 0 !important;
    padding-bottom: 0 !important;
    height: 17px;
}
#motd {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
    font-size: 9px;
    line-height: 7px;
}
.alert {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
    margin-top: 0 !important;
    margin-bottom: 0 !important;
}
.contain-push > br {
    display: none;
}
#image {
    max-height: calc(100vh - 145px);
    max-width: calc(100vw - 240px);
    width: auto !important;
    height: auto !important;
    /* height: calc(100vh - 120px); */
}
.mainBodyPadding {
    padding-top: 0px;
}
.searchArea {
    margin-top: 0 !important;
    margin-bottom: 0 !important;
    height: auto !important;
}
.searchArea #tags-search,
.searchArea input.searchList,
.searchArea form > a > img {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
    margin-top: 0 !important;
    margin-bottom: 0 !important;
}
.searchArea form > a > img {
    height: 18px;
    max-height: 18px;
    width: 18px;
    max-width: 18px;
}
.searchArea form {
    max-height: 18px;
    overflow: hidden;
}
}
