css:
@-moz-document regexp("(http|https)://(gelbooru|youhate).(com|us)/.*") {
#navbar .nav > li > a,
#navbar2 .nav > li > a {
    padding-top: 0px;
    padding-bottom: 0px;
    max-height: 17px;
}
#navbar .nav > li > a.ui-icon {
    margin-top: 0 !important;
    margin-bottom: 0 !important;
    line-height: 17px !important;
    font-size: 14px !important;
}
#navbar .nav {
    max-height: 17px;
}
#navbar {
    max-height: 17px;
}
body > div.container-fluid > nav.navbar.navbar-default {
    min-height: 17px;
    height: 17px;
    max-height: 17px;
}
body > div.container-fluid > nav.navbar.navbar-default .navbar-header .navbar-brand {
    background-size: cover;
    padding-top: 0 !important;
    padding-bottom: 0 !important;
    height: 17px;
}
#motd {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
}
.alert {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
    margin-bottom: 0;
}
.contain-push > br {
    display: none;
}
#image {
    max-height: calc(100vh - 145px);
    max-width: calc(100vw - 240px);
    width: auto !important;
    height: auto !important;
    /* height: calc(100vh - 120px); */
}
}
