@-moz-document domain("allparts.com") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }
}

@-moz-document domain("allparts.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

.buttons__item .btn,
.collection-list__collection-title,
.hero h1,
.site-primary-nav .nav-item,
h2, h3
 {
	text-transform: capitalize!important;
}

.ais-facets.ais-facets__shown:hover,
.ais-hits--item:hover,
.ais-page .ais-current-refined-values--link,
.ais-page .ais-input,
.ais-page .ais-pagination--item,
.card--yellow,
.dropdown-menu:hover,
.form-control,
.site-primary-nav .dropdown-item,
:focus
 {
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
}

.collection-list__item a:hover {
    transform: none;
}

/*___ Fonts ________________________________________________________________________*/

selector
 {
	font-size: 21px!important;
}

.tabs .tab-content div,
.tabs .tab-content li,
.tg td
 {
	font-size: 19px!important;
}

.card-top p
 {
	font-size: 17px!important;
}

#storemapper #storemapper-form .storemapper-error
 {
	font-size: 15px!important;
}

selector
 {
	line-height: 19px!important;
}

/*___ Remove Items _________________________________________________________________*/

selector
 {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.tab-content>.tab-pane h2,
.faq
 {
    margin: 20px 0 30px 0!important;
}

/*___ Margins ______________________________________________________________________*/

#MainContent,
.ais-hits--item,
.collection-list,
.collection-list__item,
.featured-products
 {
	margin: 12px!important;
}


.mega-menu ul
 {
	margin: 13px 6px!important;
}

/*___ Paddings _____________________________________________________________________*/

.ais-facet-conjunctive.ais-facet-named_tags.brand,
.ais-facets.ais-facets__shown,
.collection-list__item,
.container,
.mega-menu ul
 {
	padding: 12px!important;
}

selector
 {
	padding: 3px 6px!important;
}

/*___ Heights ______________________________________________________________________*/

.gm-style .gm-style-iw-d,
.gm-style .gm-style-iw-c
 {
	max-height: none!important;
}

.hero--image,
.hero--image .row
 {
	height: auto!important;
	 min-height: 0;
}

.gm-style .gm-style-iw-d {
    height: 9em;
}

/*___ Widths _______________________________________________________________________*/

.gm-style .gm-style-iw-d,
.gm-style .gm-style-iw-c,
.ais-hits--item,
.col-xl-2,
.container,
.max--200
 {
	max-width: none!important;
}

selector
 {
	width: auto!important;
}

.container
 {
	width: 98%!important;
}

.ais-hits--item {
    flex: 0 0 23%;
}

.col-xl-2,
.mega-menu__product-title {
    width: 16.375em;
}

.gm-style .gm-style-iw-d {
    width: 18.375em;
}




}

@-moz-document domain("allparts.com") {
/*__________________________________________________________________________________*/
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.ais-page .ais-pagination--item__active .ais-pagination--link,
.collection-list__item:hover p,
.site-primary-nav .dropdown-item:hover span,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.tg td,
#storemapper #storemapper-form .storemapper-error,
.ais-page .ais-search-header,
.dropdown-item:focus,
.dropdown-item:hover,
.site-primary-nav .dropdown-item a,
.site-primary-nav .dropdown-item span,
body,
li,
p
 {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

.tg th,
h1,
h2
 {
	color: #F0E890!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

.tg td:first-child,
.tg td:nth-child(2),
.tg td:nth-child(3),
h3,
h4
 {
	color: #e0b880!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

#storemapper #storemapper-form .storemapper-dropdown-btn,
.ais-sort,
.col-form-label,
.product_availability,
h5,
h6,
label
 {
	color: #F0A060!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

time
 {
	color: #07beff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/

.product__price
 {
	color: #07ff24!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
	
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.form-control:focus,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
	padding-left: 6px!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder {
  color: #f48f8f;
  opacity: 1;
}
























}

@-moz-document domain("allparts.com") {

/*__________________________________________________________________________________*/

body,
html {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

.lines--gold {
	background-image: none;
}


/*___ Background Gray ______________________________________________________________*/


.footer__copyright
 {
	background-color: #060505!important;
}

.background-gray
 {
    background-color: #0a0909!important;
}

/*___ Background 0000 ______________________________________________________________*/

#storemapper #storemapper-form,
.ais-page .ais-current-refined-values--link > div,
.ais-page .ais-current-refined-values--link:before,
.ais-page .ais-input--label,
.ais-page .ais-search-header,
.collection-list__title,
.featured-products__title,
.pagination-row--yellow
 {
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 ________________________________________________________*/

.tg td,
tr:nth-child(even),
.tg th,
#storemapper #storemapper-list li,
#storemapper #storemapper-list,
.card-top,
.card--yellow .card-header
 {
	background-color: rgba(0, 0, 0, 0)!important;
}

/*___ Highlight Background _________________________________________________________*/

selector:hover {
	border-left-color: #434343!important;
	background: linear-gradient(to right, #383838, #0000)!important;
}

/*__ Blue __________________________________________________________________________*/

#storemapper #storemapper-form #storemapper-go,
.ais-hit--cart-buttonz.btn.btn-success,
.cc-color-override-1341153709 .cc-btn
 {
	background-image: linear-gradient(to bottom right, #043dda, #000)!important;
	border-radius: 35px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

#storemapper #storemapper-form #storemapper-go:hover,
.ais-hit--cart-buttonz.btn.btn-success :hover,
.cc-color-override-1341153709 .cc-btn:hover
 {
	background-image: linear-gradient(to bottom right, #154eeb, #000)!important;
	color: #ff0!important;
}

#storemapper #storemapper-form #storemapper-go:active,
.ais-hit--cart-buttonz.btn.btn-success :active,
.cc-color-override-1341153709 .cc-btn:active
 {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Red __________________________________________________________________________*/

selector
 {
	background-image: linear-gradient(to bottom right, #f00, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

selector:hover
 {
	background-image: linear-gradient(to bottom right, #f11, #000)!important;
	color: #ff0!important;
}

selector:active
 {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Orange ________________________________________________________________________*/

.col-md-6 .btn[href="/collections"]
 {
	background-image: linear-gradient(to bottom right, #da3a04, #000)!important;
	border-radius: 35px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.col-md-6 .btn[href="/collections"]:hover
 {
	background-image: linear-gradient(to bottom right, #eb4b15, #000)!important;
	color: #ff0!important;
}

.col-md-6 .btn[href="/collections"]:active
 {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Green ________________________________________________________________________*/

.product--thumb .product__button a,
.add-to-cart-button.btn.btn-success
 {
	background-image: linear-gradient(to bottom right, #81BC2E, #000)!important;
	border-radius: 35px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.product--thumb .product__button a:hover,
.add-to-cart-button.btn.btn-success:hover
 {
	background-image: linear-gradient(to bottom right, #92cd3f, #000)!important;
	color: #ff0!important;
}

.product--thumb .product__button a:active,
.add-to-cart-button.btn.btn-success:active
 {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Gray ________________________________________________________________________*/

.col-md-6 .btn[href="/pages/dealer-resources"],
.ais-page .ais-hit--cart-button__disabled
 {
	background-image: linear-gradient(to bottom right, #ccc, #000)!important;
	border-radius: 35px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.col-md-6 .btn[href="/pages/dealer-resources"]:hover,
.ais-page .ais-hit--cart-button__disabled:hover
 {
	background-image: linear-gradient(to bottom right, #ddd, #000)!important;
	color: #ff0!important;
}

.col-md-6 .btn[href="/pages/dealer-resources"]:active,
.ais-page .ais-hit--cart-button__disabled:active
 {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Plain Black __________________________________________________________________*/

#MainContent,
.ais-page .ais-facet,
.card,
.collection-list,
.container.ais-page,
.featured-products,
.gm-style .gm-style-iw-c,
.mega-menu ul
 {
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

#MainContent:hover,
.ais-page .ais-facet:hover,
.card:hover,
.collection-list:hover,
.container.ais-page:hover,
.featured-products:hover,
.gm-style .gm-style-iw-c:hover,
.mega-menu ul:hover
 {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Plain ________________________________________________________________________*/

.ais-facets.ais-facets__shown,
.ais-hits--item,
.col-md-5,
.collection-list__item,
.dropdown-menu,
.mega-menu,
div#storemapper #storemapper-left
 {
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.ais-facets.ais-facets__shown:hover,
.ais-hits--item:hover,
.col-md-5:hover,
.collection-list__item:hover,
.dropdown-menu:hover,
.mega-menu:hover,
div#storemapper #storemapper-left:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Red Gradient _________________________________________________________________*/

.tabs .nav-link,
.ais-page .ais-facet--header,
.site-primary-nav
 {
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
}

.newsletter,
#storemapper #storemapper-form .storemapper-error {
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}


/*__ Blue Gradient _________________________________________________________________*/

selector
 {
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Green Gradient ________________________________________________________________*/

.tabs .nav-link.active, .tabs .nav-link:focus, .tabs .nav-link:hover
 {
	background: linear-gradient(to bottom, #15534d 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
}

/*__ Yellow Gradient _______________________________________________________________*/

.lines--gold
 {
	background: linear-gradient(to bottom, #6a72c9 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
}

/*__ Orange Gradient _______________________________________________________________*/


.ad-special
 {
	background: linear-gradient(to bottom, #da3a04 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*___ Highlight Background _________________________________________________________*/

tr:nth-child(even):hover,
tr:nth-child(odd):hover,
.ais-page .ais-facet--active,
.ais-page .ais-facet--item:hover,
.dropdown-item:focus,
.dropdown-item:hover,
.mega-showing
 {
	background: linear-gradient(to right, #383838, #0000)!important;
	border-left-color: #434343!important;
}








}

@-moz-document domain("allparts.com") {
/*__________________________________________________________________________________*/

tr:nth-child(even),
tr:nth-child(odd),
#storemapper #storemapper-list li,
.ais-hits--item,
.col-md-4,
.collection-list__item,
.product--thumb
 {
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

tr:nth-child(even):hover,
tr:nth-child(odd):hover,
#storemapper #storemapper-list li:hover,
.ais-hits--item:hover,
.col-md-4:hover,
.collection-list__item:hover,
.product--thumb:hover
 {
	filter: brightness(100%)!important;
}

.ais-page .ais-facet--header {
	border-top-left-radius: 22px;
	border-top-right-radius: 22px;
}

.dropdown-item:first-child,
.mega-showing:first-child {
	border-top-left-radius: 22px;
	border-top-right-radius: 22px;
}

.dropdown-item:last-child,
.mega-showing:last-child {
	border-bottom-left-radius: 22px;
	border-bottom-right-radius: 22px;
}

/*__________________________________________________________________________________*/

img:not([alt="Allparts Music"]):not([style="width: 256px; height: 256px; user-select: none; border: 0px; padding: 0px; margin: 0px; max-width: none;"])
 {
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

.container-image,
.article__image {
    z-index: 9;
}
}

@-moz-document domain("allparts.com") {
/* Avatar _____________*/

.align-items-center .icon-responsive svg,
.scrolled .site-header .site-header__account-menu svg {
	background: url(https://i.ibb.co/g3wQg00/avatar-32x32.png) no-repeat!important;
	box-sizing: border-box;
}

.align-items-center .icon-responsive svg,
.scrolled .site-header .site-header__account-menu svg {
	background-size: 19px!important;
	height: 20px!important;
	padding-left: 39px;
}

/* Cart _____________*/

.site-header__cart svg {
	background: url(https://i.ibb.co/42GPC9s/shoppingcart.png) no-repeat!important;
	box-sizing: border-box;
}

.site-header__cart svg {
	background-size: 19px!important;
	height: 20px!important;
	padding-left: 39px;
}

/* Search _______________________________________________________________*/

form[role=search] button[type=submit] svg {
	background: url(https://image.ibb.co/hVxuzR/search.png) no-repeat!important;
	box-sizing: border-box;
}

form[role=search] button[type=submit] svg {
	background-size: 15px!important;
	height: 26px!important;
	padding-left: 22px!important;
	width: 10px!important;
}
}
