@-moz-document domain("www.nitrotype.com") {
/* Hi, this theme wouldn't be possible without Syed's NT Vibrant, I worked off the code that he made on that theme, and he has 2 pretty cool themes you should check out. Some of the code is also from LeSirH, which also has a pretty smooth Blue Dark theme, that I think you should check out. */
body {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
}
path:first-child,
path:nth-child(7) {
    fill: #272b2e;
}
.structure-ad {
    display: none;
}
.btn.btn--xs.btn--d.btn--float {
    background: white;
    color: #000;
}
.card.card--e.card--sq.card--shadow-l.card--f.mbf.dhf.card--bc-emerald {
    background: #ff00;
    border: none;
}
.h5.tc-ts {
    color: #fff;
}
.btn.btn--stroke-i.btn--xs.btn--float.btn--fw {
    background: #006457;
    border: none;
}
.btn--stroke-i.mbf.dhf {
    color: #000;
}
.btn.btn--c.btn--xxs.btn--icon {
    position: absolute;
    right: 90px
}
.h3.mbs {
    color: #fff;
}
.news-teaser {
    color: #fff;
}
.card-cap.bg--gradient {
    background: url("https://www.fg-a.com/backgrounds/black-planet-surface-1920.jpg");
}
.card.card--b.card--shadow.card--f.card--grit.well.well--b.well--l {
    border: none;
    box-shadow: none;
    opacity: 1;
    color: #fff
}
 
.well--p.well--l_p {
    background: linear-gradient(to right, #4F4141, #333131)
}
.card.card--b.card--login.card--shadow.card--grit.card--f.card--o.cell.cell--s {
    background: linear-gradient(to right, #fa0a16, #c36381)
}
.btn.btn--google.btn--fw.btn--icon.btn--float {
    color: lightblue
}
.g-b.g-b--10of12 {
    background: linear-gradient(to right, #4F4141, #000)
}
.footer-nav {
    background: linear-gradient(to right, #4F4141, #333131)
}
.footer-copyright {
    background: linear-gradient(to right, #4F4141, #333131)
}
.btn.btn--a.btn--fw.btn--float {
    background: #fff;
    color: #000;
    text-shadow: none;
}
.footer-card.card.card--c.card-shadow.tac.mbf {
    background: #4F4141;
}
.btn.btn--a.btn--float {
    background: white;
    text-shadow: none;
    color: #000;
}
.btn--a {
 
    background-color: #ffffff;
}
.news--solo .news-body p {
    color: #FFFDFD;
    line-height: 1.8;
}
.rv-xy-plot__series.rv-xy-plot__series--line.chart-area {
    background: linear-gradient(to top, red, lightblue);
}
.h2.tbs {
    color: #fff;
}
.hp-heroBG {
    content: ' ';
    display: block;
    position: absolute;
    top: 0;
    left: -40px;
    background: linear-gradient(to bottom, #000, #16c0c3);
    width: 140%;
    height: 125%;
    z-index: 1;
    box-shadow: 0 1px 10px rgba(2, 2, 2, 0.14);
}
 
.btn--c {
    background-color: #ff00;
    border-radius: 5px;
    border: none;
    color: rgba(255, 255, 255, 0.9);
    font-weight: 600;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
}
.card.card--e.card--sq.card--shadow-l.card--bc-dodger.dhf.stat-box.stat-box--races {
    opacity: .7
}
.stat-box--races:before {
    background-image: none;
    background: none;
}
.btn {
    background-color: #ff00;
}
.hp-heroBG {
    background: url("https://cdn.wallpapersafari.com/76/86/ltFcJ2.jpg");
    background-size: 130%;
}
.btn--a {
    background-color: #ff00;
    border-radius: 5px;
    border: none;
    color: rgba(255, 255, 255, 0.9);
    font-weight: 600;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
}
 
.btn.btn--a.btn--float.btn--icon.animate--iconSlam.header-cta {
    background: url("https://www.publicdomainpictures.net/pictures/30000/nahled/solid-purple-background.jpg")
}
.dais-car {
    background: #FFF;
    opacity: .8;
    image-rendering: optimizeQuality
}
.card--shadow-l {
    box-shadow: none;
    opacity: .9;
}
.evenodd {
    color: #fff
}
 
 
 
.season--default {
 
    background-color: #fff0;
}
 
.card--shadow-l {
    box-shadow: none;
}
.news-body {
    color: rgb(0, 0, 0) !important;
}
.section > p {
    color: rgb(0, 0, 0) !important;
}
.card--roundRt {
    background: url("https://hdwallpaperfx.com/wp-content/uploads/2016/10/Red-");
}
 
.comments {
    background: #ff00
}
.race-results {
    background-color: #fff0;
}
.ad {
    display: none;
    position: relative;
    margin-right: auto;
    margin-left: auto;
    text-align: center;
}
 
.ad--sq {
    display: none;
    width: 336px;
    height: 280px;
    background: #f2f2f2;
}
 
 
.well--l {
    background-color: #fff0;
}
 
.card--shadow-l {
    box-shadow: none;
}
.tlh-1 {
    color: #ffffff;
}
.well--t {
    color: #ffffff;
}
.ad--ldrbrd {
    display: none;
    width: 728px;
    height: 90px;
    background: #f2f2f2;
}
.garage {
    background: url("https://i.pinimg.com/originals/51/fb/7b/51fb7b8e73065488a888cfb095439bba.jpg");
}
.ad--ldrbrd_l {
    display: none;
    width: 970px;
    height: 90px;
}
 
.ad--fixed {
    display: none;
    position: fixed;
    top: 30px;
}
 
.card--a {
    background-color: #fff0;
}
 
.profile-head {
    box-shadow: none;
}
.structure-header {
    margin-left: auto;
    margin-right: auto;
    max-width: 1020px;
}
 
.tbs {
    background-color: #fff0;
}
 
.card--shadow-l {
    box-shadow: none;
}
 
html {
    background: url("https://data.1freewallpapers.com/download/space-open-space-planets-art-colorful.jpg"), #272b2e;
    background-repeat: repeat;
    background-size: 100%;
    box-sizing: border-box;
    color: #ffffff;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
}
.btn.btn--a.btn--s {
    background: url("https://www.fg-a.com/backgrounds/black-planet-surface-1920.jpg");
}
.theme--pDefault .profile-bg {
    background: url("https://www.fg-a.com/backgrounds/black-planet-surface-1920.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #d3d3d3;
}
 
.theme--pDefault .profile-bgHead {
    background: url("https://www.fg-a.com/backgrounds/black-planet-surface-1920.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
}
.p {
    color: rgb(0, 0, 0);
}
.btn--a:hover {
    background: #2d2e2e;
}
.btn--a {
    background: #000000;
}
.profile-invCount {
    color: #ffffff;
}
.prog-bar {
    background: #ffffff;
}
.profile-invTitle {
    color: #ffffff;
}
.card--b {
    color: #000;
}
 
 
.raceResults-title {
    color: #ffffff;
}
 
.mbf {
    color: #ffffff;
}
.list-item {
    color: #ffffff;
}
.di {
    color: #ffffff;
}
 
.tss {
    color: #ffffff;
}
 
.flash {
    background: url("http://pavbca.com/walldb/original/f/d/1/427412.jpg");
}
 
 
.stat .tc-i {
    color: #ffffff;
}
.rv-xy-plot__series.rv-xy-plot__series--line.chart-line {
    display: none;
}
.rv-xy-plot__grid-lines {
    color: red;
    background: red;
    background-color: red;
    fill: red;
}
 
#chart-gradient {
    color: #00E6FF !important;
}
 
.chart--a .chart-gradient .chart-gradient1 {
    stop-color: #7407d4;
    stop-opacity: 0.8;
}
 
.chart--a .chart-gradient .chart-gradient2 {
    stop-color: #702282;
    stop-opacity: 0.5;
}
 
.chart--a .chart-gradient .chart-gradient3 {
    stop-color: #65074f;
    stop-opacity: 1;
}
 
.chart--a .chart-gradient .chart-gradient4 {
    stop-color: #400642;
    stop-opacity: 0.8;
}
 
.chart--a .chart-gradient .chart-gradient5 {
    stop-color: #100114;
    stop-opacity: 0.8;
}
 
.chart--a .chart-gradient .chart-gradient6 {
    stop-color: #230121;
    stop-opacity: 0.8;
}
 
 
 
.chart--a .chart-line {
    stroke: rgb(0, 0, 0, 0) !important;
    stroke-width: 2 !important;
}
 
.chart--a .chart-line circle {
    fill: #000 !important;
    stroke-width: 0 !important;
    stroke: #fff !important;
    display: none;
}
.dash-copyContainer {
    background: #1d1e23;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 17px;
    display: flex;
   }
 
  .dash-copy
  {
    color: #fff;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 17px;
   
  }
 
  ::selection
  {
    background-color: #1976c6;
    color: white;
  }

}

@-moz-document url("http://www.nitrotype.com/news") {
.section > p {
    color: #000000 !important;}
.p {color: #000000 !important;}
.news--solo .news-body p {color:#fff}
}

@-moz-document url-prefix("https://www.nitrotype.com/achievements") {
.well--p.well--l_p {background: linear-gradient(to right, #fa0a16, #333131)}
.achievement-is-complete {display:none;}
.prog-points {color: #000;}
.card-cap.bg--gradient {background: linear-gradient(to right, #333131, #333131)}
.twb {color: black;}
}
