css:
@-moz-document domain("drawception.com") {
.text-muted
  {
    color: var(--text-color-muted) !important;
  }

  :root
  {
    --bg-accent-color: #2262b0 !important;
    --bg-accent-highlight-color: #f2f2f2 !important;
    --bg-color: #6fb7e1 !important;
    --bg-hover-color: #ffcdc3 !important;
    --bg-disabled-color: #ffcdc3 !important;
    --bg-highlight-color: #2262b0 !important;
    --bg-duck-option: #2262b0 !important;
    --bg-duck-option-hover: #2262b0 !important;
    --text-color: #20426c !important;
    --text-hover-color: #2262b0 !important;
    --text-color-lighter: #2262b0 !important;
    --text-color-darker: #6ab7bf !important;
    --text-color-muted: #ffcdc3 !important;
    --border-color: #2262b0 !important;
    --border-highlight-color: #2262b0 !important;
    --link-color: #e67d8c !important;
    --link-color-hover: #f5b4be !important;
    --comment-highlight-color: #fffdc9 !important;
    --store-btn-bg-color: #65bbe2 !important;
    --store-btn-bg-hover-color: #2262b0 !important;
    --store-btn-text-color: #f5b4be !important;
    --modal-overlay-color: rgba(184,165,147,0.2) !important;
    --colorpicker-border-color: rgba(0,0,0,0.25) !important;
    --popup-border-color: #cfe9e4 !important;
    --navbar-bg-color: #8cccbb !important;
    --navbar-border-color: #cfe9e4 !important;
    --navbar-btn-text-color: #20426c !important;
    --navbar-btn-text-hover-color: #f5b4be !important;
    --navbar-btn-border-color: #cfe9e4 !important;
    --navbar-btn-border-hover-color: #6ab7bf !important;
    --navbar-btn-text-shadow: 0px 0px 3px #3f8fa0 !important;
    --progress-bar-bg-color: #cfe9e4 !important;
  }

  .text-danger
  {
    color: var(--border-color) !important;
  }

  .btn-primary
  {
    background-color: var(--navbar-btn-text-hover-color) !important;
  }

  a:focus, a:hover
  {
    color: var(--link-color-hover) !important;
    text-decoration: underline !important;
  }

  .label-warning
  {
    background-color: var(--border-color) !important;
  }

  .theme-night
  {
    --bg-accent-color: #dff3fe !important;
    --bg-accent-highlight-color: #f2f2f2 !important;
    --bg-color: #c2ceff !important;
    --bg-hover-color: #a6bbb4 !important;
    --bg-disabled-color: #dff3fe !important;
    --bg-highlight-color: #a6bbb4 !important;
    --bg-duck-option: #a6bbb4 !important;
    --bg-duck-option-hover: #a6bbb4 !important;
    --text-color: #433d49 !important;
    --text-hover-color: #464a56 !important;
    --text-color-lighter: #63687e !important;
    --text-color-darker: #dff3fe !important;
    --text-color-muted: #433d49 !important;
    --border-color: #433d49 !important;
    --border-highlight-color: #a6bbb4 !important;
    --link-color: #c7baa7 !important;
    --link-color-hover: #63687e !important;
    --comment-highlight-color: #fffdc9 !important;
    --store-btn-bg-color: #c7baa7 !important;
    --store-btn-bg-hover-color: #a6bbb4 !important;
    --store-btn-text-color: #7799a2 !important;
    --modal-overlay-color: rgba(184,165,147,0.2) !important;
    --colorpicker-border-color: rgba(0,0,0,0.25) !important;
    --popup-border-color: #a6bbb4 !important;
    --navbar-bg-color: #63687e !important;
    --navbar-border-color: #a6bbb4 !important;
    --navbar-btn-text-color: #c2ceff !important;
    --navbar-btn-text-hover-color: #7799a2 !important;
    --navbar-btn-border-color: #a6bbb4 !important;
    --navbar-btn-border-hover-color: #dff3fe !important;
    --navbar-btn-text-shadow: 0px 0px 3px #c7baa7 !important;
    --progress-bar-bg-color: #7b7285 !important;
  }

  .progressbar-bg
  {
    background-color: var(--navbar-btn-border-color) !important;
  }
}
