css:
@-moz-document domain("drawception.com") {

}

@-moz-document domain("drawception.com") {
.text-muted
  {
    color: var(--text-color-muted) !important;
  }

  :root
  {
    --bg-accent-color: #aedee3 !important;
    --bg-accent-highlight-color: #f2f2f2 !important;
    --bg-color: #f2f3f7 !important;
    --bg-hover-color: #7d849b !important;
    --bg-disabled-color: #fec083 !important;
    --bg-highlight-color: #9f747f !important;
    --bg-duck-option: #9f747f !important;
    --bg-duck-option-hover: #9f747f !important;
    --text-color: #4f352f !important;
    --text-hover-color: #9f747f !important;
    --text-color-lighter: #fec083 !important;
    --text-color-darker: #4f352f !important;
    --text-color-muted: #9a594b !important;
    --border-color: #9f747f !important;
    --border-highlight-color: #9f747f !important;
    --link-color: #ff6013 !important;
    --link-color-hover: #fec083 !important;
    --comment-highlight-color: #fffdc9 !important;
    --store-btn-bg-color: #426277 !important;
    --store-btn-bg-hover-color: #9f747f !important;
    --store-btn-text-color: #fec083 !important;
    --modal-overlay-color: rgba(184,165,147,0.2) !important;
    --colorpicker-border-color: rgba(0,0,0,0.25) !important;
    --popup-border-color: #7d849b !important;
    --navbar-bg-color: #426277 !important;
    --navbar-border-color: #7d849b !important;
    --navbar-btn-text-color: #aedee3 !important;
    --navbar-btn-text-hover-color: #fec083 !important;
    --navbar-btn-border-color: #7d849b !important;
    --navbar-btn-border-hover-color: #9f747f !important;
    --navbar-btn-text-shadow: 0px 0px 3px #aedee3 !important;
    --progress-bar-bg-color: #7d849b !important;
  }

  .text-danger
  {
    color: var(--link-color) !important;
  }

  .btn-primary
  {
    background-color: var(--navbar-btn-text-hover-color) !important;
  }

  a:focus, a:hover
  {
    color: var(--link-color-hover) !important;
    text-decoration: underline !important;
  }

  .label-warning
  {
    background-color: var(--border-color) !important;
  }


  .progressbar-bg
  {
    background-color: var(--navbar-btn-border-color) !important;
  }
}
