@-moz-document domain("stewmac.com") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }
}

@-moz-document domain("stewmac.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

#nav>li .fourcolumns .title,
#nav>li .threecolumns .title,
#new-items ul li .item-content span.new-label,
#product-detail-links li a,
.add-nav>ul>li
 {
	text-transform: capitalize!important;
}

.features .sub-feature-span1, .features .sub-feature-span2, .features .sub-feature-span3, .features .sub-feature-span4,
.button-plusminus.left,
.button-plusminus.right,
.hierarchy-grid li,
:focus,
[style="border-width:3px; border-style:solid; background-color:#FFFFFF; border-color:#FFFFFF;"],
div.FacetFilterDisplay .facet-group ul li label,
td[style="border-width:3px; border-style:solid; border-color:#FFFFFF;"],
ul.product-image-thumbs li a
 {
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

tableppp {
    border-spacing: 15px 0;
}

/*___ Fonts ________________________________________________________________________*/

.products-grid .price
 {
	font-size: 17px!important;
}

.products-grid .save-price,
ul.products-grid
 {
	font-size: 15px!important;
}

/*___ Remove Items _________________________________________________________________*/

td[bgcolor="#CCCCCC"] img
 {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.hierarchy-grid .title {
    top: 13rem;
    left: 2rem;
}

div.FacetFilterDisplay a.clear-all {
    top: 53px;
    right: 27px;
}

td:first-child {
	padding: 0 12px!important;
}

.article ul li {
    margin-left: 17px;
	margin-top:12px;
}

/*___ Margins ______________________________________________________________________*/

#left-column,
#main-content,
.hierarchy-grid li,
.products-grid li
 {
	margin: 12px!important;
}

ul.product-image-thumbs li
 {
	margin: 12px 0!important;
}

/*___ Paddings _____________________________________________________________________*/

#left-column,
#main-content,
.breadcrumbs,
.hierarchy-grid li,
.products-grid li
 {
	padding: 12px!important;
}

div.FacetFilterDisplay .facet-group ul li
 {
	padding: 3px 6px!important;
}

/*___ Heights ______________________________________________________________________*/

.hierarchy-grid li {
    height: 15rem;
}

/*___ Widths _______________________________________________________________________*/

body
 {
	max-width: none!important;
}

#main
 {
	width: 98%!important;
}

#new-items ul li .item-content,
#main-content {
	width: 82%!important;
}

#header .logo {
    width: 358px;
}

#nav>li .fourcolumns a,
#nav>li .threecolumns a
{
    width: 202px;
}

.products-grid li {
    width: 175px;
}



}

@-moz-document domain("stewmac.com") {
/*__________________________________________________________________________________*/

a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

#new-items ul li .item-content span.new-label,
.cd91000,
.checkout-box .container strong,
.hierarchy-grid li:hover div .title a,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.checkout span,
.products-grid .rating,
body,
div.FacetFilterDisplay .facet-group ul li span.facet-text,
li,
p {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

h1,
h2 {
	color: #F0E890!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

.cff0000,
h3,
h4 {
	color: #e0b880!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

.products-grid .price span,
.reviews-block .meta,
h5,
h6,
label {
	color: #F0A060!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

.products-grid .specialprice,
.reviews-block .date,
.shopping-cart .available,
.table-holder .available,
time {
	color: #07beff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/

#product-top .shopping-cart .prices h2,
.price-box .total span,
.products-grid .price {
	color: #07ff24!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/

.products-grid li a.link-block {
	color: #f00!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

span.select-area,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
	padding-left: 6px!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder {
	color: #f48f8f;
	opacity: 1;
}

/*___ Misc _________________________________________________________________________*/

.products-grid span.more-options {
	color: #fc0!important;
}

}

@-moz-document domain("stewmac.com") {
/*__________________________________________________________________________________*/

body,
html {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

#nav>li .drop {
	background: #000000;
}

/*___ Background Gray ______________________________________________________________*/

#header,
.f2 {
	background-color: #060505!important;
}

.breadcrumbs,
section.search-bar {
	background: #0a0909!important;
	background-color: #0a0909!important;
}

/*___ Background 0000 ______________________________________________________________*/

#more-items h2,
#product-top .shopping-cart:after,
.button-plusminus,
.footer-bottom,
section.reviews-block h2,
section.search-bar .logo {
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 ________________________________________________________*/

#item-details h2,
[bgcolor="#cccccc"],
[style="border-width:3px; border-style:solid; background-color:#FFFFFF; border-color:#FFFFFF;"],
ul.products-grid {
	background-color: rgba(0, 0, 0, 0)!important;
}

/*__ Blue __________________________________________________________________________*/

#product-top .price-box a.btn-save {
	background-image: linear-gradient(to bottom right, #043dda, #000)!important;
	border-radius: 35px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

#product-top .price-box a.btn-save:hover {
	background-image: linear-gradient(to bottom right, #154eeb, #000)!important;
	color: #ff0!important;
}

#product-top .price-box a.btn-save:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Orange ________________________________________________________________________*/

section.reviews-block .panel a.aside {
	background-image: linear-gradient(to bottom right, #da3a04, #000)!important;
	border-radius: 35px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

section.reviews-block .panel a.aside:hover {
	background-image: linear-gradient(to bottom right, #eb4b15, #000)!important;
	color: #ff0!important;
}

section.reviews-block .panel a.aside:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Green ________________________________________________________________________*/

#product-top .price-box .btn-addtocart {
	background-image: linear-gradient(to bottom right, #81BC2E, #000)!important;
	border-radius: 35px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

#product-top .price-box .btn-addtocart:hover {
	background-image: linear-gradient(to bottom right, #92cd3f, #000)!important;
	color: #ff0!important;
}

#product-top .price-box .btn-addtocart:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Plain Black __________________________________________________________________*/

#main.inner,
#left-column,
#main-content {
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

#main.inner:hover,
#left-column:hover,
#main-content:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Plain ________________________________________________________________________*/

#ctl00_paPopUp_pnlContent,
.flyout-featured-item,
.product-carousel,
.products-grid li {
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

#ctl00_paPopUp_pnlContent:hover,
.flyout-featured-item:hover,
.product-carousel:hover,
.products-grid li:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.products-grid li li {
	background: none!important;
	border: none!important;
	box-shadow: none!important;
}

/*___ Red Gradient _________________________________________________________________*/

#nav>li:hover>a,
.nav-bar {
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
}

/*__ Blue Gradient _________________________________________________________________*/

#product-instructions,
#tradesecrets,
div.instructions {
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*___ Highlight Background _________________________________________________________*/

.breadcrumbs li.last,
div.FacetFilterDisplay .facet-group ul li:hover,
tr:not(#ctl00_mPageContent_trNewReleases):hover {
	background: linear-gradient(to right, #383838, #0000)!important;
	border-left-color: #434343!important;
}
}

@-moz-document domain("stewmac.com") {
/*__________________________________________________________________________________*/

#new-items ul li,
#product-top .shopping-cart,
.features .sub-feature-span1,
.features .sub-feature-span2,
.features .sub-feature-span3,
.features .sub-feature-span4,
.hierarchy-grid li,
.products-grid li,
.related-items-details li,
.reviews-block .reviews,
tr,
ul.product-image-thumbs li a {
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

#new-items ul li:hover,
#product-top .shopping-cart:hover,
.features .sub-feature-span1:hover,
.features .sub-feature-span2:hover,
.features .sub-feature-span3:hover,
.features .sub-feature-span4:hover,
.hierarchy-grid li:hover,
.products-grid li:hover,
.related-items-details li:hover,
.reviews-block .reviews:hover,
tr:hover,
ul.product-image-thumbs li a.active,
ul.product-image-thumbs li a:hover {
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

img:not([src="//content.stewmac.com/images/sm_logo_footer.png"]):not([src="https://content.stewmac.com/images/StewMAX/smx_header_signup.png"]) {
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/

li,
p,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

li:hover,
p:hover,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}
}

@-moz-document domain("stewmac.com") {
#header .search-from span.ui-helper-hidden-accessible:after,
.fa-search:before {
	content: "";
}

#footer .search-from i.fa-search {
	font-size: 17px;
}

