	background: url(https://i.ibb.co/MP6XD7Z/sm-logo-footer.png) no-repeat!important;
	box-sizing: border-box;
}

[src="//content.stewmac.com/images/sm_logo_footer.png"] {
	background-size: 7em!important;
	height: 3em!important;
	padding-left: 203px!important;
