css:
@-moz-document url-prefix("https://www.gearslutz.com") {
body {
background-color: #242427 !important;
} 

.forum-container {
    background: #242427 !important;
    padding: 0 10px 20px;
    overflow: hidden;
}

.quickthread-nav {
    width: 100%;
    background-color: #242427 !important;
}

.threads-list td {
    padding: 8px 11px;
    background: #242427 !important;
}
.threads-list .thread-name h2 a {
    color: #dadada !important;
    text-decoration: none;
    font-family: open_sansregular;
}
.threads-list .thread-name .thread-owner {
    color: #dadada !important;
}
.threads-list .thread-name h2 a {
    color: #dadada !important;
    text-decoration: none;
    font-family: open_sansregular;
}
.threads-list .thread-views span a, .threads-list .thread-views span span {
    color: #dadada;
}
.threads-list .thread-name h2 {
    font-size: 15px;
    color: #FF7F00 !important;
    margin: 0;
    line-height: 22px;
}
.forum-bit-wrapper {
    width: 100%;
    background: #242427 !important;
    border-top: 0;
}
.navbar-breadcrumbs {
    width: 100%;
    background-color: #242427!important;
    height: auto;
    font-size: 10pt;
}
.noticeBit {
    background: #242427!important;
    background: -moz-linear-gradient(top,#f5f5ff 0,#FFF 100%);
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0,#f5f5ff),color-stop(100%,#FFF));
    background: -webkit-linear-gradient(top,#f5f5ff 0,#FFF 100%);
    background: -o-linear-gradient(top,#f5f5ff 0,#FFF 100%);
    background: -ms-linear-gradient(top,#f5f5ff 0,#FFF 100%);
    background: linear-gradient(to bottom,#242427 0,#242427 100%);
    padding: 8px;
    color: #dadada!important;
    margin-top: 5px;
    margin-bottom: 5px;
    border-radius: 5px;
    -moz-border-radius: 5px;
    border: 1px solid #666;
}
.forum-nav {
    box-sizing: border-box;
    border-top: 1px solid #242427!important;
    border-bottom: 1px solid #242427!important;
    width: 100%;
    background: linear-gradient(to bottom,#242427 0,#242427 65%,#242427 98%,#242427 99%,#fff 99%,#fff 100%,#fff 100%,#e5e5e5 100%,#fff 101%);
    box-shadow: 0 2px 3px 0 #242427;
}
.notices-wrapper {
    background-color: #242427!important;
    padding: 1em 0 1px;
}
.threads-list .thread-views span a, .threads-list .thread-views span span {
    color: #dadada!important;
}
.threads-list .thread-last-post>a {
    color: #dadada!important;
    font-size: 14px;
    text-decoration: none;
}
a:link, body_alink {
    color: #FF7F00!important;
}
.blue-btn {
    border-style: none;
    background-color: #242427!important;
    color: #FF7F00!important;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    border-bottom: 2px solid #dadada!important;
}
.orange-btn {
    background-color: #242427!important;
    border: none!important;
    border-bottom: 2px solid #dadada!important;
    border-radius: 4px!important;
    color: #FF7F00!important;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
}
.navbar-fixed li, .quickthread-nav.fixed li {
    display: inline-block;
    background: #242427!important;
    border-radius: 8px;
    box-shadow: inset 0 -1px 1px #242427, inset 0 1px 1px #242427!important;
}
.gold-btn {
    background-color: #242427!important;
    line-height: 27px!important;
    padding-top: 2px!important;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
    color: #FF7F00!important;
    font-size: 13px!important;
    position: relative;
    top: -8px;
}
.inner_nav .brace {
    background: #242427!important;
    font: 400 14px open_sansregular,tahoma,verdana,geneva,lucida,'lucida grande',arial,helvetica,sans-serif!important;
    padding: 4px 11px!important;
    -webkit-border-radius: 3px!important;
    -moz-border-radius: 3px!important;
    border-radius: 3px!important;
}
.threads-list thead tr th {
    color: #dadada!important;
    font-size: 12px;
    height: 28px;
    padding: 0 15px;
    text-align: left;
    font-weight: 400;
    line-height: 16px;
}
.threads-list thead tr {
    background: #242427!important;
    box-shadow: 1px 1px #FFF inset, -1px -1px #FFF inset;
    border: 1px solid #d4d4d4!important;
    border-bottom-color: #bdbfcf!important;
}
.threads-header {
    background: #242427!important;
    color: #FF7F00!important;
    border-radius: 3px 3px 0 0;
    -moz-border-radius: 3px 3px 0 0;
    -webkit-border-radius: 3px 3px 0 0;
    padding: 5px 26px 5px 20px;
}
.navbar-breadcrumbs #mainBreadCrumb a {
    text-decoration: none;
    color: #dadada!important;
}
.thread-posted-in {
    background-color: #FF7F00!important;
    width: 10px;
    height: 10px;
    border-radius: 8px;
    display: inline-block;
    margin-right: 2px;
}
.inner_nav>a, .inner_nav>span, .navcell>a {
    background: #242427!important;
    color: #dadada!important;
    -webkit-border-radius: 3px!important;
    -moz-border-radius: 3px!important;
    border-radius: 3px!important;
    padding: 5px 8px!important;
    border-right: 0!important;
    font-size: 14px!important;
    margin-left: 2px;
}
.quicksearch-icon {
    height: 30px;
    width: 32px;
    border: none;
    padding-left: 0;
    background-position: center;
    background-repeat: no-repeat;
    cursor: pointer;
    background-color: #FF7F00!important;
    box-sizing: border-box;
    color: transparent;
    transform: translate(2px,1px);
    border-top-right-radius: 3px;
    border-bottom-right-radius: 3px;
}
.participated-count, .quote-count {
    color: #fff;
    background-color: #FF7F00!important;
    border-radius: 2px;
    margin-left: -1em;
    font-family: open_sansbold;
    cursor: pointer;
}
.post_data {
    display: table-cell;
    background-color: #424242!important;
}
.toptoolbar {
    padding: 6px 0 10px 10px;
    background: #242427;
}
.user_data {
    display: table-cell;
    width: 175px;
    padding: 5px;
    margin: 0;
    vertical-align: top;
    background: #424242!important;
}
.thead {
    background: #242427!important;
    color: #FFFFFF;
    font: bold 11px tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
p.thread-snippet {
    color: #989898!important;
}
.alt2 {
    background-color: #424242!important;
}
.showthread-search-bottom {
    display: inline-block;
    padding: 6px;
    border: none;
    border-radius: 3px;
    cursor: pointer;
    background-color: #FF7F00!important;
    box-sizing: border-box;
    color: #fff;
    font-size: 14px;
    margin-top: 5px;
    width: 145px;
}
.tcat {
    background: #242427!important;
    color: #FFFFFF;
    font: bold 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}
.tcat, #forumdisplayoptions .thead {
    display: table-cell;
    text-align: left;
    background: #242427!important;
    border: 1px solid #FF7F00!important;
    padding: 6px;
    border-radius: 2px!important;
    -webkit-border-radius: 2px!important;
}
#content {
    background-color: #242427!important;
    width: 98%;
    padding: 10px;
    max-width: 1180px;
}
.panel {
    background: #242427!important;
    color: #FF7F00;
    padding: 10px;
    border: 1px outset;
}
.newreplytitle {
    background: #242427!important;
    color: #FFF;
}
.panelsurround {
    padding: 6px!important;
    background: #242427!important;
}
.alt1, .alt1Active {
    background: #989898!important;
    color: #222222!important;
}
.rbCell {
    display: table-cell;
    border: 2px solid #989898!important;
    border-left: 0;
}
.tfoot {
    background: #242427!important;
    color: #E0E0F6!important;
}
.forum-home-category {
    box-sizing: border-box;
    width: 100%;
    padding: 1em;
    background-color: #242427!important;
    font-family: open_sansregular,Helvetica,Arial,sans-serif;
    color: #FF7F00;
    border-top-right-radius: 5px;
    border-top-left-radius: 5px;
}
.xsmalltext {
    font-size: 1em;
    color: #dadada!important;
}
.fb-title {
    font-family: open_sansbold!important;
    text-decoration: none;
    font-size: 12pt;
    color: #dadada!important;
    width: 550px;
}
#finalNavbit {
    display: block;
    font-size: 18pt;
    color: #dadada!important;
}
.forum-nav li a {
    font-family: open_sansregular,Helvetica,Arial,sans-serif!important;
    text-decoration: none;
    text-transform: capitalize;
    color: #dadada!important;
}
.subForumList a {
    font-family: open_sanssemibold,Helvetica,Arial,sans-serif;
    line-height: 1.5!important;
    color: #dadada!important;
}
.sub-forum-smalltext {
    text-decoration: none;
    font-family: open_sansregular,Helvetica,Arial,sans-serif;
    font-weight: 400;
    color: #FF7F00!important;
}
.portal-searchbar {
    width: calc(100%);
    text-align: right;
    position: relative;
    -webkit-transition: text-align 2s;
    transition: text-align 2s;
    margin: -10px 10px 10px -10px;
    padding: 10px;
    border-top: 1px solid #242427!important;
    border-bottom: 1px solid #242427!important;
    background: #f0f0f0;
    background: -moz-linear-gradient(top,#f0f0f0 0,#f0f0f0 65%,#e5e5e5 98%,#fff 99%,#fff 99%,#fff 100%,#fff 100%,#e5e5e5 100%,#fff 101%);
    background: -webkit-linear-gradient(top,#f0f0f0 0,#f0f0f0 65%,#e5e5e5 98%,#fff 99%,#fff 99%,#fff 100%,#fff 100%,#e5e5e5 100%,#fff 101%);
    background: linear-gradient(to bottom,#242427 0,#242427 65%,#242427 98%,#fff 99%,#242427 99%,#fff 100%,#fff 100%,#e5e5e5 100%,#fff 101%);
    -webkit-box-shadow: 0 2px 3px 0 rgba(101,101,101,.2);
    -moz-box-shadow: 0 2px 3px 0 rgba(101,101,101,.2);
    box-shadow: 0 2px 3px 0 rgba(101,101,101,.2);
}
.portal-searchbar ul.social-links a, .portal-searchbar ul.social-links span {
    text-decoration: none;
    color: #FF7F00!important;
    font-family: opendemi;
}
#reviews h2, #top_ad_section h2 {
    display: inline-block;
    color: #FF7F00!important;
    font-family: opendemi,Arial,Helvetica,sans-serif;
    width: 66%;
    width: calc(66% + 3px);
}
#content #reviews h2 span {
    background-color: #242427!important;
    padding-right: 20px;
    display: inline-block;
}
.bigusername {
    cursor: pointer;
    font-weight: 700;
    font-size: 105%;
    color: #CC6600!important;
}
#cssmenu>ul>li>a {
    padding: 25px 30px 14px;
    margin-top: 35px;
    font-size: 18px;
    color: #FF7F00!important;
    margin-left: 2px;
    text-transform: uppercase;
    text-decoration: none;
    background: rgba(251,251,251,1);
    background: -moz-linear-gradient(top,rgba(251,251,251,1) 0,rgba(246,246,246,1) 47%,rgba(235,235,235,1) 100%);
    background: -webkit-gradient(left top,left bottom,color-stop(0,rgba(251,251,251,1)),color-stop(47%,rgba(246,246,246,1)),color-stop(100%,rgba(235,235,235,1)));
    background: -webkit-linear-gradient(top,rgba(251,251,251,1) 0,rgba(246,246,246,1) 47%,rgba(235,235,235,1) 100%);
    background: -o-linear-gradient(top,rgba(251,251,251,1) 0,rgba(246,246,246,1) 47%,rgba(235,235,235,1) 100%);
    background: -ms-linear-gradient(top,rgba(251,251,251,1) 0,rgba(246,246,246,1) 47%,rgba(235,235,235,1) 100%);
