css:
@-moz-document domain("fimfiction.net") {
div.rating_container {
    display: inline-block;
    font-weight: bold;
    vertical-align: middle;
    height: 34px;
    text-align: center;
    position: relative;
    padding: 0px 12px
}
.rating_container .rating-bar {
    display: inline-block;
    border-style: solid;
    background: #c75b5b;
    vertical-align: -3px;
    position: relative;
    height: 16px;
    width: 75px;
    overflow: hidden;
    border: 1px solid rgba(0, 0, 0, 0.2);
    border-color: #979797;
    border-radius: 1px;
    outline-style: solid;
    outline-color: rgba(0, 0, 0, 0.2);
    outline-width: 1.5px;
}
.rating_container .rating-bar .like-bar {
    position: absolute;
    height: 16px;
    background: #75a83f;
    background-image: repeating-linear-gradient(-45deg, #75a83f 0px, #75a83f 6px, #81b945 6px, #81b945 12px)
}
.story-card .rating-bar {
    display: inline-block;
    border-style: solid;
    background: #c75b5b;
    vertical-align: 0px;
    position: relative;
    height: 10px;
    width: 50px;
    overflow: hidden;
    border: 1px solid rgba(0, 0, 0, 0.2);
    border-color: #979797;
    border-radius: 1px;
    outline-style: solid;
    outline-color: rgba(0, 0, 0, 0.2);
    outline-width: 1.5px;
}
.story-card .rating-bar span {
    position: absolute;
    height: 10px;
    background: #75a83f;
    background-image: repeating-linear-gradient(-45deg, #75a83f 0px, #75a83f 6px, #81b945 6px, #81b945 12px)
}
}
