css:
@-moz-document domain("wnp.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #e20513 #1c1c1c;
}
a,h1,h2,h3{
    color: #e20513 !important;
}
a:hover,h1:hover,h2:hover,h3:hover,small{
    color: #fff !important
}
select{
    background: #1c1c1c !important;
    color: #fff !important;
    border-bottom: 0 !important;
}
input{
    background: #1c1c1c !important;
    color: #fff !important;
}
/*HEADER*/
#header,#nav,div.help-nav{
    background: #1c1c1c;
}
#header h2.logo span,div.box-29 a.info strong,#nav ul.menu > li > a,div.help-nav div.pageWidth > ul > li a{
    color: #e20513;
}
#header h2.logo span:hover,div.box-29 a.info small,#nav ul.menu > li > a:hover,div.help-nav div.pageWidth > ul > li a:hover{
    color: #fff;
}
div.box-29:hover a.info strong{
    color: #1c1c1c;
}
div.help-nav div.pageWidth > ul::before{
    background: transparent;
    box-shadow: none;
}
form.form-5{
    border: 0;
}
form.form-5 input{
    background: #1c1c1c;
    color: #fff;
}
/*HEADER*/
/*STRONA-GŁÓNA*/
ul.list-2 small,ul.list-3 strong small,ul.list-4 small,ul.list-6 p,ul.list-2.v2 small,ul.list-9 small,ul.list-11 small{
    color: #fff;
}
div.box-5 div.head h2,div.box-8 h2,div.box-8 div.right{
    background: #1c1c1c;
}
div.box-5 div.head::before,div.box-8.premium::before,div.box-5 div.head::before, div.box-8.premium::before,div.box-8::before{
    border-bottom: 1px solid #E5E5E5 !important;
}
/*STRONA-GŁÓNA*/
/*ARTYKUŁ*/
div.breadcrumbs span,article p.lead{
    color: #fff;
}
ul.meta.is-premium::before{
    background: none;
}
div.box-r14,div.box-r14 input{
    background: #1c1c1c;
    border: 1px solid #fff;
}
div.box-r14 p.wynik{
    color: #e20513;
}
/*ARTYKUŁ*/
/*PODSTRONY*/
/*PREMIUM*/
header.new > div{
    background: #ffae00;
}
div.box-p2 div.desc small,div.box-p3 h2,div.box-p3 div.menu,ul.list-p2 div.desc,div.box-p4 div.img + div.desc,div.box-p5 div.head,ul.list-p3 div.desc,div.box-p6 div.head,div.box-p6 a.next, div.box-p6 a.prev{
    background: #1c1c1c;
}
div.box-p6 a.next:hover, div.box-p6 a.prev:hover,#nav ul.sub li:hover{
    background: #e20513;
}
div.box-p6 a.next img, div.box-p6 a.prev img{
    filter: invert();
}
/*PREMIUM*/
/*WYSZUKIWANIE*/
.gsc-control-cse,.gsc-webResult.gsc-result, .gsc-results .gsc-imageResult,.gsc-input-box{
    background: #1c1c1c;
}
.gsc-tabHeader.gsc-tabhActive{
    border: none;
}
body .gsc-control-cse .gsc-tabHeader.gsc-tabhInactive{
    color: #fff !important;
}
.gsc-result-info{
    color: #fff;
}
body .gsc-control-cse .gs-fileFormatType, body .gsc-control-cse .gs-imageResult .gs-snippet, body .gsc-control-cse .gs-imageResult a.gs-title:link, body .gsc-control-cse .gs-imageResult a.gs-title:link b, body .gsc-control-cse .gs-imageResult div.gs-visibleUrl, body .gsc-control-cse .gs-webResult .gs-snippet, body .gsc-control-cse .gs-webResult div.gs-visibleUrl, body .gsc-control-cse .gs-webResult.gs-result a.gs-title:link, body .gsc-control-cse .gs-webResult.gs-result a.gs-title:link b{
    color: #e20513 !important;
}
.gsc-results .gsc-cursor-box .gsc-cursor-current-page{
    background: #e20513 !important;
}
body .gsc-control-cse .gsc-results .gsc-cursor-box .gsc-cursor-page{
    background: #1c1c1c;
    border: 1px solid #e20513;
}
/*WYSZUKIWANIE*/
/*PODSTRONY*/
/*FOOTER*/
#footer h2 a img{
    background: #e20513;
    border-radius: 8px;
}
#footer{
    background: #1c1c1c;
    border-top: 1px solid #e20513
}
#footer div.body:last-child:after{
    content: "Ciemny motyw by Maciejozaman";
    color: #fff;
}
/*FOOTER*/
}
