css:
@-moz-document domain("drawception.com") {
.text-muted
  {
    color: var(--text-color-muted) !important;
  }

  :root
  {
    --bg-accent-color: #e6c3c3 !important;
    --bg-accent-highlight-color: #f2f2f2 !important;
    --bg-color: #f5ebd2 !important;
    --bg-hover-color: #6ecdbe !important;
    --bg-disabled-color: #6ecdbe !important;
    --bg-highlight-color: #e6c3c3 !important;
    --bg-duck-option: #e6c3c3 !important;
    --bg-duck-option-hover: #e6c3c3 !important;
    --text-color: #643241 !important;
    --text-hover-color: #e6c3c3 !important;
    --text-color-lighter: #e6c3c3 !important;
    --text-color-darker: #321e32 !important;
    --text-color-muted: #6ecdbe !important;
    --border-color: #e6c3c3 !important;
    --border-highlight-color: #e6c3c3 !important;
    --link-color: #284b41 !important;
    --link-color-hover: #379687 !important;
    --comment-highlight-color: #fffdc9 !important;
    --store-btn-bg-color: #f5ebd2 !important;
    --store-btn-bg-hover-color: #e6c3c3 !important;
    --store-btn-text-color: #b4556e !important;
    --modal-overlay-color: rgba(184,165,147,0.2) !important;
    --colorpicker-border-color: rgba(0,0,0,0.25) !important;
    --popup-border-color: #b4556e !important;
    --navbar-bg-color: #6ecdbe !important;
    --navbar-border-color: #b4556e !important;
    --navbar-btn-text-color: #643241 !important;
    --navbar-btn-text-hover-color: #b4556e !important;
    --navbar-btn-border-color: #b4556e !important;
    --navbar-btn-border-hover-color: #321e32 !important;
    --navbar-btn-text-shadow: 0px 0px 3px #3f8fa0 !important;
    --progress-bar-bg-color: #c8aac8 !important;
  }

  .text-danger
  {
    color: var(--navbar-btn-border-color) !important;
  }

  .btn-primary
  {
    background-color: var(--navbar-btn-text-hover-color) !important;
  }

  a:focus, a:hover
  {
    color: var(--link-color-hover) !important;
    text-decoration: underline !important;
  }

  .label-warning
  {
    background-color: var(--border-color) !important;
  }

  .theme-night
  {
   --bg-accent-color: #e6c3c3 !important;
    --bg-accent-highlight-color: #f2f2f2 !important;
    --bg-color: #f5ebd2 !important;
    --bg-hover-color: #6ecdbe !important;
    --bg-disabled-color: #6ecdbe !important;
    --bg-highlight-color: #e6c3c3 !important;
    --bg-duck-option: #e6c3c3 !important;
    --bg-duck-option-hover: #e6c3c3 !important;
    --text-color: #643241 !important;
    --text-hover-color: #e6c3c3 !important;
    --text-color-lighter: #6ecdbe !important;
    --text-color-darker: #321e32 !important;
    --text-color-muted: #b4556e !important;
    --border-color: #e6c3c3 !important;
    --border-highlight-color: #e6c3c3 !important;
    --link-color: #284b41 !important;
    --link-color-hover: #379687 !important;
    --comment-highlight-color: #fffdc9 !important;
    --store-btn-bg-color: #f5ebd2 !important;
    --store-btn-bg-hover-color: #e6c3c3 !important;
    --store-btn-text-color: #b4556e !important;
    --modal-overlay-color: rgba(184,165,147,0.2) !important;
    --colorpicker-border-color: rgba(0,0,0,0.25) !important;
    --popup-border-color: #b4556e !important;
    --navbar-bg-color: #8cccbb !important;
    --navbar-border-color: #b4556e !important;
    --navbar-btn-text-color: #643241 !important;
    --navbar-btn-text-hover-color: #b4556e !important;
    --navbar-btn-border-color: #b4556e !important;
    --navbar-btn-border-hover-color: #321e32 !important;
    --navbar-btn-text-shadow: 0px 0px 3px #3f8fa0 !important;
    --progress-bar-bg-color: #c8aac8 !important;
  }

  .progressbar-bg
  {
    background-color: var(--navbar-btn-border-color) !important;
  }
}
