@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mingpaonews.com"), domain("mingpao.com"), domain("ol.mingpao.com"), domain("mpfinance.com"),domain("inews.mingpao.com")

{

* {background-color: #CCEECC !important;
 color: #000000 !important;}

/* 刪廣告 */
iframe[src*="adc"],
iframe[src*="overture"],
#footer.ft{display:none!important}

}

@-moz-document domain("mingpaonews.com")

{

* {width: auto !important;}

 .top_menu_bg
{font-size: 0em !important;}

 .mainbox_col
h1 {font-size: 1.1em !important;}
body {font-size: 0.9em !important;}

 .news_box
{font-size: 1.0em !important;}

}



@-moz-document domain("mingpao.com")

{

* {width: auto !important;}

.rlink
{font-size: 0em !important;}

 .top_menu_bg
{font-size: 0em !important;}

 .content_col
h1 {font-size: 1.4em !important;}
body {font-size: 1.0em !important; line-height: 1.6em !important;}

 .news_box
{font-size: 1.0em !important;}

 .left_col
{font-size: 0.1em !important;}

.content_big
{display:none!important;}

.push
{display:none!important;}

.content_medium_g
{display:none!important;}

.bottom_space
{display:none!important;}

}



@-moz-document domain("ol.mingpao.com")

{

* {font-size: 1.015em !important;}

}

@-moz-document domain("mpfinance.com")

{

* {font-size: 1.02em !important;}

}

@-moz-document domain("inews.mingpao.com")

{

 .top_menu_bg
{font-size: 0.7em !important;}

 .mainbox_col
{font-size: 1.0em !important;}

 .news_box
h1 {font-size: 1.5em !important;}
body {font-size: 0.9em !important;}
*{line-height: 1.6em !important;}

 .left_col
{font-size: 0.1em !important;}

}
