css:
@-moz-document url-prefix("https://www.mountainbike.nl/forum/") {
:root {
    --zwart:    #000000;
    --grijs:    #252525;
    --grijs2:    #353535;
    --grijs3:   #454545;
    --wit:      #ffffff;
    --wit2:      #d0d0d0;
    --wit3:      #b0b0b0;
    --rood:     #c41414;
  }

html {
    background-color: var(--zwart); }

div#inner-wrap {
    background-color: var(--grijs); }

ul.topiclist {
    background-color: var(--grijs3); }

li.row {
    color: var(--wit3);
    background-color: var(--grijs2); }

li.row strong {
    color: var(--wit); }

a {
    color: var(--wit2); }

h2 {
    color: var(--wit); }

.panel-container h2 {
    color: var(--wit); }

dl.details dt {
    color: var(--wit); }

div {
    color: var(--wit2); }

.post:target .content {
    color: var(--wit3); }

div.viewtopic_wrapper {
    background-color: var(--grijs3); }

div.post {
    background-color: var(--grijs2); }

div.post strong {
    color: var(--wit); }

.postprofile {
    color: var(--wit3); }

div.content {
    color: var(--wit3); }

div.panel.panel {
    color: var(--wit3);
    background-color: var(--grijs3); }

table.table1 td {
    color: var(--wit2);
    background-color: var(--grijs2); }

table.table1 thead th {
    background-color: var(--rood); }

.cp-mini {
    background-color: var(--grijs2); }

.cp-main .pm {
    background-color: var(--grijs2); }

.codebox code {
    background-color: var(--grijs3); }

blockquote {
    background-color: var(--grijs3); }

blockquote blockquote {
    background-color: var(--grijs2); }

blockquote blockquote blockquote {
    background-color: var(--grijs3); }

.phpbb_alert {
    color: var(--wit3);
    background-color: var(--grijs3); }

div.dropdown-contents.dropdown-nonscroll {
    color: var(--wit3);
    background-color: var(--grijs3); }

ul.dropdown-contents.dropdown-nonscroll {
    color: var(--wit3);
    background-color: var(--grijs3); }

ul.dropdown-contents.search-results {
    color: var(--wit3);
    background-color: var(--grijs3); }

a.jumpbox-sub-link {
    color: var(--wit3);
    background-color: var(--grijs3); }

select {
    color: var(--wit3);
    background-color: var(--grijs2); }

input.button2 {
    color: var(--zwart);
    background-color: var(--wit3); }

.inputbox {
    color: var(--zwart);
    background-color: var(--wit3); }

.button {
    color: var(--zwart);
    background-color: var(--wit3); }

.panel .button {
    background-color: var(--wit3); }

a.button.specialbutton {
    background-color: var(--rood); }

.pagination li a {
    color: var(--wit);
    background-color: var(--grijs3); }

.button .icon, .button-secondary {
    color: var(--zwart); }

.panel .inputbox {
    background-color: var(--wit3); }

dd label {
    color: var(--wit3); }

dl.details dd {
    color: var(--wit3); }

dl.mini dt {
    color: var(--wit); }

.navigation .active-subsection a {
    background-color: var(--wit3); }

.navigation a {
    background-color: var(--wit3); }

.tabs .tab > a {
    color: var(--zwart);
    background-color: var(--wit3); }

.fancy_panel {
    background-color: var(--grijs); }
}
