.dIehj::before {
    position: absolute;
    top: 21px;
    right: auto;
    bottom: auto;
    left: 20px;
    z-index: 1;
    content: '';
    display: block;
    width: 4px;
    height: 4px;
    border-radius: 2px;
    background: #FF7F00;
}
div.Kj-JD-Jl>button.J-at1-atl, div.Kj-JD-Jl>button.J-at1-auR {
    align-items: center;
    border: none;
    display: inline-flex;
    justify-content: center;
    outline: none;
    position: relative;
    z-index: 0;
    -webkit-font-smoothing: antialiased;
    font-family: 'Google Sans',Roboto,RobotoDraft,Helvetica,Arial,sans-serif;
    font-size: .875rem;
    letter-spacing: .25px;
    background: none;
    border-radius: 4px;
    box-sizing: border-box;
    color: #5f6368;
    cursor: pointer;
    font-weight: 500;
    height: 36px;
    min-width: 80px;
    padding: 0 16px;
    background-color: #FF7F00;
    margin: 0 0 0 8px;
}
.hG.T-I-atl, .hG.T-I-atl.T-I-JW, .hG.T-I-atl:focus {
    border-left: 1px solid #2c2c2c;
}
.T-I-atl.T-I-JW, .T-I-atl:hover {
    box-shadow: 0 1px 2px 0 #2c2c2c, 0 1px 3px 1px rgb(44, 44, 44);
    background-color: #989898;
    border: none;
}
.T-I-atl:focus {
    box-shadow: 0 1px 2px 0 rgb(44, 44, 44), 0 1px 3px 1px rgb(44, 44, 44);
    background-color: #FF7F00;
    border: none;
}
div.Kj-JD-Jl>button.J-at1-atl:hover, div.Kj-JD-Jl>button.J-at1-auR:hover {
    box-shadow: 0 1px 2px 0 rgb(26, 26, 26), 0 1px 3px 1px rgb(26, 26, 26);
    background-color: #989898;
    border: none;
}
.dC:hover {
    box-shadow: 0 1px 2px 0 rgb(19, 19, 19), 0 1px 3px 1px rgb(19, 19, 19);
    background-color: #131313;
}
::-webkit-scrollbar {
	width: 10px!important;
	height: 10px!important;
}
::-webkit-scrollbar-button {
	width: 0px!important;
	height: 0px!important;
}
::-webkit-scrollbar-thumb {
	background: #cc6600!important;
	border: none!important;
	border-radius: 0px!important;
}
::-webkit-scrollbar-thumb:hover {
	background: #FF9900!important;
}
::-webkit-scrollbar-thumb:active {
	background: #FF9900!important;
}
::-webkit-scrollbar-track {
	background: #333333!important;
	border: none!important;
	border-radius: 0px!important;
}
::-webkit-scrollbar-track:hover {
	background: #333333!important;
}
::-webkit-scrollbar-track:active {
	background: #333333!important;
}
::-webkit-scrollbar-corner {
	background: transparent!important;
}
