/* latin-ext */
@font-face
{
  font-family: 'Lexend Deca';
  font-style: normal;
  font-weight: 400;
  font-display: swap;
  src: local('Lexend Deca Regular'), local('LexendDeca-Regular'), url(https://fonts.gstatic.com/s/lexenddeca/v1/K2F1fZFYk-dHSE0UPPuwQ5qpJy_KZA.woff2) format('woff2');
  unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face
{
  font-family: 'Lexend Deca';
  font-style: normal;
  font-weight: 400;
  font-display: swap;
  src: local('Lexend Deca Regular'), local('LexendDeca-Regular'), url(https://fonts.gstatic.com/s/lexenddeca/v1/K2F1fZFYk-dHSE0UPPuwQ5qnJy8.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}

@-moz-document domain("www.sparknotes.com")
{
  html
  {
    color: white;
    background: #121212;
    font-family: 'Lexend Deca', sans-serif;
  }

  ::selection
  {
    background: #99AAB5;
    font-family: 'Lexend Deca', sans-serif;
  }

  .interior-sticky-nav__navigation, .interior-sticky-nav__main
  {
    background: #23272A;
    font-family: 'Lexend Deca', sans-serif;
  }

  .interior-sticky-nav
  {
    border-bottom: 1px solid #7289DA;
    font-family: 'Lexend Deca', sans-serif;
  }

  .interior-sticky-nav__navigation__list--short__link--active
  {
    color: white !important;
  }

  .interior-sticky-nav__navigation__list--short__link
  {
    color: #99AAB5;
  }

  .interior-sticky-nav
  {
    background: #212121;
    font-family: 'Lexend Deca', sans-serif;
  }

  .title-nav
  {
    background: #212121;
    font-family: 'Lexend Deca', sans-serif;
  }

  .title-nav__item>a
  {
    color: #99AAB5;
    transition-duration: 0.2s;
    font-family: 'Lexend Deca', sans-serif;
  }

  .subnav__bold a
  {
    color: white;
    font-family: 'Lexend Deca', sans-serif;
  }

  .subnav__light
  {
    color: white;
    font-family: 'Lexend Deca', sans-serif;
  }

  .interior__page__title
  {
    color: white;
    font-family: 'Lexend Deca', sans-serif;
  }

  p
  {
    font-family: 'Lexend Deca', sans-serif;
    color: #fafafa;
  }

  h3
  {
    font-family: 'Lexend Deca', sans-serif;
    color: white;
  }

  .quotation
  {
    font-family: 'Lexend Deca', sans-serif;
    color: #fafafa;
  }

  .most-viewed-pages__heading
  {
    font-family: 'Lexend Deca', sans-serif;
    color: white;
  }

  .most-viewed-blogs__heading
  {
    color: white;
  }

  .most-viewed-blogs__link__name
  {
    color: #99AAB5;
  }

  .interior-sticky-nav__title__text
  {
    color: white;
  }

  .global-header
  {
    background: #23272A;
  }

  .nav-link
  {
    color: white !important;
  }

  #search-overlay
  {
    background: #23272A;
  }

  .search-overlay__search-bar
  {
    background: #2C2F33;
    border-bottom: 0px solid #7289DA !important;
    border-left: 25px solid #2C2F33 !important;
    color: white !important;
  }

  #overlay-search
  {
    background: #2C2F33;
    border-bottom: 2px solid #7289DA !important;
  }

  .search-overlay__wrapper
  {
    background: #2C2F33 !important;
  }

  #search-suggestions li a
  {
    color: #99AAB5;
  }

  #search-suggestions::hover li a
  {
    background: #7289DA;
    color: #99AAB5;
  }

  .sub-list, .list-grid
  {
    background: #2C2F33 !important;
  }

  blockquote
  {
    color: black;
  }

  .footer
  {
    background: #23272A;
  }

  .footer p
  {
    color: white !important;
  }

  .footer a
  {
    color: white !important;
  }

  .footer h2
  {
    color: white !important;
  }

  b, .container--faq, .container--faq h3
  {
    color: white;
    font-family: 'Lexend Deca', sans-serif;
  }

  .quick-quiz-question, #result-zone
  {
    background: #23272A !important;
    font-family: 'Lexend Deca', sans-serif;
  }

  .noFear__cell--modern
  {
    padding-left: 12px;
    color: white;
    font-family: 'Lexend Deca', sans-serif;
  }

/* Quotes */
  .mainTextContent__quote
  {
    background-color: #212121;
    color: #ffffffde;
  }

  .mainTextContent__quote__line
  {
    color: #ffffff;
  }

/* search icon */
  .img[type="image/svg+xml"]
  {
    color: #ffffffde;
  }

/* Shakey shakey */
  .summary_sentence
  {
    color: #ffffff;
  }

  .texts-landing-page__toc__section__link__description
  {
    color: #ffffffde;
  }

  .texts-landing-page__toc__section__title
  {
    color: #ffffff;
  }
  
  .most-viewed-pages__link:hover {
    background-color: #242424;
  }
/* END - DO NOT TOUCHY */
}
