css:
@-moz-document domain("vatsim.uk") {
:root {
    --bg1: hsl(200, 19%, 13%);
    --bg2: hsl(200, 19%, 15%);
    --bg3: hsl(200, 19%, 18%);
    --bg4: hsl(200, 19%, 20%);
    --bg5: hsl(200, 19%, 24%);
    --bg6: hsl(200, 19%, 27%);

    --selected: hsl(174, 38%, 66%);
    --selected2: hsl(174, 38%, 55%);

    --btn: hsl(174, 38%, 40%);
    --btn2: hsl(174, 29%, 33%);
    --btn3: hsl(174, 21%, 22%);
    --btn4: hsl(174, 25%, 15%);
}

textarea:focus {
    background-color: var(--bg1) !important;
}
body, html {
    background-color: var(--bg1) !important;
}
#container, .container, .container-fluid {
    background: var(--bg1) !important;
    color: var(--selected) !important;
}
.panel-body, .panel-ukblue, .header_container {
    background: var(--bg5) !important;
    color: var(--selected) !important;
}
.pagetitle, .breadcrumb, .breadcrumb_outer_container, .breadcrumb_container, .breadcrumb_content_left, .listmenu, .list, input[type="text"], input[type="checkbox"], .info-box, .zulu_remind {
    background: var(--bg4) !important;
    color: var(--selected2) !important;
}
.subheadbox, .table-striped tr {
    background: var(--bg6) !important;
    color: var(--selected2) !important;
}
.menu, table td, .cell_blue_right, .header_container {
    background: var(--bg6) !important;
    color: var(--selected) !important;
}
input[type="submit"] {
    background: var(--btn2) !important;
    color: var(--selected2) !important;
}
.highlight:not(.lines) tr:not(.inline-comments):hover td.selected-line.blob-num:not(.line-age),
.highlight:not(.lines) tr:not(.inline-comments):hover td.blob-num,
.blob-num[colspan],
.blob-num-hunk {
    background: var(--bg3) !important;
}
::-webkit-scrollbar-track,
::-webkit-scrollbar-corner {
    background: var(--bg3) !important;
}
::-webkit-scrollbar-thumb {
    background: rgba(255, 255, 255, 0.1) !important;
}
.container, .foot div, .font_10 {
    color: var(--selected) !important;
}
.container a, .foot div a, .font_10 a {
    color: var(--selected2) !important;
}
}
