@-moz-document domain("bilibili.com") {
html {
	/*全局容器圆角*/
	--radius: 10px;
	/*顶栏毛玻璃模糊程度*/
	--topbar-blur: 20px;
	/*全局容器透明度*/
	--opacity: 1;
	/*容器背景色看，solid有时是用来做遮挡色，比如没视频时就用遮挡色覆盖结果*/
	--blue: hsl(195, 100%, 42%);
	--pink: hsl(340, 85%, 66%);
	--yellow: hsl(33, 95%, 61%);
	--green: hsl(142, 61%, 55%);
	/*主题色*/
	--theme-color: hsl(195, 100%, 42%);
	--theme-light-color: hsla(195, 100%, 42%, .4);
	/*字体颜色*/
	/*透明填充色，用于鼠标划过元素后添加相应效果*/
	/*鼠标划过一个列表的发亮，如划过每一条评论会有淡淡的白色遮罩*/
	/*分隔线颜色*/
	/*浮层颜色、暗色模式通过改变背景颜色体现z轴位置关系，阴影不能在暗色模式完全显示*/
	/*顶栏或固定顶栏的阴影*/
	/*card-shadow主要是每个板块的阴影，如首页的动画、番剧、国创区...*/
	/*card-shadow2这个阴影是用来做一些小弹窗用的，如用户信息卡片、评论中的表情选择框、评论区的勋章说明窗、视频中的标签简介*/
	/*还有就是用于首页或其它分区页的横向分区导航栏（也就是首页、动画、番剧、国创、音乐...的横向长列表栏）中的划过分区后的子导航项的阴影，如动画划过还有MAD-AMV、MMD-3D这些选项阴影*/
	/*dialog-shadow是一些弹窗并背景变暗的阴影，如关注up主后的提示、充电提示、收藏提示*/
	/*滤镜效果，专门把过于亮色的图标做处理*/
	--filter-darken: invert(1) hue-rotate(180deg) brightness(1.5);
	/*直接把图标变为白色*/
	/*这里能把粉色的图标强制变为蓝色*/
	/*以下都是选择主题所覆盖的变量。。。*/
	/*蓝色亮色*/
	--bgcolor: #EFEFF4;
	--topbar-color: rgba(255, 255, 255, .9);
	--bg-primary: rgba(255, 255, 255, .9);
	--bg-primary-solid: rgb(255, 255, 255);
	--bg-secondary: rgba(239, 239, 244, var(--opacity));
	--bg-secondary-solid: rgb(239, 239, 244);
	--bg-tertiary: rgba(220, 220, 234, var(--opacity));
	--bg-tertiary-solid: rgb(220, 220, 234);
	--blue-light: hsla(195, 100%, 42%, .4);
	--pink-light: hsla(340, 85%, 66%, .4);
	--yellow-light: hsla(33, 95%, 61%, .4);
	--green-light: hsla(142, 61%, 55%, .4);
	--content-border-color: transparent;
	--line-color: rgb(231, 231, 240);
	--text-primary: #000;
	--text-secondary: hsl(0, 0%, 55%);
	--text-tertiary: hsl(0, 0%, 20%);
	--fill-primary: rgba(120, 120, 128, .24);
	--fill-secondary: rgba(120, 120, 128, .14);
	--fill-tertiary: rgba(120, 120, 128, .08);
	--fill-quaternary: rgba(120, 120, 128, .05);
	--line-color: rgba(120, 120, 128, .08);
	--elevated-primary: rgba(255, 255, 255, var(--opacity));
	--elevated-secondary: rgba(255, 255, 255, var(--opacity));
	--elevated-tertiary: rgba(255, 255, 255, var(--opacity));
	--shadow-dark-color: hsla(0, 0%, 0%, .08);
	--shadow-color: hsla(0, 0%, 0%, .03);
	--topbar-shadow: 0 5px 10px hsla(0, 0%, 0%, .03), 0 10px 30px hsla(0, 0%, 0%, .08);
	--card-shadow: 0 5px 10px hsla(0, 0%, 0%, .03);
	--card-shadow2: 0 0 10px hsla(0, 0%, 0%, .03), 0 15px 20px hsla(0, 0%, 0%, .08);
	--dialog-shadow: 0 0 20px hsla(0, 0%, 0%, .03), 0 10px 30px hsla(0, 0%, 0%, .08), 0 30px 80px 30px var(--shadow-dark-color);
	--filter-white: opacity(1);
	--filter-pink-turn-blue: hue-rotate(230deg) brightness(1.12);
	--filter-blue-turn-pink: opacity(1);
	--filter-brightness: contrast(0);
}

@keyframes showInfo {
	0% {
		opacity: 0;
		transform: translate(0, 40%);
	}
}
}

@-moz-document url-prefix("https://www.biligame.com/"), url-prefix("https://www.bilibili.com/cinema/"), regexp("^https?://www\\.bilibili\\.com/?((index\\.html)|(\\?spm_id_from.*))?"), regexp("^https?://www\\.bilibili\\.com/(v/)?(douga|anime|guochuang|music|dance|game(?!/match)|technology|digital|life|kichiku|fashion|ad|ent|cinephile).*"), regexp("^https?://www\\.bilibili\\.com/(watchlater|ranking|account).*"), regexp("^https?://www\\.bilibili\\.com/(video|bangumi/play)/(av|ss|ep).*"), regexp("^https?://(search|space|message|live)\\.bilibili\\.com.*"), regexp("^https?://t\\.bilibili\\.com/?(\\?tab=\\d*)?"), regexp("^https?://elec\\.bilibili\\.com/plugin_v3/elec-plugin.html.*"), regexp("^https://www\\.bilibili\\.com/read/.*") {
/* 全局 */
html {
	background: none;
}
body,
.bili-header-m > .nav-menu {
	background-attachment: fixed;
	background-size: cover;
	background-repeat: no-repeat;
	background-position: center;
}

body,
.bili-header-m,
.footer .bili-footer .footer-wrp,
body div.international-fooler,
body div.footer.bili-footer,
.bili-footer .footer-wrp,
body .international-footer,
body .page-container {
	background-color:var(--bgcolor);
}
.nav-menu {
	min-width: 1120px!important
}
/** **滚动条调整
 **/
.ps__rail-y,
.mCSB_draggerRail,
.ps__scrollbar-y-rail,
/* .bscroll-vertical-scrollbar, */
/*弹幕列表划过或下拉的滚动条背景*/
.danmaku-wrap .player-auxiliary-area .player-auxiliary-danmaku-wrap .bscroll-vertical-scrollbar.mousedown,
.danmaku-wrap .player-auxiliary-area .player-auxiliary-danmaku-wrap:hover .bscroll-vertical-scrollbar {
	background-color: transparent;
}

::-webkit-scrollbar,
.custom-scrollbar::-webkit-scrollbar {
	width: 8px;
	height: 8px;
}

::-webkit-scrollbar-track-piece,
::-webkit-scrollbar-corner,
.custom-scrollbar::-webkit-scrollbar-track-piece {
	background: transparent;
}
body::-webkit-scrollbar-track-piece,
body::-webkit-scrollbar-corner {
	background: rgb(239, 239, 244);
}


::-webkit-scrollbar-thumb,
.custom-scrollbar::-webkit-scrollbar-thumb {
	background-color: hsla(0, 0%, 50%, .7);
	border-radius: 10px;
	border: none;
}
::-webkit-scrollbar-thumb:hover,
::-webkit-scrollbar-thumb:active {
	background-color: hsla(0, 0%, 50%, 1);
}
/**
 **搜索建议调整
 **/
/*去除热度搜索*/
.suggest-wrap .history-wrap {
	margin-top: 10px;
}

/*搜索建议中右下角“清空所有搜索历史按钮”位置调整*/
.home-wrap .suggest-wrap {
	/*撑大搜索建议，给“清空所有搜索历史按钮”留空间*/
	padding-bottom: 8px!important;
	padding-top: 0;
}

.suggest-wrap .history-wrap .clearall {
	bottom: 0px;
}

/*颜色改成醒目的红色*/
.suggest-wrap .history-wrap .clearall a {
	color: hsl(195, 100%, 42%);
	transition: .25s;
}
.suggest-wrap .history-wrap .clearall a:hover {
	color: hsl(340, 85%, 66%);
}

/* 放大镜 */
/* .nav-search #nav_searchform button.nav-search-submit {
	top: -11%;
	margin-top: 14px;
	background-color: transparent;
}
.nav-search .nav-search-keyword + .nav-search-submit,
.search-submit {
	transition: right .3s, transform .3s;
}
 */
.custom-navbar form button[type=submit]:hover {
	transform: scale(1.3) rotate(-7deg) translate(0px, -11px);
}
/*搜索建议*/
/* .bilibili-suggest,
.suggest-wrap {
  animation: .5s showInfo;
  background: var(--bg-primary);
  border: none!important;
  box-shadow: var(--card-shadow2);
  overflow: hidden;
  transition: .25s;
  margin-top: 5px;
  padding-bottom: 0;
	border-radius:5px;
} */
/* 返回顶部 */
.go-top-m {
	margin-left: 600px
}
.go-top-m .go-top,
.right-side-bar .to-comment,
.right-side-bar .to-top {
	border-radius: 40px;
	transition: background-color .25s;
	box-shadow: var(--card-shadow2);
	cursor: pointer;
}
.go-top-m .go-top.icon {
	transform: scale(.8);
}
.go-top-m .go-top:hover,
.right-side-bar .to-comment:hover,
.right-side-bar.on .to-top:hover {
	border-radius: 40px;
	/* 	border: none; */
	box-shadow: 0 6px 10px 0 hsla(195, 100%, 42%, .4);
}
/**
 **标签栏调整
 **/
/*首页与分区页面板块标签栏调整，适当调整了文字大小*/
.report-wrap-module .bili-tab {
	font-size: 14px;
	line-height: 28px;
	margin-left: 20px;
	padding: 2px 0;
}
/* 某些标签栏如果不添加padding的话会产生边框下半不能完整显示 */
.bili-tab-item,
.tab-list li,
.season-item,
.be-tab,
.vl-hd #tab_list_type .tab-i,
.tab-item,
.comment-header .tabs-order li {
	transition: color .25s;
	color: hsl(0, 0%, 55%);
	border: none!important;
}

.tab-item.active,
.tab-item:hover,
.season-item:hover,
.season-item.on,
.bili-tab-item:hover,
.bili-tab-item.on,
.tab-list li:hover,
.tab-list li.on,
.comment-header .tabs-order li:hover,
.comment-header .tabs-order li.on {
	color: #000!important;
}

.tab-list.type-tab li.on {
	color: hsl(195, 100%, 42%)!important;
	border-color: transparent;
}

/*去除尖角与原本的横线*/
.bili-tab .bili-tab-item:beforer {
	opacity: 0;
}
/*去除评论分隔线*/
.comment-list .list-item .con,
/*去除转发动态分割线*/
.dynamic-list-item-wrap {
	border: none!important;
}
.season-item::after,
.bili-tab-item::after,
.be-tab::after,
.vl-hd #tab_list_type .tab-i::after,
.tab-item:after,
.comment-header .tabs-order li::after {
	content: '';
	display: block!important;
	position: absolute;
	bottom: 0;
	transition: .25s ease-in-out;
	/* 	视频评论区按热度、时间排序设置了margin-left的属性，醉了 */
	margin-left: 0!important;
	width: 100%!important;
	height: 0!important/*视频评论区按热度、时间排序设置了height:4，醉了*/;
	left: 0!important;
	border: 2px solid hsl(195, 100%, 42%)!important;
	transform: scalex(0) translatey(2px)!important;
}

/*分区中的子分区，如动画分区中的短片-手书-配音那里最新动态中的投稿时间、视频热度排序标签栏*/
/*比如这个链接 https://www.bilibili.com/v/douga/voice/#/  */
.video-list .vl-hd .tab-list.order-tab li:after {
	content: '';
	position: absolute;
	margin-left: -84px;
	bottom: 0;
	width: 84px;
	height: 4px;
	background-color: #00A1D6;
	transform: scalex(0) translatey(2px);
	/*防止动画向上偏移*/
	transition: transform .25s ease-in-out;
}

.season-item.on:after,
.bili-tab-item.on:after,
.tab-list li.on:after,
.vl-hd #tab_list_type .tab-i.on:after,
.comment-header .tabs-order li.on:after {
	transform: scalex(.6) translatex(-4%) translatey(2px)!important;
}

/*消息页我的消息应援团助手标签页修改*/
.group-helper-msg-list .tab-item::after {
	top: 18px;
	height: 0;
	border: 2px solid hsl(195, 100%, 42%);
}

.tab-list .tab-item.active:before {
	display: none;
}
/*标签栏滑动标签条 */
.be-tab-cursor {
	border: none;
}

.be-tab-cursor::before {
	content: '';
	position: absolute;
	width: 100%;
	height: 4px;
	background-color: #00A1D6;
	transform: scalex(.6) translatey(-2px)
}

.be-tab-cursor::after {
	display: none;
}

.be-tab-item {
	color: hsl(0, 0%, 55%);
	font-size: 14px;
	transition: color .25s;
}

.default,
.back-to-info {
	background-color: transparent;
}

.default:hover,
.back-to-info:hover {
	border-color: hsl(195, 100%, 42%)!important;
	color: #fff!important;
}
/*默认按钮*/
.submit .cancel,
.btn-box .message,
.btn-cancel,
.watch-later-list header .s-btn,
.more-link,
.draft-button,
.btn.default,
#id-card .idc-btn.btn:last-of-type,
/*番剧详情、查看全部点评*/
.bangumi-media .bangumi-media-header .btn-more-review,
/*番剧详情、更多*/
.media-tab-module-wrp .media-tab-module-more,
/*个人空间>收藏>批量操作>返回、清楚失效内容*/
#page-fav .fav-main .fav-action-top .back-to-info,
/*旧版番剧播放页承包榜-查看承包榜*/
.sponsor-rank-v1 .sponsor-rank-header .show-sponsor-rank,
/*空间粉丝关注按钮*/
.h-f-btn.h-follow,
/*这是空间中的稍后再看和更多按钮*/
#page-index .channel .section-right-options .operation,
.section .more {
	transition: color .25s, background-color .25s;
}

.submit .cancel:hover,
.btn-box .message:hover,
.btn-cancel:hover,
.watch-later-list header .s-btn:hover,
.more-link:hover,
.draft-button:hover,
.btn.default:hover,
#id-card .idc-btn.btn:last-of-type:hover,
#page-index .channel .section-right-options .operation:hover,
.bangumi-media .bangumi-media-header .btn-more-review:hover,
.media-tab-module-wrp .media-tab-module-more:hover,
#page-fav .fav-main .fav-action-top .back-to-info:hover,
.sponsor-rank-v1 .sponsor-rank-header .show-sponsor-rank:hover,
.section .more:hover,
.h-f-btn.h-follow:hover {
	background-color: rgba(120, 120, 128, .44);
	border-color: rgba(220, 220, 234, .9);
	box-shadow: var(--card-shadow);
}
/*按钮图标颜色强制变白调整，方便在彩色背景中突出*/
.read-push .icon,
.link-more .icon,
.new-stat-more i,
.back-to-info:not(:hover),
.follow-more i,
.random-change i {
	filter: brightness(3);
}
/*针对灰色按钮做的图标调整，灰色按钮在变为亮色后图标颜色要变会黑色*/
.sec-rank .more-link .icon-arrow-r {
	filter: contrast(0);
}

/*亮灰色按钮*/
.watch-later-list header .d-btn {
	background-color: rgba(220, 220, 234, .9)!important;
	border: none;
}

/*蓝色发光按钮*/
/*首页与分区的xxx条新动态，换一换按钮，带有刷新图标，在更多旁边的按钮*/
.btn.btn-change,
.read-push,
.change-push,
/* .attention-btn, */
.random-change {
	border-radius: 3px;
	background-color: hsl(195, 100%, 42%)!important;
	border: none;
	color: white!important;
	box-shadow: 0 4px 10px var(--card-shadow);
}
/* 蓝色发光变大 */
.read-push:hover,
.change-push:hover,
/* .attention-btn:hover, */
.random-change:hover,
.btn.btn-change:hover,
.bilibili-player-upinfo-follow:hover {
	transform: scale(1.05);
	background-color: hsl(195, 100%, 42%);
	box-shadow: 0 4px 10px hsla(195, 100%, 42%, .4);
}
/*蓝色发光按钮（无放大效果）*/
/*无法用上面的效果*/
.random-change:hover {
	background-color: hsl(195, 100%, 42%);
	border: none;
	color: white;
	box-shadow: 0 3px 8px var(--card-shadow);
	transition: .25s;
}
/*首页和分区也在xxx条新动态旁边的更多按钮*/
.btn.more,
.video-floor-m .zone-title .headline .link-more,
.block-area .block-left .block-header a.link-more,
.rank-header .more,
.new-stat-more,
.follow-right-module .follow-head .follow-more {
	border-radius: 3px;
	background-color: hsl(340, 85%, 66%)!important;
	border: none!important;
	color: white!important;
	box-shadow: 0 4px 10px var(--card-shadow)!important;
}
/* 粉色发光变大 */
.btn.more:hover,
.rank-header .more:hover,
.new-stat-more:hover,
.follow-more:hover,
.link-more:hover {
	transition: .25s;
	box-shadow: 0 4px 10px hsla(340, 85%, 66%, .4);
	background-color: hsl(340, 85%, 66%);
	transform: scale(1.05);
}
/* 粉色无变大 */
/* {
	background-color: var(--pink)!important;
  border: none!important;
  color: white;
	box-shadow: 0 3px 8px var(--card-shadow);
  transition: .25s!important;
} */
.btn.more:active,
.rank-header .more:active,
.new-stat-more:active,
.follow-more:active,
.link-more:active,
.btn.more:active,
.btn.btn-change:active,
.read-push:active,
.change-push:active,
/* .attention-btn, */
.random-change:active {
	background-color: hsl(340, 85%, 66%);
	transform: scale(1);
	box-shadow: 0 2px 5px var(--card-shadow);
}
/*蓝色发光放大按钮*/
a.like,
.b-gz,
.primary,
.btn-submit,
.btn-follow.cinema,
.submit-move,
.confirm,
.tag-info-pane .btn-subscribe,
.attention-btn {
	transition: .25s!important;
	background-color: hsl(195, 100%, 42%)!important;
	box-shadow: 0 2px 5px hsla(195, 100%, 42%, .4)!important;
	outline: none!important;
}

a.like:hover,
.b-gz:hover,
.primary:hover,
.btn-submit:hover,
.btn-follow.cinema:hover,
.follow-btn:hover,
.submit-move:hover,
.confirm:hover,
.tag-info-pane .btn-subscribe:hover,
