/*发送评论*/
.bb-comment .comment-send .comment-submit {
	transition: .25s;
	border: none;
	color: transparent;
	overflow: hidden;
	box-shadow: 0 2px 5px hsla(195, 100%, 42%, .4);
}

.bb-comment .comment-send.no-login .comment-submit {
	transform: none!important;
	box-shadow: none;
}
.bb-comment .comment-send:not(.no-login) .comment-submit:hover {
	transform: scale(1.03);
	box-shadow: 0 5px 10px hsla(340, 85%, 66%, .4);
}

.bb-comment .comment-send:not(.no-login) .comment-submit:active {
	transform: scale(1);
	box-shadow: 0 2px 5px hsla(340, 85%, 66%, .4);
}

.bb-comment .comment-send:not(.no-login) .comment-submit:before {
	content: '发表评论';
	color: #fff;
	position: absolute;
	top: -1px;
	bottom: 0;
	left: 0;
	right: 0;
	font-size: 14px;
	padding: 14px 20px;
	z-index: 1;
}
.bb-comment .comment-send:not(.no-login) .comment-submit:after {
	content: '';
	z-index: 0;
	position: absolute;
	top: 0;
	bottom: 0;
	left: 0;
	right: 0;
	background-color: hsl(340, 85%, 66%);
	transform: scalex(0);
	transform-origin: right center;
	transition: transform .25s ease-in-out;
}

.bb-comment .comment-send .comment-submit:hover:after {
	transform-origin: left center;
	transform: scalex(1);
}

