css:
@-moz-document url-prefix("https://www.watch2gether.com/rooms/"), url-prefix("https://w2g.tv/rooms/") {
.w2g-topbar {
    min-height: 42px;
}
.w2g-player-search {
    min-width: calc(728px + 1.5em);
    max-width: calc((100vh) * 1.77);
    max-height: 100%;
    margin-top: 0;
    margin-right: 0;
    flex-grow: 1;
    display: flex;
    flex-direction: column;
    position: relative;
}
.w2g-main-lower {
    flex-grow: 1;
    display: flex;
    justify-content: center;
    width: 100%;
    padding: 0 0 0 0;
    margin: auto;
    overflow: hidden;
}
.w2g-video-container {
    max-height: calc(100vh - 80px);
}
.w2g-panel-menu {
    display: none;
    position: absolute;
    bottom: 100%;
    background-color: #d2d2d2;
    color: #000;
    padding: 0.2em 0.2em 0.5em 0.5em;
    right: 2%;
    text-align: center;
    cursor: pointer;
    border-top-right-radius: 6px;
    border-top-left-radius: 6px;
    box-shadow: 0 0 4px rgba(0,0,0,0.6);
}
.w2g-main-right {
    width: 160;
    max-width: calc(260px + 1.5em);
    min-width: 0;
    padding-top: 0;
    margin-top: 0;
    margin-left: 0;
    margin-right: 0;
}
.w2g-chat.w2g-panel-bottom {
    display: flex !important;
    flex-direction: column;
    background-color: #f0f0f0;
    overflow: unset;
    right: 0;
    left: 0;
    padding: 0.75em;
    margin: auto;
    height: calc(100vh - 350px);
    max-height: 600px;
    border-top-right-radius: 0;
    border-top-left-radius: 0;
}
}
