css:
@-moz-document domain("drawception.com") {

}

@-moz-document domain("drawception.com") {
.text-muted
  {
    color: var(--text-color-muted) !important;
  }

  :root
  {
    --bg-accent-color: #eab618 !important;
    --bg-accent-highlight-color: #f2f2f2 !important;
    --bg-color: #ffffff !important;
    --bg-hover-color: #f6de97 !important;
    --bg-disabled-color: #b58800 !important;
    --bg-highlight-color: #7a5c00 !important;
    --bg-duck-option: #7a5c00 !important;
    --bg-duck-option-hover: #7a5c00 !important;
    --text-color: #000000 !important;
    --text-hover-color: #7a5c00 !important;
    --text-color-lighter: #f6de97 !important;
    --text-color-darker: #000000 !important;
    --text-color-muted: #b58800 !important;
    --border-color: #7a5c00 !important;
    --border-highlight-color: #7a5c00 !important;
    --link-color: #f6de97 !important;
    --link-color-hover: #b58800 !important;
    --comment-highlight-color: #fffdc9 !important;
    --store-btn-bg-color: #b58800 !important;
    --store-btn-bg-hover-color: #7a5c00 !important;
    --store-btn-text-color: #f6de97 !important;
    --modal-overlay-color: rgba(184,165,147,0.2) !important;
    --colorpicker-border-color: rgba(0,0,0,0.25) !important;
    --popup-border-color: #7a5c00 !important;
    --navbar-bg-color: #b58800 !important;
    --navbar-border-color: #7a5c00 !important;
    --navbar-btn-text-color: #f6de97 !important;
    --navbar-btn-text-hover-color: #f6de97 !important;
    --navbar-btn-border-color: #7a5c00 !important;
    --navbar-btn-border-hover-color: #7a5c00 !important;
    --navbar-btn-text-shadow: 0px 0px 3px #eab618 !important;
    --progress-bar-bg-color: #7a5c00 !important;
  }

  .text-danger
  {
    color: var(--link-color-hover) !important;
  }

  .btn-primary
  {
    background-color: var(--navbar-btn-text-hover-color) !important;
  }

  a:focus, a:hover
  {
    color: var(--link-color-hover) !important;
    text-decoration: underline !important;
  }

  .label-warning
  {
    background-color: var(--border-color) !important;
  }

  .progressbar-bg
  {
    background-color: var(--navbar-btn-border-color) !important;
  }
}
