css:
@-moz-document domain("5minutdlazdrowia.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #ff5b5a #1c1c1c;
}
/*HEADER*/
.main-navigation, .main-navigation ul ul,.navigation-search.nav-search-active,.main-navigation .main-nav ul li.sfHover > a{
    background: #1c1c1c;
}
.navigation-branding img, .site-logo.mobile-header-logo img{
    background: #fff;
    border-left: 4px solid #ff5b5a;
    border-right: 4px solid #ff5b5a;
}
.main-navigation .main-nav ul li a,.slideout-navigation.do-overlay .slideout-exit,.main-navigation .main-nav ul ul li a{
    color: #ff5b5a;
}
.main-navigation .main-nav ul li:hover > a,.main-navigation .main-nav ul ul li:hover a,.slideout-navigation.do-overlay .slideout-exit:hover{
    background: #ff5b5a;
    color: #1c1c1c;
}
nav.main-navigation .main-nav ul ul li img{
    filter: invert();
}
input[type="search"]{
    background: #1c1c1c !important;
}
.slideout-navigation.main-navigation .main-nav ul li:hover > a,.slideout-navigation.main-navigation .main-nav ul ul li:hover > a{
    color: #1c1c1c;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
body.home{
    background: #1c1c1c;
}
.home h2.section-title a, .home .section-title h2 a,#category-section .pis-li a,.home main#main h2,.section-title i,section#popularne-teraz li a,.author-name{
    color: #ff5b5a;
}
#category-section .pis-li,section#popularne-teraz li{
    border: 1px solid #ff5b5a;
    background: #1c1c1c;
}
#category-section .pis-li:hover a,section#popularne-teraz li:hover a,.entry-meta{
    color: #fff;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.main-navigation .main-nav ul li[class*="current-menu-"] > a,.main-navigation .main-nav ul ul li[class*="current-menu-"] > a{
    background: #ff5b5a;
    color: #1c1c1c;
}
.main-navigation .main-nav ul li[class*="current-menu-"] > a:hover,.main-navigation .main-nav ul ul li[class*="current-menu-"] > a:hover{
    background: #ff5b5a;
    color: #fff;
}
.single span.categories-links a, .single span.series-links a,.sidebar .widget .widget-title,footer.entry-meta span.tags-links, footer.entry-meta span.tags-links a,aside.posts-in-sidebar h3 a, .single header .entry-meta > span a{
    color: #ff5b5a;
}
.photo-sources,.text-sources,.breadcrumb_last,aside.posts-in-sidebar .pis-li:hover h3 a,.single header .entry-meta > span time.published{
    color: #fff;
}
aside.posts-in-sidebar li{
    border: 1px solid #ff5b5a;
    background: #1c1c1c;
}
/*ARTYKUŁ*/
/*PODSTRONY*/
/*AUTOR*/
.author header.page-header{
    background: #ff5b5a;
}
.vcard{
    color: #1c1c1c;
}
.inside-article{
    background: #1c1c1c;
}
.entry-title a{
    color: #ff5b5a !important;
}
.entry-title:hover a{
    color: #fff !important;
}
/*AUTOR*/
/*PODSTRONY*/
/*FOOTER*/
input[type="email"],.newsletter-form .newsletter-footer p,.site-info{
    color: #fff;
}
.newsletter-form .newsletter-footer{
    opacity: 1;
}
.footer-widgets .widget-title{
    color: #ff5b5a;
}
.copyright-bar:before{
    content: "Ciemny motyw by Maciejozaman";
    color: #ff5b5a;
}
/*FOOTER*/
}
