.o-availability__value.o-availability__value--stock_9:hover {
    color: #f46036;
}
.dropdown .list .drop {
    border: 1px solid #111;
    background: #000;
}
.c-products__compare {
    background-color: #222;
}
.c-products__compare:before {
    background: -webkit-gradient(linear, right top, left top, from(hsla(0, 0%, 100%, 0)), to(#fff0));
    background: linear-gradient(270deg, hsla(0, 0%, 100%, 0) 0, #0000);
}
.owl-carousel .owl-item img {
    filter: brightness(0.8);
}
.owl-carousel .owl-item img.owl-lazy {
    filter: brightness(0.8);
}
.b-lazy.b-error,
.b-lazy.b-loaded {
    filter: brightness(0.8);
}
.c-products-list.c-products-list__row .c-products-list__item .c-product__thumb .o-checkbox {
    filter: brightness(0.8);
}
.c-product__list-reviews .c-product__review .o-review__author {
    background-color: #222;
}
.c-autocomplete {
    background-color: #222;
    border: 1px solid #f46036;
}
.c-autocomplete:before {
    background-color: #222;
    border-left: 1px solid #f46036;
    border-top: 1px solid #f46036;
}
.c-autocomplete .c-autocomplete__container a {
    color: #f46036;
}
.bg-light {
    background-color: #000000!important;
}
.c-collapse a.c-collapse__title {
    border-bottom: 1px solid #f46036;
    border-top: 1px solid #f46036;
}
.c-collapse.js-is-opened a.c-collapse__title {
    background-color: #111111;
}}
