css:
@-moz-document domain("zdroweplecy.net") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #3cc187 #1c1c1c;
}
/*HEADER*/
.main-navigation, .main-navigation ul ul,.navigation-search.nav-search-active{
    background: #1c1c1c;
}
.navigation-branding img, .site-logo.mobile-header-logo img{
    background: #fff;
    border-left: 3px solid #3cc187;
    border-right: 3px solid #3cc187;
}
.main-navigation .main-nav ul li a,.main-navigation .main-nav ul ul li a{
    color: #3cc187;
}
.main-navigation .main-nav ul li:hover > a,.main-navigation .main-nav ul ul li:hover a,.main-navigation .main-nav ul li.sfHover > a{
    background: #3cc187;
    color: #1c1c1c;
}
input[type="search"],input[type="search"]:active{
    background: #1c1c1c !important;
}
.sf-menu > li.slideout-toggle{
    display: none;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
body.home,section#popularne-teraz li,#category-section .pis-li{
    background: #1c1c1c;
}
.home .section-title h2 a,.elementor-252268 .elementor-element.elementor-element-33374892.elementor-widget-heading .elementor-heading-title,h3.pis-title a,p.pis-utility a,.home .section-title h2,.author-name{
    color: #3cc187;
}
.pis-li:hover a,p.pis-categories-links a,.entry-meta{
    color: #fff;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
p#breadcrumbs,.single header .entry-meta > span time.published,.inside-article a:hover,.post-sources, .photo-examples{
    color: #fff;
}
.single header .entry-meta > span a,.single span.categories-links a,.sidebar .widget .widget-title,.inside-article a,footer.entry-meta span.tags-links a,aside.posts-in-sidebar h3 a{
    color: #3cc187;
}
.main-navigation .main-nav ul li[class*="current-menu-"] > a{
    background: #3cc187;
    color: #1c1c1c;
    border-left: 3px solid #1c1c1c;
    border-right: 3px solid #1c1c1c;
}
.main-navigation .main-nav ul li[class*="current-menu-"] > a:hover{
    background: #1c1c1c;
    border-left: 3px solid #3cc187;
    border-right: 3px solid #3cc187;
    color: #3cc187;
}
aside.posts-in-sidebar li,.taxonomy-description{
    background: #1c1c1c;
}
/*ARTYKUŁ*/
/*PODSTRONY*/
.inside-article{
    background: #1c1c1c;
}
/*PROFIL-AUTORA*/
.author header.page-header{
    background: #3cc187;
}
.archive h1.page-title,.author .page-header .author-info{
    color: #1c1c1c;
}
/*PROFIL-AUTORA*/
/*PODSTRONY*/
/*FOOTER*/
.newsletter-form input[type="email"],.site-info{
    color: #fff;
}
.newsletter-form .newsletter-footer{
    opacity: 1;
}
.copyright-bar:before{
    content: "Ciemny motyw by Maciejozaman";
    color: #3cc187;
}
/*FOOTER*/
}
