css:
@-moz-document domain("forum.projectnine.de") {
/*  =====================================
    Projectnine Dark Theme
    by Nico Wolli
    https://userstyles.org/styles/179721
    Last updated: v1.4: 04/03/2020
    =====================================   */





/*// Dashboard Seite /////////////////////////////*/
/*// Body Background //*/

#content{background-color: rgba(10, 10, 10, .89);}

.boxesFooterBoxes > .layoutBoundary, .pageSidebarMenu,.boxesSidebarLeft .box:not(.boxBorderless),.boxesSidebarRight .box:not(.boxBorderless), #pageHeaderContent > div,#pageHeaderFader > div, #pageFooter > div {
	background-image: linear-gradient(rgba(255, 255, 255, 0.08), rgba(255, 255, 255, 0.08));
	box-shadow: 0 1px 0 rgba(255, 255, 255, 0.05) inset, 0 -1px 0 rgba(255, 255, 255, 0.054) inset;}
#pageHeaderContent > div, #pageHeaderFader > div, #pageHeaderPanel > div, .pageSidebarMenu,.boxesSidebarLeft .box:not(.boxBorderless),.boxesSidebarRight .box:not(.boxBorderless) {
	background-color: rgba(10, 10, 10, .89);}

.containerList > li:hover {background-color: rgb(66, 62, 62);}

.containerList > li:nth-child(2n) {background-color: rgba(36, 34, 34, .89);}

.containerList > li {background-color: rgba(36, 34, 34, .89);}

.containerList > li:not(:last-child) {border-bottom: 1px solid rgba(0, 0, 0, 1);}

.containerList > li:first-child {border-top: 1px solid rgba(0, 0, 0, 1);}

.containerList > li:last-child {border-bottom: 0px solid rgba(210, 210, 210, 1);}

body {color: rgba(85, 85, 85, 1);}

.containerList > li.containerListButtonGroup:hover {background-color: rgba(36, 34, 34, 1);}


/*// Chatbox //*/
/*// Background //*/
.shoutbox.modernDesign {background-color: rgb(32, 32, 32);}

/*// Inline Boxen //*/
.shoutbox.modernDesign .shoutboxEntry .shoutboxMessage {
	background: rgb(20, 22, 32);
	color: rgb(200, 199, 199);}

.shoutbox.modernDesign .shoutboxEntry.isOwner .shoutboxMessage {
	background: rgb(44, 64, 31);
	color: rgba(200, 199, 199, 1);}


/*// Textfarbe (Nicht Links !) //*/
body {color: rgb(206, 206, 206);}

.boxesSidebarLeft .boxTitle, .boxesSidebarRight .boxTitle {color: rgb(168, 37, 37);}

.boxesFooterBoxes > .layoutBoundary, .pageSidebarMenu, .boxesSidebarLeft .box:not(.boxBorderless), .boxesSidebarRight .box:not(.boxBorderless), #pageHeaderContent > div, #pageHeaderFader > div, #pageFooter > div {
	background-image: linear-gradient(rgba(255, 255, 255, 0.01), rgba(255, 255, 255, 0.01));
	box-shadow: 0 1px 0 rgba(255, 255, 255, 0.05) inset, 0 -1px 0 rgb(0, 0, 0) inset;}


/*// Forum Seite /////////////////////////////*/
/*// Body Background //*/
.wbbBoardList .wbbBoard:hover {background-color: rgba(36, 34, 34, 1);}
.clanCategoryList .clanCategoryList:hover {
background-color: rgba(236, 236, 236, 0);
}

body {color: rgba(85, 85, 85, 1);}

.wbbBoardList > li, .wbbCategory.wbbDepth1 > ul > li {background-color: rgba(36, 34, 34, 1);}

.wbbBoardList > li:nth-child(2n), .wbbCategory.wbbDepth1 > ul > li:nth-child(2n) {background-color: rgba(36, 34, 34, 1);}

.wbbBoardList .wbbBoardContainer {border-bottom: 1px solid rgba(0, 0, 0, 1);}

.wbbLastPost {background-color: rgb(51, 50, 50);}

.wbbBoardList .wbbBoardDescription {color: rgb(255, 255, 255);}

body {color: rgb(255, 255, 255);}

.sidebar {background-color: rgba(10, 10, 10, .89);}

.tabularList > li:nth-child(2n) {background-color: rgb(36, 34, 34);}

.tabularList > li {background-color: rgba(36, 34, 34, 1);}

.tabularListRow:not(.tabularListRowHead) {border-top: 1px solid rgb(0, 0, 0);}

.tabularList {border-bottom: 1px solid rgb(0, 0, 0);}

.messageGroupList .tabularList .tabularListRow:not(.tabularListRowHead) .columnStats > dl {visibility: visible;}

.messageGroupListStatsSimple {color: rgba(119, 119, 119, 0);}

.tabularListRow:not(.tabularListRowHead):hover {background-color: rgba(36, 34, 34, 1);}



.messageList:not(.messageReducedList) > li .message {background-color: rgba(36, 34, 34, 1);}

.messageList:not(.messageReducedList) > li .message {border-top: 1px solid rgb(0, 0, 0);}

.messageList:not(.messageReducedList) > li {border-bottom: 1px solid rgb(0, 0, 0);}

.embeddedAttachmentLink, .embeddedImageLink {border: 1px solid rgb(0, 0, 0);}

.messageList:not(.messageReducedList) > li .message > .messageSidebar + .messageContent {
	background-color: rgba(36, 34, 34, 1);
	border-left: 2px solid rgba(0, 0, 0, 1);}

.userAvatarImage {background-color: rgba(36, 34, 34, 1);}

.messageSidebar {
	background-color: rgb(17, 17, 17);
	color: rgb(115, 113, 113);}

Element {color: rgba(130, 130, 130, 0);}

#profileContent .tabMenuContent > .section {
	background-color: rgba(36, 34, 34, 1);
	border-bottom: 1px solid rgba(36, 34, 34, 1);}

.htmlContent table tr:hover > td, .messageBody > .messageText table tr:hover > td, .messageSignature > div table tr:hover > td, .redactor-layer table tr:hover > td, .table tr:hover > td {background-color: rgba(36, 34, 34, 1);}

.redactor-box, .messageTabMenuNavigation > ul, .messageTabMenu > .messageTabMenuContent.active, .messageContent.loading > .messageContentLoadingOverlay {background-color: rgb(49, 48, 48);}

.redactor-layer {border: 0px solid rgba(210, 210, 210, 1);}

.messageTabMenuNavigation > ul {border: 0px solid rgba(210, 210, 210, 1);}

.messageTabMenuNavigation > ul > li {border-right: 0px solid rgba(210, 210, 210, 1);}

.messageTabMenu:not(.messageTabMenuContent) > .messageTabMenuContent.active {border: 0px solid rgba(210, 210, 210, 1);}

.messageTabMenuNavigation > ul > li.active > a::after {border-bottom: 1px solid rgb(0, 0, 0);}

.messageTabMenuNavigation > ul > li.active > a {color: rgb(255, 255, 255);}

.inputItemList {
	background-color: rgb(130, 130, 130);
	border: 1px solid rgba(204, 204, 204, 1);
	color: rgb(255, 255, 255);}

.inputItemList:focus, .inputItemList:hover {
	background-color: rgb(150,150,150);
	border-color: rgba(54, 114, 189, 1);}
input[type="date"], input[type="datetime"], input[type="email"], input[type="number"], input[type="password"], input[type="search"], input[type="tel"], input[type="text"], input[type="url"], select, textarea {
	background-color: rgb(130, 130, 130);
	border: 1px solid rgba(204, 204, 204, 1);
    color: rgba(255, 255, 255, 1);}

dl:not(.plain) > dt {
	color: rgb(150, 150, 150);}

.messageTableOverflow table thead tr, .messageTableOverflow table tr:nth-child(2n) {
	background-color: rgba(230, 230, 230, 0);}

/* Update 01.02.2020 / Bugfix */
woltlab-quote, .quoteBox {
	background-color: rgb(19, 18, 18);
	box-shadow: 0 0 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24);}
.dialogContainer > .dialogContent {background-color: rgba(19, 18, 18, 1);}
input[type="date"], input[type="datetime"], input[type="email"], input[type="number"], input[type="password"], input:not(#tagSearchInput):not(#tag)[type="text"], input[type="url"], select, textarea, .inputItemList, input[type="date"]:focus, input[type="datetime"]:focus, input[type="email"]:focus, input[type="number"]:focus, input[type="password"]:focus, input:not(#tagSearchInput):not(#tag)[type="text"]:focus, input[type="url"]:focus, select:focus, textarea:focus, input[type="date"]:hover, input[type="datetime"]:hover, input[type="email"]:hover, input[type="number"]:hover, input[type="password"]:hover, input:not(#tagSearchInput):not(#tag)[type="text"]:hover, input[type="url"]:hover, select:hover, textarea:hover, .inputItemList:hover {
	background-color: rgb(0, 0, 0);}

.boxesSidebarLeft a:hover, .boxesSidebarRight a:hover {color: rgb(255, 255, 255);}

a:hover {color: rgb(255, 255, 255);}

.boxesSidebarLeft .box .boxMenu li.active > .boxMenuLink, .boxesSidebarRight .box .boxMenu li.active > .boxMenuLink {background-color: rgb(36, 34, 34);}

/*// Update 01.03.2020 //*/
/*// Fix Zitat Box //*/

.quoteBox > .toggleButton {
background-color: rgb(62, 57, 57);}
    
.quoteBox.collapsed > .toggleButton {
box-shadow: 0 -10px 50px 10px rgb(62,57,57);}

/*// Update 04.03.2020 //*/
/*// Fix Erwähnungen //*/

.userMention {
background-color: rgba(0, 0, 0, 0);
padding: 1px 0px;}

.userMention:hover {
color: rgb(255, 255, 255);}

}
