/*
    * Name:    CleanTube v2
    * Author:  Sardorbek Usmonov
    * Version: 2.0.0
*/

@media (prefers-color-scheme: dark), (prefers-color-scheme: no-preference) {
/* ============ COLOR PALETTE ============ */
:root {
    --blue-day: #306AF2;
    --blue-night: #4789E5;
    --red-day: #F8333C;
    --red-night: #ee353e;
    --green-day: #28B75C;
    --green-night: #20944B;
    /* Day */
    --day-darker: #7d7d7d;
    --day-dark: #B7B7B7;
    --day-light: #DDDDDD;
    --day-lighter: #EAEAEA;
    --day-lightest: #FFFFFF;
    /* Night */
    --night-darkest: #0F0F0F;
    --night-darker: #262626;
    --night-dark: #424242;
    --night--light: #6B6B6B;
    --night-lighter: #B2B2B2;
}
/* Cream White Theme */
html:not([style-scope]):not(.style-scope) {
    --yt-spec-brand-background-primary: var(--day-lightest) !important;
}
ytd-masthead, app-toolbar.ytd-c4-tabbed-header-renderer {
    box-shadow: var(--masterhead-shadow) !important;
}


/* Night */
html:not([style-scope]):not(.style-scope), :not([style-scope]):not(.style-scope) {
    /* CUSTOM */
