@-moz-document domain("egybest.com") {
.msg_box.error {
    background: #f98a8a;
}

.mbox {
    background: #171516;
}

.mbox .hd {
    background: #041419;
}

body {
    direction: rtl;
    margin: auto;
    max-width: 1320px;
    background: #0c0c0c;
    font-size: 0.9em;
    color: #222;
}

#body {
    position: relative;
    margin: 70px 0 10px 0;
    box-sizing: border-box;
}


#head {
    z-index: 30;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    margin: 0;
    background: #3e3e3e;
    border: none;
    box-shadow: 1px 0 2px 1px #ddd;
    box-sizing: border-box;
    color: #0066CC;
    font-size: 1em;
}

a {
    color: #bdbdbd;
}

strong {
    color: white;
}

.ba a {
    background: #3e3e3e;
    color: #cecece;
}

.ba a:hover {
    background: #a5a5a5;
}


.pda {
    color: #d8d8d8;
}

.movieTable td {
    background: #252525;
    border: 1px solid #7f7e7e;
    color: #bdbdbd;
}

.bdb {
    border-bottom: 1px solid #7f7e7e;
}


.dls_table tr:nth-child(even) td,
.dls_table th {
    background: #808080;
}

.dls_table {
    background: #9e9e9e;
}

.dls_table tr th {
    color: white;
}

.btn.g,
.btn._g:hover {
    background: #616161;
    background-color: #616161;
    border-color: #000000;
}

.btn.g:hover,
.btn.g:active {
    background: #000000;
}

a.btn.g.dl {
    background: #272727;
}

.tam.pdb {
    color: white;
}

.viewport_left {
    color: white;
}

a:hover {
    color: white;
}

.cast_item {
    color: white;
}
}

