css:
@-moz-document url("https://canno.lemonray.monster/user/node") {
.col-lg-6 {
	-ms-flex: 0 0 90%;
	flex: 0 0 90%;
	max-width: 90%;
}
.media img {
	border: 1px solid #969696;
	width: 34px;
}
.card .card-body {
	padding-top: 3px;
	padding-bottom: 3px;
}
.card {
	box-shadow: 0 4px 8px rgba(56, 55, 55, .08);
	margin-bottom: 7px;
	transition: 0.3s;
}
.card:hover {
	border: 2px solid black;
	box-shadow: 0 2px 14px rgba(103, 129, 243, .52);
	padding-top: 8px;
	padding-bottom: 8px;
}
}
