css:
@-moz-document domain("latopic.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #ed488e #1c1c1c;
}
/*HEADER*/
.site-header.style9.headerstyle--default{
    background: #1c1c1c;
}
.main-nav > ul > li:hover{
    background: #1c1c1c;
}
.main-nav.mainnav--active-bg > ul > li > a::before,.main-nav.mainnav--active-bg > ul > li > a::after{
    background: #04a6e9;
}
.sh--dark .main-nav > ul > li > a{
    color: #04a6e9;
}
.sh--dark .headsearch--min .gensearch__input{
    color: #fff;
}
.sh--dark .headsearch--min .header-search-container{
    border-color: #04a6e9;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.vc_custom_1537803918019{
    background: #1c1c1c !important;
}
.wppm-grid .entry-title a{
    color: #04a6e9;
}
.sa_hover_container{
    background: transparent !important;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ-PRODUKT*/
.kl-single-layout--modern .kl-blog-post{
    background: #1c1c1c;
}
.vc_custom_1536135721885,.vc_custom_1537802193304,.vc_custom_1537858239496,.vc_custom_1537858138983{
    background: #1c1c1c !important;
}
.skladniki h3{
    color: #04a6e9 !important;
}
/*ARTYKUŁ-PRODUKT*/
/*PODSTRONY*/
/*KUP-TERAZ*/
.vc_btn3.vc_btn3-color-grey.vc_btn3-style-modern{
    background: #04a6e9;
    border-color: #04a6e9;
    color: #1c1c1c;
}
.vc_btn3.vc_btn3-color-grey.vc_btn3-style-modern:hover{
    background: #1c1c1c;
}
/*KUP-TERAZ*/
/*SZUKAJ*/
.gensearch__input{
    background: #1c1c1c;
    color: #fff;
    border-color: #04a6e9;
}
.glyphicon{
    position: absolute;
}
.gensearch__submit::after{
    background: #04a6e9;
}
.glyphicon-search::before{
    color: #04a6e9;
}
.kl-blog-item-container{
    background: #1c1c1c;
    box-shadow: none;
}
.kl-blog-item-overlay{
    background-color: rgba(28, 28, 28, 0.5);
}
.pagination-item-active-span{
    border-color: #04a6e9;
    color: #04a6e9;
}
.pagination-item-link, .pagination-item-span{
    color: #04a6e9;
}
.pagination-item-next,.pagination-item-prev{
    border: 1px solid #1c1c1c;
}
.pagination-item-next:hover, .pagination-item-prev:hover{
    border: 1px solid #04a6e9;
}
.pagination-item-active-span:hover{
    background: #04a6e9;
    color: #1c1c1c;
}
.pagination-item-next:hover .zn_icon, .pagination-item-prev:hover .zn_icon{
    color: #04a6e9;
}
.kl-blog-item-details{
    color: #fff;
}
/*SZUKAJ*/
/*KONTAKT*/
.wpcf7-form-control:not(.wpcf7-checkbox):not(.wpcf7-radio){
    background: #1c1c1c;
    color: #fff;
    border-color: #04a6e9;
}
/*KONTAKT*/
/*PODSTRONY*/
/*FOOTER*/
.site-footer{
    background: #1c1c1c;
}
.nawigacjadolna{
    border-color: #04a6e9;
}
.widget_nav_menu ul.menu li a{
    color: #04a6e9 !important;
}
#text-2 p{
    color: #fff !important;
}
#text-2 p:before{
    content: "Ciemny motyw by Maciejozaman | ";
    color: #04a6e9;
    cursor:not-allowed;
}
/*FOOTER*/
}
