css:
@-moz-document url-prefix("https://sklep.skript.pl/") {
body {
    background-color: #222;
    background: url("https://code.skript.pl/api/background/night");
    color: #fff;
}
.bg-light {
    background-color: #212121!important;
}
.card {
    background-color: #212121!important;
    color: #fff
}
.navbar-light .navbar-nav .nav-link{
    color: #fff !important;
}
.navbar-brand {
	color: #fff !important;
}
a {
    color: #3cd2ff;
}    
a:hover {
    color: #17bbec;
}    
.table td, .table th {
   border-top: 1px solid #252525
}

.text-muted {
    color: #a9e7ff !important;
}    
.modal-content {
    background-color: #212121
}
.modal-footer {
 	border-top: 1px solid #737373;   
}
.alert-danger {
    color: #ffffff;
    background-color: #821923;
    border-color: #000000;
}
.input-group-text {
    color: #ffffff;;
    background-color: #3a3a3a;
    border: 1px solid #404040;
}
.form-control {
    color: #fff;
    background-color: #3a3a3a!important;
    border: 1px solid #404040;
}
.form-control:focus {
    color: #fff;
    background-color: #3a3a3a!important;
    border-color: #404040;
}
table.items tr:not(:first-child):hover, a.card-footer:hover {
    background: #171717;
}
}
