css:
@-moz-document domain("facebook.com") {
/* i really want this to be global */

#facebook .system-fonts--body
  {
    font-family: Roboto Condensed, Segoe UI, Helvetica Neue, Helvetica, Tahoma, Arial, sans-serif, SimSun, "Malgun Gothic", "Tai Lanna", "Cambria Tai Yo", "Cambria ASEAN", "Aksara Bali", Yogyakarta, "Tai Son La", TaiViet, HanaMinA, HanaMinB, FSung-3, Sawndip, SimSun-ExtB, "Noto Serif Ahom", 'Nepal Lipi Unicode' !important;
  }

}

@-moz-document domain("wiktionary.org"), domain("wikipedia.org") {
 .Lana {
     font-family:'Tai Lanna', sans-serif !important;
 }
 .Bali {
     font-family:'Aksara bali', 'Noto Sans Balinese', sans-serif !important;
 }
 .Java {
     font-family:'Yogyakarta', 'Tuladha Jejeg', 'Javanese Text', adjisaka, 'Noto Sans Javanese', sans-serif !important;
 }
 .Ahom {
     font-family:'Segoe Ahom Print', AhomUnicode, 'Noto Serif Ahom', 'Unifont Upper', sans-serif !important;
 }
}
