css:
@-moz-document url-prefix("https://boards.4chan"), url-prefix("https://boards.4channel"){
/*__                         __          
 |  |_.--.--.--.-----.---.-.|  |--.-----.
 |   _|  |  |  |  -__|  _  ||    <|__ --|
 |____|________|_____|___._||__|__|_____|
*/

/*center*/
.thread {
    width: 64vw !important;
    margin-left: auto !important;
    margin-right: auto !important;
  }

  .post.reply  {
    width: 100%;
  }

/*polish*/
input[type="checkbox"],
#blotter,
.page-num,
.sideArrows{
    display: none;
}

.brackets-wrap::before,
.brackets-wrap::after {
    content: "" !important;
}

#scroll-marker {
    opacity: 0;
    box-shadow: none !important;
}

.post.reply {
    border: 0;
    margin-top: 10px;
    margin-bottom: 10px;
    box-shadow: 0 0 35px var(--onealt);
    padding: 15px;
}

hr {
    opacity: 0;
}

#header-bar{
    border: 0;
    opacity: .86;
    transition: opacity .3s;
}

#header-bar:hover {
    opacity: 1;
    transition: opacity;
}

/*           __                  
 .----.-----|  .-----.----.-----.
 |  __|  _  |  |  _  |   _|__ --|
 |____|_____|__|_____|__| |_____|
*/

/*variables*/
:root {
--bg: #f8f7ff;
--one: #4a436d;
--onealt: #4a436d14;
--two: #426d66;
--three: #fff7fd;
--four: #f7fdff;
--five: #fcfcff;
--quote: #577f87;
}  

/*bg*/
.boardBanner,
.boardTitle,
body,
#qr,
#watched-threads,
#header-bar,
.post.reply,
.pagelist {
    background-color: var(--bg) !important;
    background-repeat: no-repeat;
}

/*color one*/
a,
a:visited,
#header-bar,
#thread-stats,
#updater,
#custom-board-list>a,
#custom-board-list,
.menu-button,
.notification.success,
.notification.info,
.notification.warning,
.notification.error,
.boardTitle,
.name,
.postNum>a,
.file-info,
.file-info>a, 
.fileText,
.summary,
.postNum>span,
.backlink,
.subject,
.quotelink,
a.replylink:not(:hover), 
div#absbot a:not(:hover),
.nameBlock.capcodeMod span.name,
span.capcodeMod a span.name,
span.capcodeMod span.postertrip,
span.capcodeMod strong.capcode,
.postMessage>a,
.linkify,
.postertrip,
#absbot,
.stub>a,
.embedder,
.pages>a,
.dateTime,
.video-controls>a,
.closed,
.new,
h4,
h2,
#arc-list .quotelink,
tbody,
#copyright,
header,
h3,
.content h3,
.box-content,
.boxcontent>ul,
p,
.content,
.boxcontent,
.postMessage,
.inline .post,
#menu,
.submenu,
#fourchanx-settings,
#announce,
#disclaimer-dialog,
#disclaimer-dialog .hd {
    color: var(--one) !important;
}

/*color two*/
a:hover,
.file-info>a:hover,
a.replylink,
a.linkify:hover,
a.quotelink:hover,
#custom-board-list>a:hover,
div.post div.postInfo span.postNum a:hover,
.posteruid .hand:hover,
.fa:hover,
#file-n-submit > input:hover,
#filter-btn:hover,
.menu-button:not(.fa-bars):hover,
#menu>span:hover,
.dialog.submenu.focused>label:hover,
.dialog.submenu>label:hover,
a.embedder:hover {
    color: var(--two) !important;
}

/*other colors*/
span.quote {
    color: var(--quote) !important;
}

:root.highlight-own .yourPost.opContainer,
:root.highlight-own .yourPost > .reply,
:root.yotsuba-b.highlight-own .yourPost > .reply {
    border: 0 !important;
    background-color: var(--three) !important;
}

:root.highlight-you .quotesYou.opContainer, :root.highlight-you .quotesYou > .reply,
:root.yotsuba-b.highlight-you .quotesYou > .reply {
    border-left: 0 !important;
    background-color: var(--four) !important;
}

.filter-highlight,
.filter-highlight > .reply {
    border-left: 0 !important;
    background-color: var(--five) !important;
    box-shadow: none !important;
}

.qphl {
    outline: 2px solid var(--one) !important;
}
}
