/* 16x16 Disabled */
#stylish-panel[styles-applied=""],
toolbar[iconsize="small"] #stylish-toolbar-button > *,
#styles-container [is-disabled="true"] [anonid="status-icon"] {
	list-style-image:url("data:image/png;base64,") !important;
}

/* 16x16 Enabled */
#stylish-panel,
toolbar[iconsize="small"] #stylish-toolbar-button[styles-applied~="site"] > *,
#styles-container [anonid="status-icon"] {
	list-style-image:url("data:image/png;base64,") !important;
}

/* 24x24 */
#stylish-toolbar-button {
	list-style-image:url("data:image/png;base64,") !important;
}

/* 32x32 */
#addons-page #category-userstyle .category-icon,
#viewGroup #userstyles-view {
	list-style-image:url("data:image/png;base64,") !important;
}

/* Add-ons Manager interface icons */
#addons-page .addon-view[type="userstyle"] .icon,
#addons-page .addon-view[value="{46551EC9-40F0-4e47-8E18-8E5CF550CFB8}"] .icon {
	width:0;
	height:0;
	padding:32px 32px 0 0;
}

/* Stylish's own extension row */
#addons-page .addon-view[value="{46551EC9-40F0-4e47-8E18-8E5CF550CFB8}"] .icon {
	background:url("data:image/png;base64,") !important;
}

/* 32x32 Enabled */
#addons-page .addon-view[type="userstyle"][active="true"] .icon {
	background:url("data:image/png;base64,") !important;
}

/* 32x32 Disabled */
#addons-page .addon-view[type="userstyle"][active="false"] .icon {
	background:url("data:image/png;base64,") !important;
}
