@-moz-document url("https://wfa.rcn.net"), url-prefix("https://wfa.rcn.net"), url-prefix("http://wfa.rcn.net"), domain("wfa.rcn.net"), domain("rcn.net") {
/*

Login Page UI

*/
/* i really want this to be global */
body {

    background: #000;
    background-size: cover !important;
    background-repeat: no-repeat !important;
}

#security-login-container {
/*main login back color*/
    background-color: #fff;
    background-image: url("https://residential.wavebroadband.com/wp-content/uploads/sites/6/2019/01/wavebluelogo.png");
    background-repeat: no-repeat;
    background-position: center 75px;
    min-height: 100%;
    height: 100%;
    width: 100%;
    display: inline-block;
    font-size: 14px;
    font-family:"Open Sans", Verdana, Arial, Helvetica, sans-serif;
}

#security-login-box-content {
    display: inline-block;
    margin: 1px 0;
    width: 100%;
}

#security-login-title-container {

    margin: 1px 3px 3px;
    font-weight: 400;
    font-size: 2.25em;
    color: #0f3964;
    text-align: center;

}

/* Login box container */
#security-login-box-container {

    width:310px;
    height: 210px;
    border-radius: 1px;
    background-color: #fff;
    background-repeat: no-repeat;
    background-position: center center;
    background-size: 260px;
    box-shadow: 0px 0px 0px #0e355e;
    margin: 16x auto;

}


.mobile-bg {
  
    background-image: url("https://residential.wavebroadband.com/wp-content/uploads/sites/6/2019/01/wavebluelogo.png");

}

  
#signin-table {
    margin: 2px 0 0 2px;
    display: inline-block;
}

#signin-table td {
    padding: 5px;
}

#signin-table td:first-child {
    text-align: right;
}

#signin-table label {
    font-size: 1.1em;
    font-weight: 600;
   }


signin-error {
    color: #cd0000;
    float: right;
    margin-right: 20px;
}

#signin .signin-submit input {

    float: right;
    margin-right: 20px;
    margin-top: 10px;

}

.icon-lock {
	background: none;
}

#security-change-password-expiration-warning {
    padding-left: 16px;
    padding-bottom: 16px;
    color: red;
}

.security-change-password-label {
    display: inline-block;
    text-align: right;
    width: 140px;
}

.security-change-password-error-message {
    display: none;
    padding-left: 146px;
    font-size: 0.7em;
    color: red;
}

signin-error {
    color: #cd0000;
    float: right;
    margin-right: 20px;
}

#signin .signin-submit input {
    float: right;
