   color: red;
}

.security-change-password-label {
    display: inline-block;
    text-align: right;
    width: 140px;
}

.security-change-password-error-message {
    display: none;
    padding-left: 146px;
    font-size: 0.7em;
    color: red;
}

input[type=button], button, .button {
    padding: 6px 20px;
    border-radius: 0px;
    margin-right: 0px;
    font-family:'Open Sans', Verdana, Arial, Helvetica, sans-serif;
    font-size: 1.000em;
    font-weight: 700;
    color: #fff;
}

input.button-primary-action:disabled, button.button-primary-action:disabled, .primary-action:disabled, .primary-action-disabled {
    cursor: default;
    border: 1px solid #d1d7ef;
    text-shadow: -1px -1px #d1d7ef;
    color: #fff;
    background: none;
}

/*login button on main page */
input.button-primary-action, button.button-primary-action, .primary-action {
    border: 1px solid #000;
  	background: none; 
    color: #fff;
    background: #6fba41!important; 
   }

/* topnav status menu drop downbarstuff */
#top-navigation-technician-status-menu-container{
  position: absolute;
  top: 0;
    
    color: #fff;
    width: 120px;
	/* set height to fix scroll issue on some browers */
	height: 10px;
    padding: 0px;
   margin-left: 120px;
  background: none;
   }
   
   
   /* User status drop down*/
#top-navigation-selected-technician-status-container {
	position: absolute;
	z-index: 100;

	height: 25px;
	width: 60px;
     margin-left: 15px;
	vertical-align: middle;
	margin-top: 6px;
	
	background-color: #585858;
	
	border: solid 1px #808080;
	-webkit-border-radius: 3px;
	-moz-border-radius: 3px;
	border-radius: 3px;
}

   

/* Clock Timer */

#top-navigation-technician-status-time-container {
      position: absolute;
      top: 0;
      margin-top: 9px;
      margin-left: 50px;
      vertical-align: right;
}

/* color of time etc */
#top-navigation-technician-status-time {
 
   font-size: 1.3em;
   font-weight: bold;
   color: #00b8d4;

}
/* LEFT NAV STUFF */

/* Left nav container flex boxed to use all of screen height. */
#left-navigation-container {
display: -webkit-box;
display: -moz-box;
display: -ms-flexbox;
-webkit-box-orient: vertical;
-moz-box-orient: vertical;
-ms-flex-direction: column;
   flex-direction: column;
   -webkit-box-flex: 1;
    -moz-box-flex: 1;
    -ms-flex: 1;
    flex: 1;
   min-width: 170px;
   background-color:#00b8d4;
   background-image:url('https://upload.wikimedia.org/wikipedia/commons/c/ca/1x1.png');
   background-repeat:no-repeat;
  background-size: cover;
}

/* Left nav row. */
#left-navigation-container li {
padding-left: 5px;
padding-top: 5px;
padding-right:10px;
padding-bottom: 5px;
}

#left-navigation-container li.selected {
background-color: #fdfdfd;
}

/* Container for icon and text. */
 #left-navigation-container a {
text-decoration: none;
color: #fff;

width: 100%;
}

 #left-navigation-container li.selected a {
color:#000;
font-weight: bold;
 }

#left-navigation-container a:hover {

}

/* Left navigation text. */
#left-navigation-container span {
display: inline-block;
vertical-align: top;
margin-top: 10px;
margin-left: 5px;
font-size: 1.0em;
font-weight: bold;
}

#left-navigation-container li.selected span {
font-weight: bold;
}

/* Left navigation icon. */
#left-navigation-container label {
display: inline-block;
}

/* ALL ICONS IMAGE OVERIDE*/
.sprite {display:inline-block; overflow:hidden; background-repeat: no-repeat;background-image:url(https://i.ibb.co/BTvzTXD/icons-pc-wave.png);}
}

/*NEW*/
/* MAIN Page Header text style -----*/
.page-title {
    display: inline-block;
    vertical-align: top;
    font-size: 1.5em;
    font-weight: bold;
    color: #000;
    margin-top: 15px;
    margin-left: 5px;
}


/*list and list headers---- */

.rounded-container {
    display: block;
    border: 1px solid #000;
    -webkit-border-radius: 0px;
    -moz-border-radius: 0px;
    border-radius: 0px;
    margin-right: 15px;
    margin-left: 15px;
    margin-bottom: 10px;
    background-color: #fff;
    overflow: hidden;
}

.rounded-container-header {
    color: #fff;
   background-color: #6fba41;
  background-image: none;
    -webkit-border-top-left-radius: 0px;
    -webkit-border-top-right-radius: 0px;

    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
   border-bottom: 1px solid black;
    padding-left: 10px;
    padding-top: 10px;
    padding-bottom: 10px;
    font-size: 1em;
    font-weight: bolder;
}

/* end list and list headers---- */

/* Buttons on Truck page */
.submit-button {
    border: 1px solid #000;
    border-radius: 0px;
    font-size: 1em;
    font-weight: 700;
    color: #fff;
    background: #6fba41; 
    height: 35px;
}

.orange-button {
    border: 1px solid #000;
    border-radius: 0px;
    font-size: 1em;
    font-weight: 700;
    color: #fff;
    background: #55acee;
   
    height: 35px;
}
/* end of truck page buttons */


/* JOB DETAIL STUFF */
/*bottom bar sets all back color leave on none for now*/
.job-detail-footer-link-container {
	display: block;
	-webkit-box-flex: 1;
	-moz-box-flex: 1;
	-ms-flex: 1;
	flex: 1;
 
	text-align: center;
min-width: 32px;
}
/* sets uppers make me go away to dark gray */
.job-detail-footer-link-container {
	display: block;
	    background-color: #393c3e;
	-webkit-box-flex: 1;
	-moz-box-flex: 1;
	-ms-flex: 1;
	flex: 1;
	text-align: center;
	min-width: 32px;
}

/* set selected button background to wave blue */
.job-detail-footer-link-container.selected {
    background-image: -ms-linear-gradient(top, #00b6d3 0%, #00b6d3 0%); /* IE10 Consumer Preview */ 
	background-image: -moz-linear-gradient(top, #00b6d3 0%, #00b6d3 100%); /* Mozilla Firefox */ 
	background-image: -o-linear-gradient(top, #00b6d3 0%, #00b6d3 100%); /* Opera */ 
	background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0, #669CD7), color-stop(1, #78B2EC)); /* Webkit (Safari/Chrome 10) */ 
	background-image: -webkit-linear-gradient(top, #00b6d3 0%, #00b6d3 100%); /* Webkit (Chrome 11+) */ 
	background-image: linear-gradient(to bottom, #00b6d3 0%, #00b6d3 100%); /* W3C Markup, IE10 Release Preview */
}


/*sets very bottom to black */
.job-detail-footer-sprite-container {
	margin: 0px auto;
	padding-top: 4px;
    background-color: #000;
    height: 40px;
    cursor: pointer;
}

.job-detail-footer-sprite-container.selected {
	background-image: -ms-linear-gradient(top, #00b6d3 0%, #00b6d3 0%); /* IE10 Consumer Preview */ 
	background-image: -moz-linear-gradient(top, #00b6d3 0%, #00b6d3 100%); /* Mozilla Firefox */ 
	background-image: -o-linear-gradient(top, #00b6d3 0%, #00b6d3 100%); /* Opera */ 
	background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0, #669CD7), color-stop(1, #78B2EC)); /* Webkit (Safari/Chrome 10) */ 
	background-image: -webkit-linear-gradient(top, #00b6d3 0%, #00b6d3 100%); /* Webkit (Chrome 11+) */ 
	background-image: linear-gradient(to bottom, #00b6d3 0%, #00b6d3 100%); /* W3C Markup, IE10 */
    background-color: #00b6d3;
}

.job-detail-footer-sprite-container.selected {
   background-color: #00b6d3;
}

/* Ribbon for current job set to semi transparant */
#jobs-container .current-job {
    -webkit-transform: rotate(35deg);
    -moz-transform: rotate(35deg);
    -o-transform: rotate(35deg);
    -ms-transform: rotate(35deg);
    transform: rotate(35deg);
    display: block;
    height: 20px;
    width: 135px;
    position: absolute;
    top: 6px;
    right: -35px;
    padding-top: 10px;
    text-align: center;
    font-size: 0.9em;
    font-weight: bold;
    background-color: #ff4d009c;
    color: #fff;
    z-index: 0;
}

/* swipe arrows set to transparent*/
#master-layout-left-swipe-indicator-container, #master-layout-right-swipe-indicator-container {
	display: block;
	position: absolute;

	height: 100%;
	width: 10px;
	color: none;
	background-color: rgba(0, 0, 0, 0);

	z-index: 1000;
}
