css:
@-moz-document domain("sovetromantica.com") {
:root {
        --header: #1c1717;
        --dark: rgb(32, 31, 30);
        --dark-rgb: 32, 31, 30;
        --dark-warm: #2f2b2b;
        --text-color: #ccc;
        --border-color: #3d3d3d;
        --accent: rgb(249, 53, 34);
        --accent-rgb: 249, 53, 34;
    }

    header,
    header a.user--login,
    .user_content {
        background: var(--header);
    }

    header nav a {
        color: var(--text-color);
    }

    body,
    input,
    .mainContainer,
    .anime-page .episode_info,
    .anime-page .episode_list,
    .anime-info-block,
    .anime-page .anime-info .anime-name,
    .anime-page .anime-info .anime-content,
    .anime--block:before,
    .block--header:before,
    .commentsList.main:before,
    .popular--block,
    .anime-page .anime-info .blockAnimePoster,
    .commentsSendArea,
    .block--full.borderTop.borderBottom,
    .filterLabel {
        background: var(--dark);
        color: var(--text-color);
    }

    .block--full.borderTop.borderBottom {
        background: var(--dark) !important;
    }

    .borderBottom,
    .newsAuthor {
        border-color: var(--header);
    }

    .commentsSendArea .commentBlockedAuth,
    .filterLabel:hover,
    input[type=checkbox]:checked + .filterLabel {
        background: rgba(var(--dark-rgb), 0.5);
    }

    .commentsSendArea textarea,
    div.filterContent,
    .labelText {
        border: 1px solid var(--border-color);
        color: var(--text-color);
        background: var(--dark-warm);
    }

    .anime-page .anime-info_block li span,
    .block,
    .block .footer {
        color: var(--text-color);
    }

    .animeTagInfo {
        background-color: var(--dark-warm);
    }

    .news--block__error,
    .news--block__vk,
    .popular--block .anime--block__desu > .block--full,
    .commentsList .commentsEl,
    input[type=text],
    .filterLabel,
    .anime--block__desu .block--full {
        border: 1px solid var(--border-color);
        border-radius: 2px
    }

    .anime--block__name,
    .commentsList .commentsEl,
    .commentsLoadButton,
    .filterLabel,
    .block {
        background: var(--dark-warm);
    }

    [name="user_username"] + div {
        color: var(--text-color) !important;
    }

    .user_content > :last-child {
        background: var(--dark-warm) !important;
    }

    #js-description_open-full.hide:before {
        background-image: -webkit-linear-gradient(rgba(var(--dark-rgb), 0), rgba(var(--dark-rgb), 1));
        background-image: linear-gradient(rgba(var(--dark-rgb), 0), rgba(var(--dark-rgb), 1));
    }

.button--default,
input[class=hiddenCheckboxSelector]:checked+.labelType,
.episode--block__view--background__info__bage,
body::-webkit-scrollbar-thumb,
.episodes-slick::-webkit-scrollbar-thumb,
.anime-page .episode_info nav a.selected,
.episodes-slick_item.episode-active>a>div span,
.episode-slick_button,
.anime-page .anime-header_line:after,
.commentsSendArea input[type=button], .commentsSendArea input[type=submit],
.blockAnimeAgeTorrent .animeAgeRating {
    background-color: var(--accent);
}

.video-js .vjs-big-play-button {
    background-color: rgba(var(--accent-rgb), 0.85) !important;
}
.video-js .vjs-big-play-button:hover {
    background-color: rgba(var(--accent-rgb), 1) !important;    
}

.episode-slick_button-disabled,
.commentsSendArea input[type=button]:disabled, .commentsSendArea input[type=submit]:disabled {
    background-color: rgba(var(--accent-rgb), .34);    
}
.user--login.button--default img:hover {
    background-color: rgba(var(--accent-rgb),.23);
}

header a.user--login,
header nav a.active,
.anime-page .episode_info nav a,
.commentsList.main .content .block--full text a,
.user-team__username a:link, .user-team__username a:visited, #js-description_open-full a:link, #js-description_open-full a:visited,
.commentsSendArea .commentBlockedAuth span a, .commentsSendArea .commentBlockedAuth span text {
    color: var(--accent);
}
}
