css:
@-moz-document url-prefix("https://www.serialzone.cz/")
{
  *
    body .brand-cover,
    body {
        color: #f9f9f9;
        background-color: #00112f !important;
    }

    .up-box table td,
    .art-ocekavane .art-ocekavane-box,
    .subs-paging,
    .subs-paging-tvs,
    .cbb-text,
    .c-box-body,
    .main-big-box,
    .filtry,
    .content,
    .def-list2a,
    .def-list-art,
    .poster-box-cover,
    .paging .pag-line,
    .paging2 .pag-line,
    .article .art-foot-disc-box, 
    .art-foot-disc-box,
    .article .art-social-foot,
    .SZ-bg-darker-grey,
    legend,
    .timeline-commentline,
    .brand-cover {
        color: #f9f9f9;
        background-color: transparent !important;
    }

    .subs-paging a,
    .subs-paging-tvs a,
    .paging .pag-line a,
    .article a.art-foot-link, 
    a.art-foot-link,
    ul.submenu-uni li.plain,
    a.games-list {
        background-color: #005eb0;
        color: #fff
    }

    .npl-cont-in {
        background-color: #526479;
    }
    .nplbox2,
    .time-line-list {
        height: 40px;
        background-color: #526479;
    }

    .timeline-poster img {
        height: 35px;
        margin: 0;
    }

    .timeline-head-poster img {
        width: 20px;
        height: 20px;
    }

    .time-line-list .timeline-head-nick {
        height: 40px;
    }

    .time-line-list .timeline-head-nick a.dfl2-name {
        font-size: 18px;
    }

    .time-line-list .timeline-head-date {
        height: 35px;
    }

    .c-box-body {
        padding: 0;
    }

    .cbb-text a, .timeline-in-comment p a {
        color: #0092f3;
    }

    .timeline-in-comment a.tic-head,
    .art-ocekavane .art-ocekavane-box h3,
    .art-slyste .art-ocekavane-box h3,
    .article h1,
    .article h2,
    .article h3,
    .article p,
    .def-list-art .dla-in a.dla-in-head,
    .def-list-art .dla-in p a {
        color: #fff;
    }

    .art-ocekavane .art-ocekavane-box a {
        color: #ff65b8;
    }
}
